import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { auth, db } from '../../firebase';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';

export class Register extends Component {
  state = { username: '', email: '', password: '', error: '' };

  handleRegister = async (e) => {
    e.preventDefault();
    const { username, email, password } = this.state;

    try {
      // create user in Firebase Auth
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // also store extra info in Firestore (optional)
      await setDoc(doc(db, "users", userCredential.user.uid), {
        username,
        email
      });

      // redirect to login
      window.location.href = "/user-pages/login";
    } catch (err) {
      this.setState({ error: err.message });
    }
  };

  render() {
    return (
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-4 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <h4>New here?</h4>
              <form className="pt-3" onSubmit={this.handleRegister}>
                <input className="form-control form-control-lg mb-3"
                  placeholder="Username" onChange={e => this.setState({ username: e.target.value })} />
                <input className="form-control form-control-lg mb-3"
                  placeholder="Email" onChange={e => this.setState({ email: e.target.value })} />
                <input type="password" className="form-control form-control-lg mb-3"
                  placeholder="Password" onChange={e => this.setState({ password: e.target.value })} />
                {this.state.error && <p style={{ color: "red" }}>{this.state.error}</p>}
                <button type="submit" className="btn btn-primary btn-block">SIGN UP</button>
                <div className="text-center mt-4 font-weight-light">
                  Already have an account? <Link to="/user-pages/login">Login</Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
