  getTaxDetailsFromGroup(groupName) {
    if (!groupName || groupName === 'nill' || groupName === 'more') return {
      totalPercent: 0,
      totalAmount: 0
    };
    const group = this.state.taxGroups.find(tg => tg.groupName === groupName);
    if (!group || !Array.isArray(group.lineItems)) return {
      totalPercent: 0,
      totalAmount: 0
    };
