import React, { Component } from 'react';
import { db } from '../../firebase';
import { collection, addDoc, getDocs, deleteDoc, doc, setDoc } from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { Dropdown } from 'react-bootstrap';
import '../../assets/styles/components/_overlay.scss';
import { Country, State, City } from 'country-state-city';

const FY_OPTIONS = ['Jan-Dec', 'Apr-Mar'];
const STATUS_OPTIONS = ['Active', 'In-Active'];
const LEDGER_OPTIONS = ['Ledger1', 'Ledger2'];

class OrganizationPage extends Component {
  state = {
    orgs: [],
    businessGroup: null,
    showForm: false,
    editingId: null,
    activeTab: 'contact',
    formData: {
      bgCode: '',
      shortName: '',
      bgName: '',
      effectiveFrom: '',
      establishedFrom: '',
      industry: '',
      primaryLedger: LEDGER_OPTIONS[0],
      status: 'Active',
      financialYear: FY_OPTIONS[0],
      currency: '',
      // Contact Info
      landline: '', mobile: '', fax: '', email: '', website: '', linkedin: '', skype: '', twitter: '',
      // Address
      address: '', country: '', state: '', city: '',
      // Registration
      regNo: '', regPlace: '', pan: '', gstin: '',
      // Banks
      banks: [],
      bankEditIndex: null,
      bankName: '', branchName: '', ifsc: '', accountNo: '', accountHolder: '',
      // Logo
      logo: null, logoUrl: ''
    }
  };

  componentDidMount() {
    this.fetchOrgs();
  }

  fetchOrgs = async () => {
    const snap = await getDocs(collection(db, 'businessGroups'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ orgs: data, businessGroup: data[0] || null });
  };

  toggleForm = (edit = null) => {
    if (edit) {
      this.setState({ showForm: true, editingId: edit.id, activeTab: 'contact', formData: { ...edit, bankEditIndex: null, logo: null } });
    } else {
      const bgCode = `BG${101 + this.state.orgs.length}`;
      this.setState({
        showForm: true,
        editingId: null,
        activeTab: 'contact',
        formData: {
          bgCode,
          shortName: '',
          bgName: '',
          effectiveFrom: new Date().toISOString().split('T')[0],
          establishedFrom: '',
          industry: '',
          primaryLedger: LEDGER_OPTIONS[0],
          status: 'Active',
          financialYear: FY_OPTIONS[0],
          currency: '',
          landline: '', mobile: '', fax: '', email: '', website: '', linkedin: '', skype: '', twitter: '',
          address: '', country: '', state: '', city: '',
          regNo: '', regPlace: '', pan: '', gstin: '',
          banks: [],
          bankEditIndex: null,
          bankName: '', branchName: '', ifsc: '', accountNo: '', accountHolder: '',
          logo: null, logoUrl: ''
        }
      });
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleBankChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleBankAddOrUpdate = () => {
    const { bankName, branchName, ifsc, accountNo, accountHolder, banks, bankEditIndex } = this.state.formData;
    if (!bankName || !accountNo) return alert('Bank Name and Account No required');
    let newBank = { bankName, branchName, ifsc, accountNo, accountHolder };
    let newBanks = [...banks];
    if (bankEditIndex !== null) {
      newBanks[bankEditIndex] = newBank;
    } else {
      newBanks.push(newBank);
    }
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        banks: newBanks,
        bankEditIndex: null,
        bankName: '', branchName: '', ifsc: '', accountNo: '', accountHolder: ''
      }
    }));
  };

  handleBankEdit = (idx) => {
    const bank = this.state.formData.banks[idx];
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        bankEditIndex: idx,
        bankName: bank.bankName,
        branchName: bank.branchName,
        ifsc: bank.ifsc,
        accountNo: bank.accountNo,
        accountHolder: bank.accountHolder
      }
    }));
  };

handleLogoChange = (e) => {
  const file = e.target.files[0];
  if (file && file.type.startsWith('image/')) {
    const url = URL.createObjectURL(file);
    this.setState(prev => ({
      formData: { ...prev.formData, logo: file, logoUrl: url }
    }));
  } else {
    alert("Please select a valid image file.");
  }
};
componentDidMount() {
  this.fetchOrgs();
  this.fetchCurrencies();
}

fetchCurrencies = async () => {
  const snap = await getDocs(collection(db, 'currencies'));
  const currencyList = snap.docs.map(doc => doc.data().code);
  this.setState(prev => ({
    currencyOptions: currencyList,
    formData: { ...prev.formData, currency: prev.formData.currency || currencyList[0] }
  }));
};

handleSubmit = async (e) => {
  e.preventDefault();
  const { editingId, formData } = this.state;
  if (!formData.shortName || !formData.bgName) return alert('Short Name and BG Name required');

  let logoBase64 = formData.logoUrl;

  if (formData.logo) {
    // Convert logo image to Base64
    logoBase64 = await new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(formData.logo);
    });
  }

  const saveData = { ...formData, logoUrl: logoBase64 };
  delete saveData.logo;
  
if (editingId) {
  await setDoc(doc(db, 'businessGroups', editingId), saveData);
} else {
  const docRef = await addDoc(collection(db, 'businessGroups'), saveData);
  saveData.id = docRef.id;
}
localStorage.setItem('businessGroup', JSON.stringify(saveData));
window.dispatchEvent(new Event('businessGroupUpdated'));

};


  renderForm = () => {
    const { formData, activeTab } = this.state;
    return (
      <div className="card p-3 mt-3">
        <h4 className="mb-3">Business Group Creation/Update</h4>
        <form className="form-sample" onSubmit={this.handleSubmit} autoComplete="off">
          <div className="form-row">
            <div className="form-group col-md-2"><label>BG Code</label><input className="form-control form-control-sm" value={formData.bgCode} readOnly /></div>
            <div className="form-group col-md-2"><label>Short Name *</label><input className="form-control form-control-sm" value={formData.shortName} onChange={e => this.handleChange('shortName', e.target.value)} required /></div>
            <div className="form-group col-md-4"><label>BG Name *</label><input className="form-control form-control-sm" value={formData.bgName} onChange={e => this.handleChange('bgName', e.target.value)} required /></div>
            <div className="form-group col-md-2"><label>Effective From</label><input className="form-control form-control-sm" type="date" value={formData.effectiveFrom} onChange={e => this.handleChange('effectiveFrom', e.target.value)} /></div>
            <div className="form-group col-md-2"><label>Established From</label><input className="form-control form-control-sm" type="date" value={formData.establishedFrom} onChange={e => this.handleChange('establishedFrom', e.target.value)} /></div>
          </div>
          <div className="form-row">
            <div className="form-group col-md-3"><label>Industry</label><input className="form-control form-control-sm" value={formData.industry} onChange={e => this.handleChange('industry', e.target.value)} /></div>
            <div className="form-group col-md-2"><label>Primary Ledger</label>
              <select className="form-control form-control-sm" value={formData.primaryLedger} onChange={e => this.handleChange('primaryLedger', e.target.value)}>
                {LEDGER_OPTIONS.map(opt => <option key={opt}>{opt}</option>)}
              </select>
            </div>
            <div className="form-group col-md-2"><label>Status</label>
              <div>
                {STATUS_OPTIONS.map(opt => (
                  <label key={opt} className="mr-2">
                    <input type="radio" name="status" checked={formData.status === opt} onChange={() => this.handleChange('status', opt)} /> {opt}
                  </label>
                ))}
              </div>
            </div>
            <div className="form-group col-md-3"><label>Financial Year Start</label>
              <select className="form-control form-control-sm" value={formData.financialYear} onChange={e => this.handleChange('financialYear', e.target.value)}>
                {FY_OPTIONS.map(opt => <option key={opt}>{opt}</option>)}
              </select>
            </div>
            
            <div className="form-group col-md-2"><label>Currency</label>
            <select
            className="form-control form-control-sm"
            value={formData.currency}
            onChange={e => this.handleChange('currency', e.target.value)}
          >
            {this.state.currencyOptions.map(cur => (
              <option key={cur} value={cur}>{cur}</option>
            ))}
          </select></div>
          </div>
          {/* Tabs */}
          <ul className="nav nav-tabs mt-3" role="tablist">
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'contact' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'contact' })}>Contact Info</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'address' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'address' })}>Address</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'reg' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'reg' })}>Registration Details</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'bank' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'bank' })}>Bank Details</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'logo' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'logo' })}>Logo</button>
            </li>
          </ul>
          {activeTab === 'contact' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-3"><label>Landline</label><input className="form-control form-control-sm" value={formData.landline} onChange={e => this.handleChange('landline', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Mobile No</label><input className="form-control form-control-sm" value={formData.mobile} onChange={e => this.handleChange('mobile', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Fax</label><input className="form-control form-control-sm" value={formData.fax} onChange={e => this.handleChange('fax', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>E-Mail</label><input className="form-control form-control-sm" value={formData.email} onChange={e => this.handleChange('email', e.target.value)} /></div>
              </div>
              <div className="form-row">
                <div className="form-group col-md-3"><label>Website</label><input className="form-control form-control-sm" value={formData.website} onChange={e => this.handleChange('website', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>LinkedIn</label><input className="form-control form-control-sm" value={formData.linkedin} onChange={e => this.handleChange('linkedin', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Skype</label><input className="form-control form-control-sm" value={formData.skype} onChange={e => this.handleChange('skype', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Twitter</label><input className="form-control form-control-sm" value={formData.twitter} onChange={e => this.handleChange('twitter', e.target.value)} /></div>
              </div>
            </div>
          )}
          {activeTab === 'address' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-6"><label>Address Line</label><input className="form-control form-control-sm" value={formData.address} onChange={e => this.handleChange('address', e.target.value)} /></div>
                  <div className="form-group col-md-2">
                    <label>Country</label>
                    <select
                      className="form-control form-control-sm"
                      value={formData.country}
                      onChange={e => {
                        const countryCode = e.target.value;
                        this.handleChange('country', countryCode);
                        this.handleChange('state', '');
                        this.handleChange('city', '');
                      }}
                    >
                      <option value="">Select Country</option>
                      {Country.getAllCountries().map(c => (
                        <option key={c.isoCode} value={c.isoCode}>{c.name}</option>
                      ))}
                    </select>
                  </div>
                
                  <div className="form-group col-md-2">
                    <label>Province/State</label>
                    <select
                      className="form-control form-control-sm"
                      value={formData.state}
                      onChange={e => {
                        const stateCode = e.target.value;
                        this.handleChange('state', stateCode);
                        this.handleChange('city', '');
                      }}
                      disabled={!formData.country}
                    >
                      <option value="">Select State</option>
                      {formData.country &&
                        State.getStatesOfCountry(formData.country).map(s => (
                          <option key={s.isoCode} value={s.isoCode}>{s.name}</option>
                        ))}
                    </select>
                  </div>
                
                  <div className="form-group col-md-2">
                    <label>City</label>
                    <select
                      className="form-control form-control-sm"
                      value={formData.city}
                      onChange={e => this.handleChange('city', e.target.value)}
                      disabled={!formData.state}
                    >
                      <option value="">Select City</option>
                      {formData.country && formData.state &&
                        City.getCitiesOfState(formData.country, formData.state).map(city => (
                          <option key={city.name} value={city.name}>{city.name}</option>
                        ))}
                    </select>
                  </div>
                
               </div>
            </div>
          )}
          {activeTab === 'reg' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-3"><label>Company Inc Reg Number</label><input className="form-control form-control-sm" value={formData.regNo} onChange={e => this.handleChange('regNo', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Place of Registration</label><input className="form-control form-control-sm" value={formData.regPlace} onChange={e => this.handleChange('regPlace', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>PAN</label><input className="form-control form-control-sm" value={formData.pan} onChange={e => this.handleChange('pan', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>GSTIN</label><input className="form-control form-control-sm" value={formData.gstin} onChange={e => this.handleChange('gstin', e.target.value)} /></div>
              </div>
            </div>
          )}
          {activeTab === 'bank' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-2"><label>Bank Name</label><input className="form-control form-control-sm" value={formData.bankName} onChange={e => this.handleBankChange('bankName', e.target.value)} /></div>
                <div className="form-group col-md-2"><label>Branch Name</label><input className="form-control form-control-sm" value={formData.branchName} onChange={e => this.handleBankChange('branchName', e.target.value)} /></div>
                <div className="form-group col-md-2"><label>IFSC Code</label><input className="form-control form-control-sm" value={formData.ifsc} onChange={e => this.handleBankChange('ifsc', e.target.value)} /></div>
                <div className="form-group col-md-2"><label>Account No</label><input className="form-control form-control-sm" value={formData.accountNo} onChange={e => this.handleBankChange('accountNo', e.target.value)} /></div>
                <div className="form-group col-md-2"><label>Account Holder</label><input className="form-control form-control-sm" value={formData.accountHolder} onChange={e => this.handleBankChange('accountHolder', e.target.value)} /></div>
                <div className="form-group col-md-2 d-flex align-items-end">
                  <button type="button" className="btn btn-primary btn-sm" style={{ minWidth: 100 }} onClick={this.handleBankAddOrUpdate}>
                    {formData.bankEditIndex !== null ? 'Update' : 'Add'}
                  </button>
                </div>
              </div>
              <div className="custom-table-responsive">
                <table className="table table-bordered table-sm mt-2">
                  <thead>
                    <tr>
                      <th>Edit</th>
                      <th>Bank Name</th>
                      <th>Branch Name</th>
                      <th>IFSC</th>
                      <th>Account No</th>
                      <th>Account Holder</th>
                    </tr>
                  </thead>
                  <tbody>
                    {formData.banks.map((b, idx) => (
                      <tr key={idx}>
                        <td>
                          <input
                            type="radio"
                            name="editBank"
                            checked={formData.bankEditIndex === idx}
                            onChange={() => this.handleBankEdit(idx)}
                          />
                        </td>
                        <td>{b.bankName}</td>
                        <td>{b.branchName}</td>
                        <td>{b.ifsc}</td>
                        <td>{b.accountNo}</td>
                        <td>{b.accountHolder}</td>
                      </tr>
                    ))}
                    {formData.banks.length === 0 && <tr><td colSpan="6" className="text-center">No banks</td></tr>}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          {activeTab === 'logo' && (
            <div className="mt-3">
              <div className="form-group">
                <label>Logo</label>
                <input type="file" className="form-control-file" accept="image/*" onChange={this.handleLogoChange} />
                {formData.logoUrl && <img src={formData.logoUrl} alt="Logo Preview" style={{ maxHeight: 100, marginTop: 10 }} />}
              </div>
            </div>
          )}
          <div className="d-flex justify-content-between mt-3">
            <button type="submit" className="btn btn-success btn-sm">Save</button>
            <button type="button" className="btn btn-secondary btn-sm" onClick={() => this.setState({ showForm: false })}>Cancel</button>
          </div>
        </form>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Business Groups</h4>
          {!this.state.businessGroup && (
            <button className="btn btn-primary btn-sm" onClick={() => this.toggleForm()}>+ Add Business Group</button>
          )}
        </div>
        <div className="custom-table-responsive">
          <table className="table table-bordered table-sm">
            <thead className='thead-light'>
              <tr>
                <th>BG Code</th>
                <th>Short Name</th>
                <th>BG Name</th>
                <th>Establish From</th>
                <th>Effective From</th>
                <th>Industry</th>
                <th>Financial Year</th>
                <th>Currency</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.orgs.map((o, i) => (
                <tr key={o.id}>
                  <td><button className='btn btn-link p-0' onClick={() => this.toggleForm(o)}>{o.bgCode}</button></td>
                  <td>{o.shortName}</td>
                  <td>{o.bgName}</td>
                  <td>{o.establishedFrom}</td>
                  <td>{o.effectiveFrom}</td>
                  <td>{o.industry}</td>
                  <td>{o.financialYear}</td>
                  <td>{o.currency}</td>
                  <td>{o.status}</td>
                </tr>
              ))}
              {this.state.orgs.length === 0 && <tr><td colSpan="12" className="text-center">No records found</td></tr>}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  render() {
    return <div className="container-fluid">{this.state.showForm ? this.renderForm() : this.renderTable()}</div>;
  }
}

export default OrganizationPage;
