// ProspectForm.js (React 16 + Bootstrap + Firebase Firestore)
import React, { Component } from 'react';
import { db } from '../../firebase';
import { Dropdown } from 'react-bootstrap';
import { collection, getDocs, addDoc, deleteDoc, doc, setDoc } from 'firebase/firestore';

class ProspectForm extends Component {
  state = {
    prospects: [],
    showForm: false,
    editingId: null,
    formData: {
      prospectNo: '',
      name: '',
      company: '',
      amount: '',
      representative: '',
      timeline: '',
    },
  };

  componentDidMount() {
    this.fetchProspects();
  }

  fetchProspects = async () => {
    const snap = await getDocs(collection(db, 'prospects'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ prospects: data });
  };

  toggleForm = (edit = null) => {
    if (edit) {
      this.setState({ showForm: true, editingId: edit.id, formData: edit });
    } else {
      this.setState({
        showForm: true,
        editingId: null,
        formData: {
          prospectNo: '', name: '', company: '', amount: '',
          representative: '', timeline: '',
        },
      });
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({ formData: { ...prev.formData, [field]: value } }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, prospects } = this.state;
    if (!formData.name) return alert("Enter Prospect Name");

    if (editingId) {
      await setDoc(doc(db, 'prospects', editingId), formData);
    } else {
      formData.prospectNo = `PN${1000 + prospects.length}`;
      await addDoc(collection(db, 'prospects'), formData);
    }

    this.setState({ showForm: false, editingId: null });
    this.fetchProspects();
  };

  handleDelete = async (id) => {
    await deleteDoc(doc(db, 'prospects', id));
    this.fetchProspects();
  };

  renderForm = () => {
    const { formData } = this.state;
    return (
      <form className="card p-4 mt-3" onSubmit={this.handleSubmit}>
        <h4 className="mb-3">{this.state.editingId ? 'Edit Prospect' : 'New Prospect'}</h4>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Prospect Name</label>
            <input className="form-control" value={formData.name} onChange={e => this.handleChange('name', e.target.value)} required />
          </div>
          <div className="form-group col-md-4">
            <label>Company</label>
            <input className="form-control" value={formData.company} onChange={e => this.handleChange('company', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Amount</label>
            <input className="form-control" value={formData.amount} onChange={e => this.handleChange('amount', e.target.value)} />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Representative</label>
            <input className="form-control" value={formData.representative} onChange={e => this.handleChange('representative', e.target.value)} />
          </div>
          <div className="form-group col-md-6">
            <label>Timeline</label>
            <input className="form-control" type="date" value={formData.timeline} onChange={e => this.handleChange('timeline', e.target.value)} />
          </div>
        </div>
        <div className="d-flex justify-content-between">
        <button type="submit" className="btn btn-success">Save</button>
        <button type="button" className="btn btn-secondary" onClick={() => this.setState({ showForm: false })}>Cancel</button>
        </div>
      </form>
    );
  };

  renderTable = () => (
    <div className="card mt-4">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Prospect List</h4>
          <button className="btn btn-primary" onClick={() => this.toggleForm()}>+ Add Prospect</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>Company</th>
                <th>Amount</th>
                <th>Rep</th>
                <th>Timeline</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.prospects.map((p, index) => (
                <tr key={p.id}>
                  <td>{p.prospectNo}</td>
                  <td>{p.name}</td>
                  <td>{p.company}</td>
                  <td>{p.amount}</td>
                  <td>{p.representative}</td>
                  <td>{p.timeline}</td>
                  <td>
                <Dropdown>
                    <Dropdown.Toggle variant="btn btn-outline-primary" id="dropdownMenuOutlineButton1" size='sm'>
                      Action
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => this.toggleForm(p)}>Edit</Dropdown.Item>
                      <Dropdown.Item onClick={() => this.handleDelete(p.id)}>Delete</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                  </td>
                </tr>
              ))}
              {this.state.prospects.length === 0 && (
                <tr><td colSpan="7" className="text-center">No prospects available.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  render() {
    return <div className="container-fluid">{this.state.showForm ? this.renderForm() : this.renderTable()}</div>;
  }
}

export default ProspectForm;
