import React, { Component } from "react";
import { Modal, Button, Table, Form, Card } from "react-bootstrap";
import { db } from "../../firebase";
import { collection, getDocs, addDoc, updateDoc, doc } from "firebase/firestore";
import { Country, State, City } from "country-state-city";

class Datalist extends Component {
  state = {
    accounts: [],
    showModal: false,
    selectedAccount: null,
    contactPersons: [],
  };

  componentDidMount() {
    this.fetchAccounts();
  }

  fetchAccounts = async () => {
    const snap = await getDocs(collection(db, "accounts"));
    const accounts = snap.docs.map(d => ({ id: d.id, ...d.data() }));
    this.setState({ accounts });
  };

  // open for add or edit
  openModal = (account = null) => {
    this.setState({
      showModal: true,
      selectedAccount: account || {
        acno: "", acname: "", contact: "",
        email: "", website: "", phone: "",
        country: "", state: "", city: ""
      },
      contactPersons: account?.contactPersons || [],
    });
  };

  closeModal = () => this.setState({ showModal: false, selectedAccount: null, contactPersons: [] });

  // helper to patch selectedAccount safely
  patchSelected = (patch) => {
    this.setState(prev => ({ selectedAccount: { ...prev.selectedAccount, ...patch } }));
  };

  handleContactPersonChange = (i, field, value) => {
    this.setState(prev => {
      const cps = [...prev.contactPersons];
      cps[i] = { ...cps[i], [field]: value };
      return { contactPersons: cps };
    });
  };

  addContactPerson = () => {
    this.setState(prev => ({
      contactPersons: [...prev.contactPersons, { name: "", designation: "", phone: "", mail: "", linkedin: "" }]
    }));
  };

  saveAccount = async () => {
    const { selectedAccount, contactPersons } = this.state;
    const data = { ...selectedAccount, contactPersons };

    if (data.id) {
      await updateDoc(doc(db, "accounts", data.id), data);
    } else {
      await addDoc(collection(db, "accounts"), data);
    }
    await this.fetchAccounts();
    this.closeModal();
  };

  render() {
    const { accounts, selectedAccount } = this.state;

    return (
      <div className="container mt-4">
        {/* White box like other pages */}
        <Card className="p-3 shadow-sm bg-white border-0">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h3 className="mb-0">Account Data List</h3>
            <Button variant="primary" onClick={() => this.openModal()}>+ Add Account</Button>
          </div>

          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr>
                <th>Edit</th>
                <th>A/c No</th>
                <th>Account Name</th>
                <th>Contact</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Country</th>
                <th>State</th>
                <th>City</th>
              </tr>
            </thead>
            <tbody>
              {accounts.map(acc => (
                <tr key={acc.id}>
                  <td><span><i className="mdi mdi-lead-pencil"></i></span></td>
                  <td>{acc.acno}</td>
                  <td>{acc.acname}</td>
                  <td>{acc.contact}</td>
                  <td>{acc.email}</td>
                  <td>{acc.phone}</td>
                  <td>{Country.getCountryByCode(acc.country)?.name || ""}</td>
                  <td>{acc.country ? (State.getStateByCodeAndCountry(acc.state, acc.country)?.name || "") : ""}</td>
                  <td>{acc.city}</td>
                  
                </tr>
              ))}
            </tbody>
          </table>
        </Card>

        {/* Modal â€“ force white background */}
        <Modal
          show={this.state.showModal}
          onHide={this.closeModal}
          size="lg"
          contentClassName="bg-white"
        >
          <Modal.Header closeButton>
            <Modal.Title>{selectedAccount?.id ? "Edit Account" : "Add Account"}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {selectedAccount && (
              <Form>
                {/* Main fields */}
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label>A/c No</label>
                    <Form.Control value={selectedAccount.acno} onChange={e => this.patchSelected({ acno: e.target.value })} />
                  </div>
                  <div className="form-group col-md-4">
                    <label>Account Name</label>
                    <Form.Control value={selectedAccount.acname} onChange={e => this.patchSelected({ acname: e.target.value })} required />
                  </div>
                  <div className="form-group col-md-4">
                    <label>Contact</label>
                    <Form.Control value={selectedAccount.contact} onChange={e => this.patchSelected({ contact: e.target.value })} />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label>Email</label>
                    <Form.Control value={selectedAccount.email} onChange={e => this.patchSelected({ email: e.target.value })} />
                  </div>
                  <div className="form-group col-md-4">
                    <label>Website</label>
                    <Form.Control value={selectedAccount.website} onChange={e => this.patchSelected({ website: e.target.value })} required />
                  </div>
                  <div className="form-group col-md-4">
                    <label>Phone</label>
                    <Form.Control type="number" value={selectedAccount.phone} onChange={e => this.patchSelected({ phone: e.target.value })} required/>
                  </div>
                </div>

                {/* Country / State / City (bug fixed: single functional setState) */}
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label>Country</label>
                    <select
                      className="form-control"
                      value={selectedAccount.country}
                      onChange={(e) => {
                        const val = e.target.value;
                        this.setState(prev => ({
                          selectedAccount: { ...prev.selectedAccount, country: val, state: "", city: "" }
                        }));
                      }}
                    >
                      <option value="">Select Country</option>
                      {Country.getAllCountries().map(c => (
                        <option key={c.isoCode} value={c.isoCode}>{c.name}</option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group col-md-4">
                    <label>State</label>
                    <select
                      className="form-control"
                      value={selectedAccount.state}
                      onChange={(e) => {
                        const val = e.target.value;
                        this.setState(prev => ({
                          selectedAccount: { ...prev.selectedAccount, state: val, city: "" }
                        }));
                      }}
                      disabled={!selectedAccount.country}
                    >
                      <option value="">Select State</option>
                      {selectedAccount.country &&
                        State.getStatesOfCountry(selectedAccount.country).map(s => (
                          <option key={s.isoCode} value={s.isoCode}>{s.name}</option>
                        ))}
                    </select>
                  </div>

                  <div className="form-group col-md-4">
                    <label>City</label>
                    <select
                      className="form-control"
                      value={selectedAccount.city}
                      onChange={(e) => this.patchSelected({ city: e.target.value })}
                      disabled={!selectedAccount.state}
                    >
                      <option value="">Select City</option>
                      {selectedAccount.country && selectedAccount.state &&
                        City.getCitiesOfState(selectedAccount.country, selectedAccount.state).map(ct => (
                          <option key={ct.name} value={ct.name}>{ct.name}</option>
                        ))}
                    </select>
                  </div>
                </div>

                <hr />
                <h5>Contact Persons</h5>
                {this.state.contactPersons.map((cp, i) => (
                  <div key={i} className="mb-3 p-2 border rounded">
                    <Form.Control className="mb-2" placeholder="Name"
                      value={cp.name} onChange={e => this.handleContactPersonChange(i, "name", e.target.value)} />
                    <Form.Control className="mb-2" placeholder="Designation"
                      value={cp.designation} onChange={e => this.handleContactPersonChange(i, "designation", e.target.value)} />
                    <Form.Control className="mb-2" placeholder="Phone"
                      value={cp.phone} onChange={e => this.handleContactPersonChange(i, "phone", e.target.value)} />
                    <Form.Control className="mb-2" placeholder="Mail"
                      value={cp.mail} onChange={e => this.handleContactPersonChange(i, "mail", e.target.value)} />
                    <Form.Control className="mb-2" placeholder="LinkedIn"
                      value={cp.linkedin} onChange={e => this.handleContactPersonChange(i, "linkedin", e.target.value)} />
                  </div>
                ))}
                <Button variant="secondary" onClick={this.addContactPerson}>+ Add Contact Person</Button>
              </Form>
            )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.saveAccount}>Submit</Button>
            <Button variant="light" onClick={this.closeModal}>Cancel</Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default Datalist;
