getTaxDetailsFromGroup(groupName) {
  if (!groupName || groupName === 'nill' || groupName === 'more') return { totalPercent: 0, totalAmount: 0 };

  const group = this.state.taxGroups.find(tg => tg.groupName === groupName);
  if (!group || !Array.isArray(group.lineItems)) return { totalPercent: 0, totalAmount: 0 };

  let totalPercent = 0;
  let totalAmount = 0;

  group.lineItems.forEach(item => {
    if (item.type === 'Percentage') {
      totalPercent += parseFloat(item.percentOrAmt || 0);
    } else if (item.type === 'Amount') {
      totalAmount += parseFloat(item.percentOrAmt || 0);
    }
  });

  return { totalPercent, totalAmount };
}



handleLineItemChange = (idx, field, value) => {
  const updatedItems = [...this.state.formData.lineItems];
  const item = { ...updatedItems[idx] };

  if (field === 'taxId') {
    if (value === 'more') {
      this.setState({ showTaxOverlay: true });
      return;
    }
    item.taxId = value;
  } else {
    item[field] = value;
  }

  updatedItems[idx] = item;

  this.setState(prev => ({
    formData: {
      ...prev.formData,
      lineItems: updatedItems
    }
  }), this.recalculateInvoiceTotals);
};

recalculateInvoiceTotals = () => {
  const { lineItems, freightCharges, packingCharges, taxPercent } = this.state.formData;

  let lineTotal = 0;
  let lineTaxTotal = 0;

  const updatedLineItems = lineItems.map(item => {
    const qty = parseFloat(item.qty || 1);
    const unitPrice = parseFloat(item.unitPrice || 0);
    const baseTotal = unitPrice * qty;

   const groupNames = item.taxGroupNames?.length ? item.taxGroupNames : [item.taxGroupName || item.taxId || ''];

    let percent = 0;
    let amount = 0;
    let cgst = 0, sgst = 0, igst = 0;

    groupNames.forEach(groupName => {
      const group = this.state.taxGroups.find(t => t.groupName === groupName);
      if (group && Array.isArray(group.lineItems)) {
        group.lineItems.forEach(comp => {
          const val = parseFloat(comp.percentOrAmt || 0);
          if (comp.type === 'Percentage') {
            percent += val;
            const name = comp.component.toLowerCase();
            if (name.includes('cgst')) cgst += val;
            if (name.includes('sgst')) sgst += val;
            if (name.includes('igst')) igst += val;
          } else if (comp.type === 'Amount') {
            amount += val;
          }
        });
      }
    });


    // Total tax = percentage tax + fixed amount tax
    const itemTaxAmt = (baseTotal * percent) / 100 + amount;
    const itemTotal = baseTotal + itemTaxAmt;

    lineTotal += baseTotal;
    lineTaxTotal += itemTaxAmt;
    console.log('Item:', item.itemCode, 'GroupNames:', groupNames, 'Tax %:', percent, 'Tax Amt:', itemTaxAmt);


    return {
      ...item,
      cgst,
      sgst,
      igst,
      taxAmt: itemTaxAmt.toFixed(2),
      total: itemTotal.toFixed(2)
    };
  });

  // Freight + Freight Tax
  const freight = parseFloat(freightCharges || 0);
  const packing = parseFloat(packingCharges || 0);
  const freightPercent = parseFloat(taxPercent || 0);
  const freightTaxAmount = (freight * freightPercent) / 100;

  // Final tax and Invoice value
  const totalTaxAmount = lineTaxTotal + freightTaxAmount;
  const InvoiceValue = lineTotal + lineTaxTotal + freight + packing + freightTaxAmount;

  // Set final state
  this.setState(prev => ({
    formData: {
      ...prev.formData,
      lineItems: updatedLineItems,
      freighttaxAmount: freightTaxAmount.toFixed(2),
      taxAmount: totalTaxAmount.toFixed(2),
      InvoiceValue: InvoiceValue.toFixed(2)
    }
  }));
};


resetFOBFields = () => {
  const { freightCharges, taxPercent, taxAmount, packingCharges, InvoiceValue } = this.state.formData;
  const needsReset = [freightCharges, taxPercent, taxAmount, packingCharges, InvoiceValue]
