toggleTaxGroupSelection = (groupName, lineIdx, isChecked) => {
  const formData = { ...this.state.formData };
  const item = formData.lineItems[lineIdx];

  if (!item.taxGroupNames) item.taxGroupNames = [];

  if (isChecked) {
    if (!item.taxGroupNames.includes(groupName)) {
      item.taxGroupNames.push(groupName);
    }
  } else {
    item.taxGroupNames = item.taxGroupNames.filter(g => g !== groupName);
  }

  // Recalculates individual tax fields — but not totals
  let cgst = 0, sgst = 0, igst = 0;
  item.taxGroupNames.forEach(gname => {
    const tg = this.state.taxGroups.find(t => t.groupName === gname);
    if (tg) {
      tg.lineItems.forEach(l => {
        const comp = l.component.toLowerCase();
        const val = parseFloat(l.percentOrAmt || 0);
        if (comp.includes('cgst')) cgst += val;
        if (comp.includes('sgst')) sgst += val;
        if (comp.includes('igst')) igst += val;
      });
    }
  });

  const itemTotal = item.unitPrice * item.qty;
  const totalTaxPercent = cgst + sgst + igst;
  const taxAmt = (itemTotal * totalTaxPercent) / 100;

  item.cgst = cgst;
  item.sgst = sgst;
  item.igst = igst;
  item.taxAmt = taxAmt;
  item.total = itemTotal + taxAmt;

  this.setState({ formData }, () => this.recalculateInvoiceTotals());
};




