import React, { Component } from "react";
import { Table, Button, Form, Card, InputGroup } from "react-bootstrap";
import { db } from "../../firebase";
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

class Activity extends Component {
  state = {
    activities: [],
    accounts: [],
    addingRow: false,
    newRow: {
      acname: "",
      contact: "",
      phone: "",
      email: "",
      source: "",
      remarks: "",
      date: new Date().toISOString().split("T")[0], // yyyy-mm-dd
    },
    contactOptions: [],
    // pagination + search
    searchQuery: "",
    currentPage: 1,
    pageSize: 10,
  };

  componentDidMount() {
    this.fetchActivities();
    this.fetchAccounts();
  }

  // fetch activity logs
  fetchActivities = async () => {
    const snap = await getDocs(collection(db, "activities"));
    const activities = snap.docs.map((doc, idx) => ({
      id: doc.id,
      no: idx + 1,
      ...doc.data(),
    }));
    this.setState({ activities });
  };

  // fetch accounts + contacts from datalist
  fetchAccounts = async () => {
    const snap = await getDocs(collection(db, "accounts"));
    const accounts = snap.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    this.setState({ accounts });
  };

  handleNewRowChange = (field, value) => {
    let updated = { ...this.state.newRow, [field]: value };

    if (field === "acname") {
      const acc = this.state.accounts.find((a) => a.acname === value);
      let contactOptions = [];

      if (acc) {
        if (acc.contact) {
          contactOptions.push({
            name: acc.contact,
            designation: "Main",
            phone: acc.phone || "",
            email: acc.email || "",
          });
        }
        if (acc.contactPersons && acc.contactPersons.length > 0) {
          contactOptions = contactOptions.concat(acc.contactPersons);
        }
      }

      updated.contact = "";
      updated.phone = "";
      updated.email = "";
      this.setState({ contactOptions });
    }

    if (field === "contact") {
      const cp = this.state.contactOptions.find((c) => c.name === value);
      if (cp) {
        updated.phone = cp.phone || "";
        updated.email = cp.email || "";
      }
    }

    if (field === "remarks" && value.length > 500) {
      return; // max 500 chars
    }

    this.setState({ newRow: updated });
  };

  saveNewRow = async () => {
    const { newRow } = this.state;
    await addDoc(collection(db, "activities"), newRow);

    this.setState({
      addingRow: false,
      newRow: {
        acname: "",
        contact: "",
        phone: "",
        email: "",
        source: "",
        remarks: "",
        date: new Date().toISOString().split("T")[0],
      },
    });
    this.fetchActivities();
  };

  deleteRow = async (id) => {
    await deleteDoc(doc(db, "activities", id));
    this.fetchActivities();
  };

  // Search filter
  handleSearch = (e) => {
    this.setState({ searchQuery: e.target.value, currentPage: 1 });
  };

  // Pagination controls
  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const {
      activities,
      accounts,
      addingRow,
      newRow,
      contactOptions,
      searchQuery,
      currentPage,
      pageSize,
    } = this.state;

    // --- Filter activities ---
    const filtered = activities.filter(
      (a) =>
        a.acname?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        a.contact?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        a.remarks?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // --- Pagination logic ---
    const startIndex = (currentPage - 1) * pageSize;
    const paginated = filtered.slice(startIndex, startIndex + pageSize);
    const totalPages = Math.ceil(filtered.length / pageSize);

    return (
      <div className="container-fluid mt-4">
        <Card className="p-3 shadow-sm bg-white border-0">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h4>Activities</h4>
            <div className="d-flex gap-2">
              {/* Search box */}
              <InputGroup>
                <Form.Control
                  type="text"
                  placeholder="Search by account, contact, remarks..."
                  value={searchQuery}
                  onChange={this.handleSearch}
                />
              </InputGroup>

              {!addingRow && (
                <Button
                  variant="primary"
                  onClick={() => this.setState({ addingRow: true })}
                >
                  + Add
                </Button>
              )}
            </div>
          </div>

          <div className="table-responsive">
            <Table bordered hover className="w-100">
              <thead className="thead-light">
                <tr>
                  <th>Created Date</th>
                  <th>Account Name</th>
                  <th>Contact Person</th>
                  <th>Phone</th>
                  <th>Mail</th>
                  <th>Source</th>
                  <th>Remarks</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {paginated.map((act) => (
                  <tr key={act.id}>
                    <td>{act.date}</td>
                    <td>{act.acname}</td>
                    <td>{act.contact}</td>
                    <td>{act.phone}</td>
                    <td>{act.email}</td>
                    <td>{act.source}</td>
                    <td style={{ maxWidth: "300px", whiteSpace: "pre-wrap" }}>
                      {act.remarks}
                    </td>
                    <td>
                      <Button
                        variant="danger"
                        size="sm"
                        onClick={() => this.deleteRow(act.id)}
                      >
                        <i className="mdi mdi-delete"></i>
                      </Button>
                    </td>
                  </tr>
                ))}

                {addingRow && (
                  <tr>
                    {/* created date */}
                    <td>
                      <Form.Control
                        type="date"
                        value={newRow.date}
                        onChange={(e) =>
                          this.handleNewRowChange("date", e.target.value)
                        }
                      />
                    </td>

                    {/* account name */}
                    <td>
                      <Form.Control
                        as="select"
                        value={newRow.acname}
                        onChange={(e) =>
                          this.handleNewRowChange("acname", e.target.value)
                        }
                      >
                        <option value="">Select Account</option>
                        {accounts.map((acc) => (
                          <option key={acc.id} value={acc.acname}>
                            {acc.acname}
                          </option>
                        ))}
                      </Form.Control>
                    </td>

                    {/* contact */}
                    <td>
                      <Form.Control
                        as="select"
                        value={newRow.contact}
                        onChange={(e) =>
                          this.handleNewRowChange("contact", e.target.value)
                        }
                        disabled={!newRow.acname}
                      >
                        <option value="">Select Contact</option>
                        {contactOptions.map((cp, i) => (
                          <option key={i} value={cp.name}>
                            {cp.name}
                            {cp.designation ? ` (${cp.designation})` : ""}
                          </option>
                        ))}
                      </Form.Control>
                    </td>

                    {/* phone */}
                    <td>
                      <Form.Control type="text" value={newRow.phone} disabled />
                    </td>

                    {/* mail */}
                    <td>
                      <Form.Control type="text" value={newRow.email} disabled />
                    </td>

                    {/* source */}
                    <td>
                      <Form.Control
                        as="select"
                        value={newRow.source}
                        onChange={(e) =>
                          this.handleNewRowChange("source", e.target.value)
                        }
                      >
                        <option value="">Select Source</option>
                        <option value="Call">Call</option>
                        <option value="Meeting">Meeting</option>
                        <option value="Demo">Demo</option>
                        <option value="Mail">Mail</option>
                        <option value="Whatsapp">Whatsapp</option>
                      </Form.Control>
                    </td>

                    {/* remarks */}
                    <td>
                      <Form.Control
                        as="textarea"
                        rows={2}
                        value={newRow.remarks}
                        onChange={(e) =>
                          this.handleNewRowChange("remarks", e.target.value)
                        }
                        placeholder="Remarks (max 500 chars)"
                      />
                      <small>{newRow.remarks.length}/500</small>
                    </td>

                    {/* action */}
                    <td>
                      <Button
                        variant="success"
                        size="sm"
                        onClick={this.saveNewRow}
                      >
                        Save
                      </Button>{" "}
                      <Button
                        variant="secondary"
                        size="sm"
                        onClick={() =>
                          this.setState({
                            addingRow: false,
                            newRow: {
                              acname: "",
                              contact: "",
                              phone: "",
                              email: "",
                              source: "",
                              remarks: "",
                              date: new Date().toISOString().split("T")[0],
                            },
                          })
                        }
                      >
                        Cancel
                      </Button>
                    </td>
                  </tr>
                )}
              </tbody>
            </Table>
          </div>

          {/* Pagination controls */}
          <div className="d-flex justify-content-between align-items-center">
            <div>
              Showing {startIndex + 1}â€“
              {Math.min(startIndex + pageSize, filtered.length)} of{" "}
              {filtered.length}
            </div>
            <div>
              <Button
                variant="outline-secondary"
                size="xs"
                disabled={currentPage === 1}
                onClick={() => this.handlePageChange(currentPage - 1)}
              >
                &lt;&lt;
              </Button>{" "}
              <span>
                Page {currentPage} of {totalPages}
              </span>{" "}
              <Button
                variant="outline-secondary"
                size="xs"
                disabled={currentPage === totalPages}
                onClick={() => this.handlePageChange(currentPage + 1)}
              >
                &gt;&gt;
              </Button>
            </div>
          </div>
        </Card>
      </div>
    );
  }
}

export default Activity;
