// src/app/sales/salestransactions/order/OrderApproval.js
import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class OrderApproval extends Component {
  state = {
    orders: [],
    selectedOrder: null,
    showForm: false,
  };

  componentDidMount() {
    this.fetchOrders();
  }

  fetchOrders = () => {
    axios
      .get("http://localhost:3000/orders") // âœ… adjust API endpoint if needed
      .then((res) => {
        // Show only those Awaiting Approval
        const awaiting = res.data.filter((o) => o.status === "Awaiting for Approval");
        this.setState({ orders: awaiting });
      })
      .catch((err) => console.error(err));
  };

  openOrder = (order) => {
    this.setState({ selectedOrder: order, showForm: true });
  };

  closeForm = () => {
    this.setState({ selectedOrder: null, showForm: false });
  };

handleApproval = (status) => {
  if (!this.state.selectedOrder) return;

  axios
    .patch(`http://localhost:4000/orders/${this.state.selectedOrder.id}/status`, { status })
    .then(() => {
      this.setState({ selectedOrder: null, showForm: false }, this.fetchOrders);
    })
    .catch((err) => console.error(err));
};


  render() {
    const { orders, selectedOrder, showForm } = this.state;

    return (
      <div className="container-fluid">
        <h4 className="mb-3">Order Approval</h4>

        {!showForm && (
          <div className="card">
            <div className="card-body">
              <table className="table table-bordered table-sm">
                <thead>
                  <tr>
                    <th>Order No</th>
                    <th>Date</th>
                    <th>Customer</th>
                    <th>Order Value</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map((o, i) => (
                    <tr key={i}>
                      <td>
                        <button
                          className="btn btn-link p-0"
                          onClick={() => this.openOrder(o)}
                        >
                          {o.orderNo}
                        </button>
                      </td>
                      <td>{o.orderDate}</td>
                      <td>{o.customerName}</td>
                      <td>{o.orderValue}</td>
                      <td>
                        <span className="badge badge-warning">{o.status}</span>
                      </td>
                      <td>
                        <button
                          className="btn btn-sm btn-info mr-2"
                          onClick={() => this.openOrder(o)}
                        >
                          View
                        </button>
                      </td>
                    </tr>
                  ))}
                  {orders.length === 0 && (
                    <tr>
                      <td colSpan="6" className="text-center text-muted">
                        No awaiting approvals
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {showForm && selectedOrder && (
          <div className="card mt-3">
            <div className="card-header d-flex justify-content-between">
              <h5>Order #{selectedOrder.orderNo}</h5>
              <button className="btn btn-sm btn-secondary" onClick={this.closeForm}>
                Close
              </button>
            </div>
            <div className="card-body">
              {/* Display full order details (similar to Order.js form preview) */}
              <p><strong>Date:</strong> {selectedOrder.orderDate}</p>
              <p><strong>Customer:</strong> {selectedOrder.customerName}</p>
              <p><strong>Order Value:</strong> {selectedOrder.orderValue}</p>
              <p><strong>Status:</strong> {selectedOrder.status}</p>

              <h6>Line Items</h6>
              <table className="table table-bordered table-sm">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Qty</th>
                    <th>Unit Price</th>
                    <th>Tax Amt</th>
                    <th>Total</th>
                  </tr>
                </thead>
                <tbody>
                  {(selectedOrder.lineItems || []).map((li, idx) => (
                    <tr key={idx}>
                      <td>{li.ptshortName}</td>
                      <td>{li.qty}</td>
                      <td>{li.unitPrice}</td>
                      <td>{li.taxAmt}</td>
                      <td>{li.itemTotal}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="mt-3">
                <button
                  className="btn btn-success mr-2"
                  onClick={() => this.handleApproval("Approved")}
                >
                  Approve
                </button>
                <button
                  className="btn btn-danger mr-2"
                  onClick={() => this.handleApproval("Rejected")}
                >
                  Reject
                </button>
                <Link
                  to={{
                    pathname: '/sales/salestransactions/order/Order',
                    state: { openForm: true, editId: selectedOrder.id }
                  }}
                  className="btn btn-primary"
                >
                  Edit
                </Link>
                
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }
}
export default OrderApproval;
