                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="text-right mt-3">
                      <button type="submit" className="btn btn-success">Save All Details</button>
                    </div>
                  </>
                )}
                {this.state.activeTab === 'breakdown' && (
                <>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h5 className="card-title">Breakdown Items</h5>
                    <button type="button" className="btn btn-primary btn-sm" onClick={() => {
                      this.setState(prev => ({
                        breakdownItems: [...prev.breakdownItems, { date: '', description: '', lastDate: '' }]
                      }));
                    }}>
                      + Add Breakdown
                    </button>
                  </div>
                  <table className="table table-bordered table-sm">
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Description</th>
                        <th>Last Date to Complete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {breakdownItems.map((item, idx) => (
                       <tr key={idx}>
                        <td>
                          <input
                            type="checkbox"
                            checked={item.acknowledged || false}
                            onChange={(e) => this.handleBreakdownChange(idx, 'acknowledged', e.target.checked)}
                          />
                        </td>
                        <td>
                          <input
                            type="date"
                            className="form-control"
                            value={item.date}
                            onChange={(e) => this.handleBreakdownChange(idx, 'date', e.target.value)}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            className="form-control"
                            value={item.desc}
                            onChange={(e) => this.handleBreakdownChange(idx, 'desc', e.target.value)}
                          />
                        </td>
                        <td>
                          <input
                            type="date"
                            className="form-control"
                            value={item.lastDate}
                            onChange={(e) => this.handleBreakdownChange(idx, 'lastDate', e.target.value)}
                          />
                        </td>
                      </tr>

                      ))}
                      {breakdownItems.length === 0 && (
                        <tr><td colSpan="3" className="text-center">No breakdown items added</td></tr>
                      )}
                    </tbody>
                  </table>
                </>
              )}
              </div>
              <div className="fixed-card-footer text-right p-3 border-top bg-white">
                <button type="submit" className="btn btn-success mr-2">Save All Details</button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => this.setState({ showForm: false, editingId: null })}
                >
                  Cancel
                </button>
              </div>
            </form>
            {overlayType && this.renderOverlay()}
            {productOverlayVisible && this.renderProductOverlay()}
          </div>
           
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderorderForm() : this.renderorderTable()}
      </div>
    );
  }
}

export default withRouter(Order);
