  isPathActive(path) {
    return this.props.location.pathname.startsWith(path);
  }

  async componentDidMount() {
    this.onRouteChanged(); // Always listen for updates

    window.addEventListener('businessGroupUpdated', this.handleBusinessGroupUpdate); // Always load from localStorage

    const storedBG = localStorage.getItem('businessGroup');

    if (storedBG) {
      this.setState({
        businessGroup: JSON.parse(storedBG)
      });
    } else {
      // Only fetch from Firestore if nothing in localStorage (first ever load)
      const snap = await getDocs(collection(db, 'businessGroups'));
      const data = snap.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      if (data[0]) {
        localStorage.setItem('businessGroup', JSON.stringify(data[0]));
        this.setState({
          businessGroup: data[0]
        });
      }
    } // Sidebar hover behavior


    const body = document.querySelector('body');
    document.querySelectorAll('.sidebar .nav-item').forEach(el => {
      el.addEventListener('mouseover', function () {
        if (body.classList.contains('sidebar-icon-only')) {
          el.classList.add('hover-open');
        }
      });
      el.addEventListener('mouseout', function () {
        if (body.classList.contains('sidebar-icon-only')) {
          el.classList.remove('hover-open');
        }
      });
    });
  }

  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged(); // Always reload business group from localStorage on navigation

      const storedBG = localStorage.getItem('businessGroup');

      if (storedBG) {
        this.setState({
          businessGroup: JSON.parse(storedBG)
        });
      }
    }
  }

  componentWillUnmount() {
    window.removeEventListener('businessGroupUpdated', this.handleBusinessGroupUpdate);
  }

}

