import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Collapse } from 'react-bootstrap';
import { Dropdown } from 'react-bootstrap';
import { Trans } from 'react-i18next';
import { db } from '../../firebase';
import { collection,getDocs } from 'firebase/firestore';


class Sidebar extends Component {
  state = {
    businessGroup: null,
  };

  toggleMenuState(menuState) {
  // Close only unrelated menus, keep parent Inventory open
  this.setState(prevState => {
    const newState = { ...prevState };

    // If clicking the same menu, just toggle
    if (newState[menuState]) {
      newState[menuState] = false;
    } else {
      Object.keys(newState).forEach(key => {
        // Keep inventoryOpen open when toggling submenus
        if (key !== 'inventoryOpen' && key !== menuState) {
          newState[key] = false;
        }
      });
      newState[menuState] = true;
    }

    // Always ensure Inventory stays open if a submenu inside it is toggled
    if (menuState === 'invmasterOpen' || menuState === 'invstockdetailsOpen' || menuState === 'invreportOpen') {
      newState.inventoryOpen = true;
    }

    return newState;
  });
}



  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged();
    }
  }

  onRouteChanged() {
  document.querySelector('#sidebar').classList.remove('active');
  // Only reset menu open/close states, not businessGroup
  const menuStates = [
    'appsMenuOpen', 'formsOpen', 'inventoryOpen', 'basicUiMenuOpen', 'salesOpen','purchaseOpen',
    'adminOpen', 'formElementsMenuOpen', 'tablesMenuOpen', 'iconsMenuOpen',
    'chartsMenuOpen', 'userPagesMenuOpen', 'errorPagesMenuOpen'
  ];
  menuStates.forEach(i => {
    this.setState({[i]: false});
  });
// Add this inside your Sidebar.js file, preferably at the top or after imports
const sidebarDropdownStyle = {
  background: '#0d2fa6',
  color: '#fff',
  minWidth: 180,
  marginLeft: 10,
  marginTop: 4,
  borderRadius: 8,
  boxShadow: '0 2px 12px rgba(0,0,0,0.12)',
  zIndex: 1050,
  padding: '8px 0'
};
const sidebarDropdownItemStyle = {
  padding: '8px 24px',
  fontSize: 15,
  color: '#fff',
  borderRadius: 4,
  marginBottom: 2
};
  const dropdownPaths = [
  {path:'/apps', state: 'appsMenuOpen'},
  {path:'/forms', state: 'formsOpen'},
  {path:'/crm',state:'crmOpen'},
  {path:'/inventory', state: 'inventoryOpen'},
  {path:'/inventory/invmaster', state:'invmasterOpen'},
  {path:'/inventory/invstockdetails', state:'invstockdetailsOpen'},
  {path:'/inventory/invreport', state:'invreportOpen'},
  {path:'/basic-ui', state: 'basicUiMenuOpen'},
  {path:'/sales',state:'salesOpen'},
  {path:'/administrator',state:'adminOpen'},
  {path:'/purchase',state:'purchaseOpen'},
  {path:'/form-elements', state: 'formElementsMenuOpen'},
  {path:'/tables', state: 'tablesMenuOpen'},
  {path:'/icons', state: 'iconsMenuOpen'},
  {path:'/charts', state: 'chartsMenuOpen'},
  {path:'/user-pages', state: 'userPagesMenuOpen'},
  {path:'/error-pages', state: 'errorPagesMenuOpen'},
];



  dropdownPaths.forEach((obj => {
    if (this.isPathActive(obj.path)) {
      this.setState({[obj.state] : true})
    }
  }));
}
  render () {
    return (
      <nav className="sidebar sidebar-offcanvas" id="sidebar">
       <div className="sidebar-brand-wrapper d-flex flex-column align-items-center justify-content-center" style={{ height: 120 }}>
  {this.state.businessGroup && (
    <>
      {/* Sidebar collapsed: show shortName as text */}
      {document.body.classList.contains('sidebar-icon-only') ? (
        <span
          style={{
            fontWeight: 700,
            fontSize: 24,
            color: '#fff',
            letterSpacing: 1,
            textAlign: 'center'
          }}
        >
          {this.state.businessGroup.shortName || 'Co'}
        </span>
      ) : (
        // Sidebar expanded: show logo and shortName
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          {this.state.businessGroup.logoUrl && (
            <img
              src={this.state.businessGroup.logoUrl}
              alt="logo"
              style={{
                height: 100,
                width: 100,
                objectFit: 'contain',
                borderRadius: 8
              }}
            />
          )}
          <span
            style={{
              fontWeight: 700,
              fontSize: 20,
              color: '#fff',
              letterSpacing: 1
            }}
          >
            {this.state.businessGroup.shortName}
          </span>
        </div>
      )}
    </>
  )}
</div>

        <ul className="nav">

          <li className={ this.isPathActive('/dashboard') ? 'nav-item active' : 'nav-item' }>
            <Link className="nav-link" to="/dashboard">
              <i className="mdi mdi-television menu-icon"></i>
              <span className="menu-title"><Trans>Dashboard</Trans></span>
            </Link>
          </li>
          <li className={ this.isPathActive('/crm') ? 'nav-item active' : 'nav-item' }>
            <div className={ this.state.crmOpen ? 'nav-link menu-expanded' : 'nav-link' } onClick={ () => this.toggleMenuState('crmOpen') } data-toggle="collapse">
              <i className="mdi mdi-cart-outline menu-icon"></i>
              <span className="menu-title"><Trans>CRM</Trans></span>
              <i className="menu-arrow"></i>
            </div>
