import React, { Component } from 'react';
import { Form } from 'react-bootstrap';

import { Dropdown } from 'react-bootstrap';
class LeadForm extends Component {
  state = {
    leads: [],
    formVisible: false,
    editingIndex: null,
    formData: {
      leadNo: '',
      project: '',
      organization: '',
      client: '',
      product: '',
      status: 'Open',
      source: 'Call',
    },
  };

  handleChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value },
    }));
  };

  toggleForm = (editIndex = null) => {
    const { leads } = this.state;
    if (editIndex !== null) {
      this.setState({ formVisible: true, editingIndex: editIndex, formData: { ...leads[editIndex] } });
    } else {
      this.setState({
        formVisible: true,
        editingIndex: null,
        formData: {
          leadNo: '',
          project: '',
          organization: '',
          client: '',
          product: '',
          status: 'Open',
          source: 'Call',
        },
      });
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { leads, editingIndex, formData } = this.state;
    const updatedLeads = [...leads];
    if (editingIndex !== null) {
      updatedLeads[editingIndex] = formData;
    } else {
      formData.leadNo = `LD${1000 + leads.length}`;
      updatedLeads.push(formData);
    }
    this.setState({ leads: updatedLeads, formVisible: false });
  };

  renderTable = () => (
    <div className="card mt-4">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Lead List</h4>
          <button className="btn btn-primary" onClick={() => this.toggleForm()}>+ Add Lead</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th>Lead No</th><th>Project</th><th>Organization</th><th>Client</th>
                <th>Product</th><th>Status</th><th>Source</th><th>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.leads.map((lead, index) => (
                <tr key={index}>
                  <td>{lead.leadNo}</td>
                  <td>{lead.project}</td>
                  <td>{lead.organization}</td>
                  <td>{lead.client}</td>
                  <td>{lead.product}</td>
                  <td>{lead.status}</td>
                  <td>{lead.source}</td>
                  <td>
                    <Dropdown>
                    <Dropdown.Toggle variant="btn btn-outline-primary" id="dropdownMenuOutlineButton1">
                      Action
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => this.toggleForm(index)}>Edit</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                    
                  </td>
                </tr>
              ))}
              {this.state.leads.length === 0 && (
                <tr><td colSpan="8" className="text-center">No leads available.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderForm = () => {
    const { project, organization, client, product, status, source } = this.state.formData;
    return (
      <form className="card p-4 mt-4" onSubmit={this.handleSubmit}>
        <h4>{this.state.editingIndex !== null ? 'Edit Lead' : 'New Lead'}</h4>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Project</label>
            <input className="form-control" value={project} onChange={(e) => this.handleChange('project', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Organization</label>
            <input className="form-control" value={organization} onChange={(e) => this.handleChange('organization', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Client</label>
            <input className="form-control" value={client} onChange={(e) => this.handleChange('client', e.target.value)} />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Product</label>
            <input className="form-control" value={product} onChange={(e) => this.handleChange('product', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Status</label>
            <select className="form-control" value={status} onChange={(e) => this.handleChange('status', e.target.value)}>
              <option>Open</option><option>In Progress</option><option>Closed</option>
            </select>
          </div>
          <div className="form-group col-md-4">
            <label>Source</label>
            <select className="form-control" value={source} onChange={(e) => this.handleChange('source', e.target.value)}>
              <option>Call</option><option>Direct Meeting</option><option>Others</option>
            </select>
          </div>
        </div>
        <div className="d-flex justify-content-between">
          <button type="submit" className="btn btn-success">Save</button>
          <button type="button" className="btn btn-secondary" onClick={() => this.setState({ formVisible: false })}>Cancel</button>
        </div>
      </form>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.formVisible ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default LeadForm;
