// BillingAmendment.js
import React, { Component } from 'react';
import { db } from '../../../../firebase';
import { collection, getDocs, updateDoc, doc } from 'firebase/firestore';
import { serverTimestamp } from 'firebase/firestore';

class BillingAmendment extends Component {
  state = { cobilling: [], selected: null, editingLineItems: [], loading: false };

  componentDidMount() { this.fetchApproved(); }

  fetchApproved = async () => {
    const snap = await getDocs(collection(db, 'cobilling'));
    const data = snap.docs.map(d => ({ id: d.id, ...d.data() }));
    const approved = data.filter(c => c.status === 'Approved' || c.status === 'Amended');
    this.setState({ cobilling: approved });
  };

  selectCob = (cob) => {
    // Clone line items to an editable copy
    this.setState({ selected: cob, editingLineItems: (cob.lineItems || []).map(li => ({ ...li })) });
  };

  changeQty = (idx, val) => {
    const items = [...this.state.editingLineItems];
    items[idx].qty = val;
    this.setState({ editingLineItems: items });
  };

  applyAmendment = async () => {
    const { selected, editingLineItems } = this.state;
    if (!selected) return;
    if (!window.confirm(`Apply amendment to ${selected.cobillingNo}? This will adjust order recv quantities.`)) return;
    this.setState({ loading: true });
    try {
      // compute delta per line (new - prevApproved)
      const prevApproved = selected.approvedLineItems || selected.lineItems || [];
      const deltas = editingLineItems.map(newLi => {
        const prev = prevApproved.find(p => p.itemCode === newLi.itemCode) || {};
        const prevQty = parseFloat(prev.qty || 0) || 0;
        const newQty = parseFloat(newLi.qty || 0) || 0;
        return { itemCode: newLi.itemCode, delta: (newQty - prevQty) };
      });

      // 1) update order (if any)
      if (selected.customerOrderId) {
        const ordersSnap = await getDocs(collection(db, 'orders'));
        const orderDoc = ordersSnap.docs.find(d => (d.data().orderNo || '') === (selected.customerOrderId || ''));
        if (orderDoc) {
          const orderId = orderDoc.id;
          const orderData = orderDoc.data();
          const updatedLineItems = (orderData.lineItems || []).map(oli => {
            const match = deltas.find(d => d.itemCode === oli.itemCode);
            if (match) {
              const prevRecv = parseFloat(oli.recvQty || 0) || 0;
              const newRecv = Math.max(0, prevRecv + parseFloat(match.delta || 0));
              oli.recvQty = newRecv;
              oli.remainingQty = Math.max(0, (parseFloat(oli.qty || oli.orderQty || 0) - newRecv));
            }
            return oli;
          });
          let anyRecv = updatedLineItems.some(li => (parseFloat(li.recvQty || 0) > 0));
          let allDone = updatedLineItems.every(li => (parseFloat(li.qty || li.orderQty || 0) <= (parseFloat(li.recvQty || 0))));
          const newOrderStatus = allDone ? 'Completed' : (anyRecv ? 'Partial' : orderData.status || 'Entered');

          await updateDoc(doc(db, 'orders', orderId), {
            lineItems: updatedLineItems,
            status: newOrderStatus,
            updatedAt: serverTimestamp()
          });
        }
      }

      // 2) update COB doc: put new lineItems, set status Amended and update approvedLineItems snapshot
      await updateDoc(doc(db, 'cobilling', selected.id), {
        lineItems: editingLineItems,
        status: 'Amended',
        approvedLineItems: editingLineItems,
        amendedAt: serverTimestamp(),
        updatedAt: serverTimestamp()
      });

      alert('Amendment applied.');
      this.setState({ selected: null, editingLineItems: [] });
      this.fetchApproved();
    } catch (err) {
      console.error(err);
      alert(err.message);
    } finally { this.setState({ loading: false }); }
  };

  render() {
    const { cobilling, selected, editingLineItems } = this.state;
    return (
      <div className="container-fluid">
        <h4>Billing Amendment</h4>
        {!selected && (
          <div className="table-responsive">
            <table className="table table-sm table-bordered">
              <thead><tr><th>Bill No</th><th>Order</th><th>Customer</th><th>Date</th><th>Value</th><th>Action</th></tr></thead>
              <tbody>
                {cobilling.map(c => (
                  <tr key={c.id}>
                    <td>{c.cobillingNo}</td>
                    <td>{c.customerOrderId}</td>
                    <td>{c.customer}</td>
                    <td>{c.cobillingDate}</td>
                    <td>{c.billValue}</td>
                    <td><button className="btn btn-sm btn-primary" onClick={() => this.selectCob(c)}>Edit / Apply</button></td>
                  </tr>
                ))}
                {cobilling.length === 0 && <tr><td colSpan="6" className="text-center">No approved bills</td></tr>}
              </tbody>
            </table>
          </div>
        )}

        {selected && (
          <div className="card p-3">
            <h5>Amend: {selected.cobillingNo} â€” Order: {selected.customerOrderId}</h5>
            <table className="table table-sm table-bordered">
              <thead><tr><th>Item</th><th>OrderQty</th><th>PrevApprovedQty</th><th>New Qty</th></tr></thead>
              <tbody>
                {(editingLineItems || []).map((li, i) => {
                  const prev = (selected.approvedLineItems || []).find(p => p.itemCode === li.itemCode) || {};
                  return (
                    <tr key={i}>
                      <td>{li.itemCode} - {li.itemDescription}</td>
                      <td>{li.orderQty || li.qty || '-'}</td>
                      <td>{prev.qty || '-'}</td>
                      <td>
                        <input type="number" className="form-control form-control-sm" value={li.qty || ''} onChange={e => this.changeQty(i, e.target.value)} />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
            <div className="text-right">
              <button className="btn btn-secondary mr-2" onClick={() => this.setState({ selected: null, editingLineItems: [] })}>Cancel</button>
              <button className="btn btn-warning" onClick={this.applyAmendment}>Apply Amendment</button>
            </div>
          </div>
        )}
      </div>
    );
  }
}

export default BillingAmendment;
