import React, { Component } from "react";
import { Card, Row, Col, ButtonGroup, Button } from "react-bootstrap";
import { db } from "../../firebase";
import { collection, onSnapshot } from "firebase/firestore";
import { Line, Bar } from "react-chartjs-2";
import "chart.js";

class Dashboard extends Component {
  state = {
    activities: [],
    lineData: null,
    barData: null,
    filter: "1M", // default filter
  };

  componentDidMount() {
    onSnapshot(collection(db, "activities"), (snapshot) => {
      const activities = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      this.setState({ activities }, () => {
        this.buildLineChart();
        this.buildBarChart();
      });
    });
  }

  buildLineChart = () => {
    const { activities } = this.state;
    const grouped = {};

    activities.forEach((a) => {
      const date = a.date;
      if (!grouped[date]) grouped[date] = {};
      if (!grouped[date][a.source]) grouped[date][a.source] = 0;
      grouped[date][a.source]++;
    });

    const labels = Object.keys(grouped).sort();
    const sources = ["Call", "Meeting", "Demo", "Email", "Whatsapp"];

    const datasets = sources.map((src, i) => ({
      label: src,
      data: labels.map((d) => grouped[d][src] || 0),
      borderColor: this.getColor(i),
      backgroundColor: this.getGradient(this.getColor(i, 0.5)),
      fill: true,
      tension: 0.4, // smooth curves
      borderWidth: 2,
      pointRadius: 4,
      pointHoverRadius: 6,
    }));

    this.setState({ lineData: { labels, datasets } });
  };

  buildBarChart = () => {
    const { activities } = this.state;
    const grouped = {};

    activities.forEach((a) => {
      const company = a.acname || "Unknown";
      if (!grouped[company]) grouped[company] = {};
      if (!grouped[company][a.source]) grouped[company][a.source] = 0;
      grouped[company][a.source]++;
    });

    const labels = Object.keys(grouped);
    const sources = ["Call", "Meeting", "Demo", "Email", "Whatsapp"];

    const datasets = sources.map((src, i) => ({
      label: src,
      data: labels.map((c) => grouped[c][src] || 0),
      backgroundColor: this.getColor(i, 0.6),
      borderRadius: 8, // rounded bars
    }));

    this.setState({ barData: { labels, datasets } });
  };

  getColor = (i, alpha = 1) => {
    const colors = [
      `rgba(99, 102, 241, ${alpha})`, // Indigo
      `rgba(34, 197, 94, ${alpha})`,  // Green
      `rgba(239, 68, 68, ${alpha})`,  // Red
      `rgba(234, 179, 8, ${alpha})`,  // Yellow
      `rgba(59, 130, 246, ${alpha})`, // Blue
    ];
    return colors[i % colors.length];
  };

  getGradient = (color) => {
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");
    const gradient = ctx.createLinearGradient(0, 0, 0, 200);
    gradient.addColorStop(0, color);
    gradient.addColorStop(1, "rgba(255,255,255,0)");
    return gradient;
  };

  render() {
    const { lineData, barData, filter } = this.state;

    return (
      <div className="container mt-4">
        <Row>
          <Col md={12}>
            <Card className="p-3 shadow-sm bg-white border-0 mb-4">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <h5>Sales Statistics Overview</h5>
                  <p>Lorem ipsum is placeholder text commonly used</p>
                </div>
                <ButtonGroup>
                  {["1D", "5D", "1M", "1Y"].map((f) => (
                    <Button
                      key={f}
                      size="sm"
                      variant={filter === f ? "primary" : "light"}
                      onClick={() => this.setState({ filter: f })}
                    >
                      {f}
                    </Button>
                  ))}
                </ButtonGroup>
              </div>

              {lineData && (
                <Line
                  data={lineData}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: "top" },
                      tooltip: {
                        mode: "index",
                        intersect: false,
                        backgroundColor: "#111827",
                        titleColor: "#fff",
                        bodyColor: "#fff",
                      },
                    },
                    interaction: { mode: "nearest", axis: "x", intersect: false },
                    scales: {
                      x: { grid: { display: false } },
                      y: { beginAtZero: true, grid: { color: "#f3f4f6" } },
                    },
                  }}
                  height={100}
                />
              )}
            </Card>
          </Col>

          <Col md={12}>
            <Card className="p-3 shadow-sm bg-white border-0">
              <h5>Market Overview</h5>
              <p>Lorem ipsum dolor sit amet consectetur.</p>
              {barData && (
                <Bar
                  data={barData}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: "top" },
                      tooltip: {
                        callbacks: {
                          label: (ctx) => `$${ctx.raw} USD`,
                        },
                      },
                    },
                    scales: {
                      x: { stacked: true, grid: { display: false } },
                      y: { stacked: true, beginAtZero: true },
                    },
                  }}
                  height={100}
                />
              )}
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Dashboard;
