import React, { Component } from "react";
import {db} from "../../../../firebase";

class QuoteApproval extends Component {
  state = {
    quotes: [],
    selectedQuote: null,
  };

  componentDidMount() {
    this.fetchQuotes();
  }

  fetchQuotes = () => {
    db.collection("quotes").get().then((snapshot) => {
      const quotes = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      this.setState({ quotes });
    });
  };

  openPreview = (quote) => {
    this.setState({ selectedQuote: quote });
  };

  updateStatus = (id, status) => {
    db.collection("quotes").doc(id).update({ status })
      .then(() => {
        alert(`Quote ${status}`);
        this.fetchQuotes();
        this.setState({ selectedQuote: null });
      });
  };

  render() {
    const { quotes, selectedQuote } = this.state;

    return (
      <div>
        <h3>Quote Approval</h3>

        {!selectedQuote && (
          <table className="table">
            <thead>
              <tr>
                <th>ID</th><th>Customer</th><th>Status</th><th>Action</th>
              </tr>
            </thead>
            <tbody>
              {quotes.map(q => (
                <tr key={q.id}>
                  <td>{q.id}</td>
                  <td>{q.customerName}</td>
                  <td>{q.status}</td>
                  <td>
                    <button onClick={() => this.openPreview(q)}>Preview</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {selectedQuote && (
          <div>
            <h4>Quote Preview - {selectedQuote.id}</h4>
            <p>Customer: {selectedQuote.customerName}</p>
            <p>Total: {selectedQuote.total}</p>
            <p>Status: {selectedQuote.status}</p>

            {/* 👇 Show only in Approval page */}
            <button onClick={() => this.updateStatus(selectedQuote.id, "Approved")}>
              Approve
            </button>
            <button onClick={() => this.updateStatus(selectedQuote.id, "Rejected")}>
              Reject
            </button>
            <button onClick={() => this.setState({ selectedQuote: null })}>
              Back to List
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default QuoteApproval;
