import React, { Component } from 'react';
import { db } from '../../../../firebase';
import { collection, getDocs, addDoc, updateDoc, doc, serverTimestamp } from 'firebase/firestore';

class CustomerOrderBilling extends Component {
  state = {
    invoices: [],
    orders: [],
    customers: [],
    taxGroups: [],
    despatchModes: [],
    paymentTerms: [],
    activeTab: 0,
    showForm: false,
    editingId: null,
    showOrderOverlay: false,
    orderOverlaySearch: '',
    showTaxOverlay: false,
    currentTaxIdx: null,
    notes: '',
    formData: this.getEmptyInvoiceForm()
  };

  getEmptyInvoiceForm() {
    return {
      invoiceNo: '',
      invoiceDate: new Date().toISOString().split('T')[0],
      invoiceType: 'Standard',
      customerOrderId: '',
      customerOrderCompany: '',
      customerOrderDate: '',
      customer: '',
      status: 'Entered',
      currency: '',
      billTo: '',
      shipTo: '',
      invoiceValue: '',
      lineItems: [],
      despatchMode: '',
      paymentTerms: '',
      freightCharges: '',
      freighttaxAmount: '',
      packingCharges: '',
      notes: ''
    };
  }

  componentDidMount() {
    this.fetchInvoices();
    this.fetchOrders();
    this.fetchCustomers();
    this.fetchTaxGroups();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();
  }

  fetchInvoices = async () => {
    const snap = await getDocs(collection(db, 'invoices'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ invoices: data.reverse() });
  };

  fetchOrders = async () => {
    const snap = await getDocs(collection(db, 'orders'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ orders: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchTaxGroups = async () => {
    const snap = await getDocs(collection(db, 'taxGroups'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ taxGroups: data });
  };

  fetchDespatchModes = async () => {
    const snap = await getDocs(collection(db, 'modes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ despatchModes: data });
  };

  fetchPaymentTerms = async () => {
    const snap = await getDocs(collection(db, 'paymentTerms'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ paymentTerms: data });
  };

  showOrderOverlay = () => this.setState({ showOrderOverlay: true, orderOverlaySearch: '' });
  hideOrderOverlay = () => this.setState({ showOrderOverlay: false, orderOverlaySearch: '' });

  selectOrderForInvoice = (order) => {
    const customerObj = this.state.customers.find(
      c => c.custname === order.customer || c.custcode === order.customer
    );
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        customerOrderId: order.orderNo,
        customerOrderCompany: order.customer,
        customerOrderDate: order.orderDate,
        customer: order.customer,
        billTo: customerObj ? this.formatAddress(customerObj.billTo) : '',
        shipTo: customerObj ? this.formatAddress(customerObj.shipTo) : '',
        currency: customerObj ? customerObj.currency || '' : '',
        invoiceValue: order.orderValue || '',
        despatchMode: order.despatchMode || '',
        paymentTerms: order.paymentTerms || '',
        freightCharges: order.freightCharges || '',
        freighttaxAmount: order.freighttaxAmount || '',
        packingCharges: order.packingCharges || '',
        lineItems: (order.lineItems || []).map(item => ({
          itemCode: item.itemCode || '',
          itemDescription: item.itemDescription || '',
          custPartCode: item.custPartCode || '',
          hsnCode: item.hsnCode || '',
          locator: item.locator || '',
          uom: item.uom || '',
          onHand: item.onHand || 0,
          unitPrice: item.unitPrice || 0,
          orderQty: item.orderQty || 0,
          recvQty: item.recvQty || 0,
          qty: item.qty || item.orderQty || 1,
          taxGroupNames: item.taxGroupNames || [],
          taxAmt: item.taxAmt || 0,
          total: this.calcItemTotal(item.qty || item.orderQty || 1, item.unitPrice || 0, item.taxAmt || 0)
        }))
      },
      showOrderOverlay: false
    }));
  };

  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleLineItemChange = (idx, field, value) => {
    const updatedItems = [...this.state.formData.lineItems];
    updatedItems[idx] = { ...updatedItems[idx], [field]: value };
    // Only qty is editable, recalculate total and taxAmt
    if (field === 'qty') {
      updatedItems[idx].total = this.calcItemTotal(value, updatedItems[idx].unitPrice, updatedItems[idx].taxAmt);
    }
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        lineItems: updatedItems
      }
    }));
  };

  calcItemTotal = (qty, unitPrice, taxAmt) => {
    qty = parseFloat(qty) || 0;
    unitPrice = parseFloat(unitPrice) || 0;
    taxAmt = parseFloat(taxAmt) || 0;
    return ((qty * unitPrice) + taxAmt).toFixed(2);
  };

  toggleTaxGroupSelection = (groupName, lineIdx, isChecked) => {
    const formData = { ...this.state.formData };
    const item = formData.lineItems[lineIdx];
    if (!item.taxGroupNames) item.taxGroupNames = [];
    if (isChecked) {
      if (!item.taxGroupNames.includes(groupName)) {
        item.taxGroupNames.push(groupName);
      }
    } else {
      item.taxGroupNames = item.taxGroupNames.filter(g => g !== groupName);
    }
    // For demo, just set taxAmt as 18% of (qty*unitPrice) if GST 18% selected
    if (item.taxGroupNames.includes('GST 18%')) {
      item.taxAmt = ((item.qty || 0) * (item.unitPrice || 0) * 0.18).toFixed(2);
    } else {
      item.taxAmt = 0;
    }
    item.total = this.calcItemTotal(item.qty, item.unitPrice, item.taxAmt);
    this.setState({ formData });
  };

  handleTabChange = (idx) => this.setState({ activeTab: idx });

  handleNotesChange = (e) => this.setState({ notes: e.target.value });

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, invoices, notes } = this.state;
    if (!formData.customer) return alert("Customer is required");
    if (formData.lineItems.length === 0) return alert("At least one line item is required");

    const saveData = {
      ...formData,
      notes,
      status: "Entered",
      createdAt: serverTimestamp()
    };

    if (editingId) {
      await updateDoc(doc(db, "invoices", editingId), saveData);
    } else {
      saveData.invoiceNo = `INV${1000 + invoices.length}`;
      await addDoc(collection(db, "invoices"), saveData);
    }

    this.setState({
      showForm: false,
      editingId: null,
      formData: this.getEmptyInvoiceForm(),
      notes: ''
    });
    this.fetchInvoices();
  };

  renderOrderOverlay = () => {
    const { orders, orderOverlaySearch } = this.state;
    const filtered = orders.filter(o =>
      (o.orderNo || '').toLowerCase().includes(orderOverlaySearch.toLowerCase()) ||
      (o.customer || '').toLowerCase().includes(orderOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="custom-overlay-title">Select Customer Order</div>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Search by ID or Company..."
            value={orderOverlaySearch}
            onChange={e => this.setState({ orderOverlaySearch: e.target.value })}
          />
          <div style={{ maxHeight: 300, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Company</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {filtered.map((o, i) => (
                  <tr key={o.id || i} onClick={() => this.selectOrderForInvoice(o)} style={{ cursor: 'pointer' }}>
                    <td>{o.orderNo}</td>
                    <td>{o.customer}</td>
                    <td>{o.orderDate}</td>
                  </tr>
                ))}
                {filtered.length === 0 && (
                  <tr>
                    <td colSpan={3} className="text-center">No orders found</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          <button className="btn btn-secondary btn-sm mt-2" onClick={this.hideOrderOverlay}>Cancel</button>
        </div>
      </div>
    );
  };

  renderTaxOverlay = () => {
    const { taxGroups, currentTaxIdx, formData } = this.state;
    if (currentTaxIdx === null) return null;
    const item = formData.lineItems[currentTaxIdx];
    const selected = new Set(item.taxGroupNames || []);
    return (
      <div style={{
        position: 'fixed', zIndex: 1000, top: '10%', left: '15%',
        background: '#fff', border: '1px solid #ccc', padding: '20px',
        boxShadow: '0 0 10px rgba(0,0,0,0.3)', width: '70%',
        maxHeight: '70vh', overflowY: 'auto'
      }}>
        <h5>Select Tax Groups</h5>
        <table className="table table-sm table-bordered">
          <thead>
            <tr><th></th><th>Group</th><th>Components</th><th>%</th></tr>
          </thead>
          <tbody>
            {taxGroups.map(tg => (
              <tr key={tg.groupName}>
                <td>
                  <input
                    type="checkbox"
                    checked={selected.has(tg.groupName)}
                    onChange={e =>
                      this.toggleTaxGroupSelection(tg.groupName, currentTaxIdx, e.target.checked)
                    }
                  />
                </td>
                <td>{tg.groupName}</td>
                <td>{tg.lineItems.map(li => li.component).join(', ')}</td>
                <td>{tg.lineItems.map(li => li.percentOrAmt).join(', ')}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="text-right mt-3">
          <button className="btn btn-sm btn-success" onClick={() => this.setState({ showTaxOverlay: false, currentTaxIdx: null })}>
            Done
          </button>
        </div>
      </div>
    );
  };

  renderTabs = () => {
    const { activeTab } = this.state;
    const tabNames = ['Billing Details', 'Commercial Details', 'Notes'];
    return (
      <ul className="nav nav-tabs mb-3">
        {tabNames.map((tab, idx) => (
          <li className="nav-item" key={tab}>
            <button
              type="button"
              className={`nav-link${activeTab === idx ? ' active' : ''}`}
              onClick={() => this.handleTabChange(idx)}
              style={{ color: '#007bff', background: activeTab === idx ? '#e9ecef' : '#fff', border: '1px solid #dee2e6', borderBottom: activeTab === idx ? 'none' : undefined }}
            >
              {tab}
            </button>
          </li>
        ))}
      </ul>
    );
  };

  renderBillingTab = () => {
    const { formData, showOrderOverlay, showTaxOverlay, currentTaxIdx } = this.state;
    return (
      <div>
        <div className="form-row">
          <div className="form-group col-md-3">
            <label>Invoice No</label>
            <input type="text" className="form-control" value={formData.invoiceNo} onChange={e => this.handleInputChange('invoiceNo', e.target.value)} />
          </div>
          <div className="form-group col-md-3">
            <label>Invoice Date</label>
            <input type="date" className="form-control" value={formData.invoiceDate} onChange={e => this.handleInputChange('invoiceDate', e.target.value)} />
          </div>
          <div className="form-group col-md-3">
            <label>Customer Order</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                value={formData.customerOrderId}
                readOnly
                onClick={this.showOrderOverlay}
                style={{ cursor: 'pointer' }}
              />
              <div className="input-group-append">
                <button className="btn btn-outline-secondary" type="button" onClick={this.showOrderOverlay}>Select</button>
              </div>
            </div>
            <small>
              {formData.customerOrderCompany && (
                <>Company: {formData.customerOrderCompany} | Date: {formData.customerOrderDate}</>
              )}
            </small>
          </div>
          <div className="form-group col-md-3">
            <label>Customer</label>
            <input type="text" className="form-control" value={formData.customer} readOnly />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-3">
            <label>Invoice Value</label>
            <input type="number" className="form-control" value={formData.invoiceValue} readOnly />
          </div>
          <div className="form-group col-md-3">
            <label>Currency</label>
            <input type="text" className="form-control" value={formData.currency} readOnly />
          </div>
          <div className="form-group col-md-3">
            <label>Status</label>
            <input type="text" className="form-control" value={formData.status} readOnly />
          </div>
        </div>
        <div className="table-responsive mt-3">
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Item Code</th>
                <th>Item Desc</th>
                <th>Cust Part Code</th>
                <th>HSN/SAC</th>
                <th>Locator</th>
                <th>UOM</th>
                <th>On Hand</th>
                <th>Unit Price</th>
                <th>Order Qty</th>
                <th>Recv Qty</th>
                <th>Quantity</th>
                <th>Tax Group</th>
                <th>Tax Amt</th>
                <th>Item Total</th>
              </tr>
            </thead>
            <tbody>
              {(formData.lineItems || []).map((item, idx) => (
                <tr key={idx}>
                  <td>{item.itemCode}</td>
                  <td>{item.itemDescription}</td>
                  <td>{item.custPartCode}</td>
                  <td>{item.hsnCode}</td>
                  <td>{item.locator}</td>
                  <td>{item.uom}</td>
                  <td>{item.onHand}</td>
                  <td>{item.unitPrice}</td>
                  <td>{item.orderQty}</td>
                  <td>{item.recvQty}</td>
                  <td>
                    <input
                      type="number"
                      className="form-control"
                      value={item.qty}
                      min="1"
                      style={{ width: 80 }}
                      onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                    />
                  </td>
                  <td>
                    <button
                      type="button"
                      className="btn btn-outline-primary btn-sm"
                      onClick={() => this.setState({ showTaxOverlay: true, currentTaxIdx: idx })}
                    >
                      Select Tax
                    </button>
                    <div style={{ fontSize: '10px', marginTop: '4px' }}>
                      {(item.taxGroupNames || []).join(', ') || '-'}
                    </div>
                  </td>
                  <td>{item.taxAmt}</td>
                  <td>{item.total}</td>
                </tr>
              ))}
              {(!formData.lineItems || formData.lineItems.length === 0) && (
                <tr>
                  <td colSpan="14" className="text-center">No items</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        {showOrderOverlay && this.renderOrderOverlay()}
        {showTaxOverlay && this.renderTaxOverlay()}
      </div>
    );
  };

  renderCommercialTab = () => {
    const { formData } = this.state;
    return (
      <div>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Despatch Mode</label>
            <input type="text" className="form-control" value={formData.despatchMode} onChange={e => this.handleInputChange('despatchMode', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Payment Terms</label>
            <input type="text" className="form-control" value={formData.paymentTerms} onChange={e => this.handleInputChange('paymentTerms', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Currency</label>
            <input type="text" className="form-control" value={formData.currency} readOnly />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Freight Charges</label>
            <input type="number" className="form-control" value={formData.freightCharges} onChange={e => this.handleInputChange('freightCharges', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Freight Tax Amount</label>
            <input type="number" className="form-control" value={formData.freighttaxAmount} onChange={e => this.handleInputChange('freighttaxAmount', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Packing Charges</label>
            <input type="number" className="form-control" value={formData.packingCharges} onChange={e => this.handleInputChange('packingCharges', e.target.value)} />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Bill To</label>
            <textarea className="form-control" rows="2" value={formData.billTo} readOnly />
          </div>
          <div className="form-group col-md-6">
            <label>Ship To</label>
            <textarea className="form-control" rows="2" value={formData.shipTo} readOnly />
          </div>
        </div>
      </div>
    );
  };

  renderNotesTab = () => (
    <div>
      <div className="form-group">
        <label>Notes / Remarks</label>
        <textarea className="form-control" rows="4" value={this.state.notes} onChange={this.handleNotesChange} />
      </div>
    </div>
  );

  renderForm = () => {
    const { activeTab } = this.state;
    return (
      <div className="card full-height">
        <form className="form-sample" onSubmit={this.handleSubmit}>
          <div style={{ flex: 1, overflowY: 'auto', padding: '24px' }}>
            <h4 className="mb-3">Customer Order Billing</h4>
            {this.renderTabs()}
            {activeTab === 0 && this.renderBillingTab()}
            {activeTab === 1 && this.renderCommercialTab()}
            {activeTab === 2 && this.renderNotesTab()}
            <div className="text-right mt-3">
              <button type="submit" className="btn btn-success">Save Invoice</button>
              <button type="button" className="btn btn-secondary ml-2" onClick={() => this.setState({ showForm: false, editingId: null, formData: this.getEmptyInvoiceForm(), notes: '' })}>Cancel</button>
            </div>
          </div>
        </form>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Customer Order Billing</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Invoice</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr>
                <th>Invoice No</th>
                <th>Customer Order</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Invoice Value</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.invoices.map((inv, i) => (
                <tr key={i}>
                  <td>{inv.invoiceNo}</td>
                  <td>{inv.customerOrderId}</td>
                  <td>{inv.customer}</td>
                  <td>{inv.invoiceDate}</td>
                  <td>{inv.invoiceValue}</td>
                  <td>{inv.status}</td>
                </tr>
              ))}
              {this.state.invoices.length === 0 && (
                <tr><td colSpan="6" className="text-center">No invoices found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default CustomerOrderBilling;
