import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';

class Quote extends Component {
  state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    products: [],
    showForm: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    formData: {
      quoteNo: '',
      quoteDate: new Date().toISOString().split('T')[0],
      quoteType: 'Standard',
      customer: '',
      status: 'Entered', // Static 'Entered'
      choose: 'Service',
      qrefNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      quoteValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  // Helper to format address for Bill To / Ship To
  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();
    this.fetchProducts();
  }

  componentDidUpdate(prevProps, prevState) {
    // Customer details auto-fill
    if (
      this.state.showForm &&
      this.state.customers.length > 0 &&
      this.state.formData.customer &&
      (prevState.formData.customer !== this.state.formData.customer ||
       (!this.state.formData.billTo && !this.state.formData.shipTo))
    ) {
      const customerObj = this.state.customers.find(
        c => c.custname === this.state.formData.customer || c.custcode === this.state.formData.customer
      );
      if (customerObj) {
        this.setState(prev => ({
          formData: {
            ...prev.formData,
            billTo: this.formatAddress(customerObj.billTo),
            shipTo: this.formatAddress(customerObj.shipTo),
            currency: customerObj.currency || '',
            // paymentTerms: customerObj.paymentTerms || '', // No auto-fill for payment terms
            // despatchMode: customerObj.despatchMode || '' // No auto-fill for despatch mode
          }
        }));
      }
    }

    // Freight and Tax calculation based on impExp
    const { impExp, freightCharges, taxPercent } = this.state.formData;
    if (['None', 'CIF'].includes(impExp)) {
      const freight = parseFloat(freightCharges) || 0;
      const tax = parseFloat(taxPercent) || 0;
      const taxAmount = (freight * tax) / 100;
      const quoteValue = freight + taxAmount;

      if (this.state.formData.taxAmount !== taxAmount.toFixed(2) || this.state.formData.quoteValue !== quoteValue.toFixed(2)) {
        this.setState(prev => ({
          formData: {
            ...prev.formData,
            taxAmount: taxAmount.toFixed(2),
            quoteValue: quoteValue.toFixed(2)
          }
        }));
      }
    } else if (impExp === 'FOB') {
      // Reset relevant fields if FOB is selected
      if (this.state.formData.freightCharges !== '' || this.state.formData.taxPercent !== '' ||
          this.state.formData.taxAmount !== '' || this.state.formData.packingCharges !== '') {
        this.setState(prev => ({
          formData: {
            ...prev.formData,
            freightCharges: '',
            taxPercent: '',
            taxAmount: '',
            packingCharges: '',
            quoteValue: '' // Clear quote value as well if it depends on these
          }
        }));
      }
    }
  }

  fetchQuotes = async () => {
    const snap = await getDocs(collection(db, 'quotes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ quotes: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };

  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleLineItemChange = (idx, field, value) => {
    this.setState(prev => {
      const lineItems = [...prev.formData.lineItems];
      lineItems[idx][field] = value;
      if (field === 'qty' || field === 'unitPrice') {
        const qty = parseFloat(lineItems[idx].qty) || 0;
        const unitPrice = parseFloat(lineItems[idx].unitPrice) || 0;
        lineItems[idx].total = (qty * unitPrice).toFixed(2); // Calculate total
      }
      return { formData: { ...prev.formData, lineItems } };
    });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, quotes } = this.state;
    if (!formData.customer) return alert("Customer is required");
    if (formData.lineItems.length === 0) return alert("At least one line item is required");

    if (editingId) {
      await updateDoc(doc(db, 'quotes', editingId), formData);
    } else {
      formData.quoteNo = `QT${1000 + quotes.length}`;
      await addDoc(collection(db, 'quotes'), formData);
    }
    this.setState({ showForm: false, editingId: null });
    this.fetchQuotes();
  };

  loadQuoteForEdit = (quote) => {
    this.setState({
      formData: {
        ...quote,
        lineItems: Array.isArray(quote.lineItems) ? quote.lineItems : [] // Ensure array
      },
      editingId: quote.id,
      showForm: true,
      activeTab: 'co'
    }, () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = quote.customer;
      }
    });
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    if (this.state.overlayType === 'customer') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          customer: value.custname || value.custcode || '', // Use custname or custcode
          billTo: this.formatAddress(value.billTo),
          shipTo: this.formatAddress(value.shipTo),
          currency: value.currency || '',
          // despatchMode: value.despatchMode || '', // No auto-fill for despatch mode
          // paymentTerms: value.paymentTerms || '' // No auto-fill for payment terms
        },
        overlayType: '',
        overlaySearch: ''
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = value.custname || value.custcode || '';
        }
      });
    }
  };

  renderOverlay = () => {
    const { overlayType, overlaySearch, customers } = this.state;
    if (overlayType === 'customer') {
      return (
        <div className="custom-overlay">
          <div className="custom-overlay-content">
            <div className="custom-overlay-title">Select Customer</div>
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Search customer..."
              value={overlaySearch}
              onChange={e => this.setState({ overlaySearch: e.target.value })}
            />
            <div style={{ maxHeight: 300, overflowY: 'auto' }}>
              <table className="table table-bordered table-sm">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Code</th>
                    <th>Short Name</th>
                  </tr>
                </thead>
                <tbody>
                  {customers
                    .filter(c =>
                      (c.custname || '').toLowerCase().includes((overlaySearch || '').toLowerCase()) ||
                      (c.custcode || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
                    )
                    .map((c, i) => (
                      <tr key={c.id || i} onClick={() => this.selectOverlayValue(c)} style={{ cursor: 'pointer' }}>
                        <td>{c.custname}</td>
                        <td>{c.custcode}</td>
                        <td>{c.custshortName}</td>
                      </tr>
                    ))}
                  {customers.length === 0 && (
                    <tr>
                      <td colSpan="3" className="text-center">No records found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <button className="btn btn-secondary btn-sm mt-2" onClick={this.hideOverlay}>Cancel</button>
          </div>
        </div>
      );
    }
    return null;
  };

  renderProductOverlay = () => {
    const { products, productOverlaySearch, selectedProductIds } = this.state;
    const filtered = products.filter(p =>
      (p.ptshortName || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.ptdescription || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.itemCode || '').toLowerCase().includes(productOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="d-flex justify-content-between align-items-center mb-2">
            <div className="custom-overlay-title">Select Products</div>
            <button
              className="btn btn-success btn-sm"
              onClick={() => {
                const selectedProducts = products.filter(p => this.state.selectedProductIds.includes(p.id));
                this.setState(prev => ({
                  formData: {
                    ...prev.formData,
                    lineItems: [
                      ...prev.formData.lineItems,
                      ...selectedProducts
                        .filter(p => !prev.formData.lineItems.some(item => item.id === p.id))
                        .map(p => ({
                          id: p.id,
                          itemCode: p.itemCode || '',
                          itemDescription: p.ptdescription || '',
                          itemType: p.itemType || '',
                          materialType: p.materialType || '',
                          onHand: p.onHand || 0,
                          taxGroup: p.taxGroup || '',
                          custPartNo: p.custPartNo || '', // Assuming this is cust part table
                          hsnNo: p.hsnNo || '', // Assuming HSN No.
                          unitPrice: p.unitPrice || 0,
                          qty: 1,
                          total: (p.unitPrice || 0).toFixed(2)
                        }))
                    ]
                  },
                  productOverlayVisible: false,
                  selectedProductIds: []
                }));
              }}
            >Add Selected</button>
          </div>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Search products..."
            value={productOverlaySearch}
            onChange={e => this.setState({ productOverlaySearch: e.target.value })}
          />
          <div style={{ maxHeight: 300, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm">
              <thead>
                <tr>
                  <th></th>
                  <th>Item Code</th>
                  <th>Item Desc</th>
                  <th>Item Type</th>
                  <th>Material Type</th>
                  <th>On Hand</th>
                  <th>Tax Grp</th>
                  <th>Cust Part No</th>
                </tr>
              </thead>
              <tbody>
                {filtered.map(p => (
                  <tr key={p.id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={selectedProductIds.includes(p.id)}
                        onChange={e => {
                          const checked = e.target.checked;
                          this.setState(prev => ({
                            selectedProductIds: checked
                              ? [...prev.selectedProductIds, p.id]
                              : prev.selectedProductIds.filter(id => id !== p.id)
                          }));
                        }}
                      />
                    </td>
                    <td>{p.productId}</td>
                    <td>{p.ptdescription || ''}</td>
                    <td>{p.itemType || ''}</td>
                    <td>{p.materialType || ''}</td>
                    <td>{p.onHand || 0}</td>
                    <td>{p.taxGroup || ''}</td>
                    <td>{p.custPartNo || ''}</td>
                  </tr>
                ))}
                {filtered.length === 0 && (
                  <tr>
                    <td colSpan="8" className="text-center">No products found</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          {/* Add pagination here if needed, for now, it's just a placeholder */}
          <div className="d-flex justify-content-between align-items-center mt-2">
            <span>Page 1 of 1</span>
            <button className="btn btn-secondary btn-sm" onClick={() => this.setState({ productOverlayVisible: false, selectedProductIds: [] })}>Cancel</button>
          </div>
        </div>
      </div>
    );
  };

  showQuotePDF = (quote) => {
    const subtotal = (quote.lineItems || []).reduce((sum, i) => sum + (parseFloat(i.total) || 0), 0);
    const tax = parseFloat(quote.taxAmount) || 0;
    const total = parseFloat(quote.quoteValue) || (subtotal + tax);
    const win = window.open('', '_blank');
    win.document.write(`
      <html>
      <head>
        <title>Quote Preview</title>
        <style>
          body { font-family: Arial; padding: 24px; }
          .header { background: #2ecc40; color: #fff; padding: 10px 20px; font-size: 20px; font-weight: bold; }
          .quote-title { float: right; font-size: 18px; font-weight: bold; }
          .section { margin-top: 20px; }
          .flex { display: flex; justify-content: space-between; }
          .box { width: 45%; }
          table { border-collapse: collapse; width: 100%; margin-top: 16px; }
          th, td { border: 1px solid #ccc; padding: 8px; text-align: left; }
          th { background: #f8f8f8; }
          .total-row td { font-weight: bold; }
          .right { text-align: right; }
        </style>
      </head>
      <body>
        <div class="header">
          Your Company Name
          <span class="quote-title">QUOTE</span>
        </div>
        <div class="section flex">
          <div class="box">
            <b>Bill To</b><br/>
            ${quote.billTo ? quote.billTo.replace(/\n/g, '<br/>') : ''}
          </div>
          <div class="box">
            <b>Ship To</b><br/>
            ${quote.shipTo ? quote.shipTo.replace(/\n/g, '<br/>') : ''}
          </div>
        </div>
        <div class="section flex">
          <div class="box">
            <b>Quote #:</b> ${quote.quoteNo || ''}<br/>
            <b>Quote Date:</b> ${quote.quoteDate || ''}<br/>
            <b>P.O.#:</b> ${quote.qrefNo || ''}
          </div>
        </div>
        <div class="section">
          <table>
            <thead>
              <tr>
                <th>Item Code</th>
                <th>Item Description</th>
                <th>Qty</th>
                <th>Unit Price</th>
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              ${(quote.lineItems || []).map((item, idx) => `
                <tr>
                  <td>${item.itemCode || ''}</td>
                  <td>${item.itemDescription || ''}</td>
                  <td>${item.qty}</td>
                  <td class="right">${item.unitPrice}</td>
                  <td class="right">${item.total}</td>
                </tr>
              `).join('')}
              <tr>
                <td colspan="4" class="right">Subtotal</td>
                <td class="right">${subtotal.toFixed(2)}</td>
              </tr>
              <tr>
                <td colspan="4" class="right">Sales Tax</td>
                <td class="right">${tax.toFixed(2)}</td>
              </tr>
              <tr class="total-row">
                <td colspan="4" class="right">TOTAL</td>
                <td class="right">${total.toFixed(2)}</td>
              </tr>
            </tbody>
          </table>
        </div>
        <div class="section">
          <b>Terms & Conditions</b><br/>
          Payment is due within 15 days.<br/>
          Please make checks payable to: Your Company Name.
        </div>
      </body>
      </html>
    `);
    win.document.close();
  };

  renderQuoteTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Quotes</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Quote</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr style={{ fontSize: '14px' }}>
                <th>Quote No</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Quote Value</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.quotes.map((q, i) => (
                <tr key={i} style={{ fontSize: '14px' }}>
                  <td>
                    <button
                      className="btn btn-link p-0"
                      onClick={() => this.loadQuoteForEdit(q)}
                    >
                      {q.quoteNo}
                    </button>
                  </td>
                  <td>{q.customer}</td>
                  <td>{q.quoteDate}</td>
                  <td>{q.quoteValue}</td>
                  <td><label className="badge badge-info">{q.status}</label></td>
                  <td>
                    <button className="btn btn-sm btn-outline-info" onClick={() => this.showQuotePDF(q)}>Show PDF</button>
                  </td>
                </tr>
              ))}
              {this.state.quotes.length === 0 && (
                <tr><td colSpan="6" className="text-center">No quotes found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderQuoteForm = () => {
    const { formData, overlayType, productOverlayVisible } = this.state;
    const isFOB = formData.impExp === 'FOB';

    return (
      <div>
        <div className="card full-height">
          <div style={{ flex: 1, overflowY: 'auto', padding: '24px' }}>
            <h4 className="mb-3">Quote Form</h4>
            <ul className="nav nav-tabs" role="tablist">
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'co' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'co' })}>CO Details</button>
              </li>
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'commercial' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'commercial' })}>Commercial Terms</button>
              </li>
            </ul>
            <form className="form-sample" onSubmit={this.handleSubmit}>
              <div className="tab-content pt-3">
                {this.state.activeTab === 'co' && (
                  <>
                    <div className="form-row">
                      <div className="form-group col-md-3">
                        <label>Quote No</label>
                        <input type="text" className="form-control form-control-sm" value={formData.quoteNo} onChange={(e) => this.handleInputChange('quoteNo', e.target.value)} />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Date</label>
                        <input type="date" className="form-control form-control-sm" value={formData.quoteDate} onChange={(e) => this.handleInputChange('quoteDate', e.target.value)} />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Type</label>
                        <select className="form-control form-control-sm" value={formData.quoteType} onChange={(e) => this.handleInputChange('quoteType', e.target.value)}>
                          <option>Standard</option>
                          <option>Manual</option>
                        </select>
                      </div>
                      <div className="form-group col-md-3">
                        <label>Status</label>
                        <input type="text" className="form-control form-control-sm" value={formData.status} readOnly />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Customer</label>
                        <div className="input-group input-group-sm">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Choose Customer"
                            ref={this.customerInputRef}
                            value={formData.customer}
                            readOnly
                            onClick={() => this.showOverlay('customer')}
                            style={{ cursor: 'pointer' }}
                          />
                          <div className="input-group-append">
                            <button className="btn btn-outline-secondary btn-sm" type="button" onClick={() => this.showOverlay('customer')}>Select</button>
                          </div>
                        </div>
                      </div>
                      <div className="form-group col-md-3">
                        <label>IMP/EXP</label>
                        <select
                          className="form-control form-control-sm"
                          value={formData.impExp}
                          onChange={(e) => this.handleInputChange('impExp', e.target.value)}
                        >
                          <option>None</option>
                          <option>COB</option>
                          <option>FOB</option>
                          <option>CIF</option>
                        </select>
                      </div>
                      <div className="form-group col-md-2">
                        <label>Ref No.</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.qrefNo}
                          onChange={(e) => this.handleInputChange('qrefNo', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Choose</label>
                        <div className="d-flex align-items-center">
                          <div className="form-check mr-4">
                            <input type="radio" className="form-check-input" name="choose" id="chooseService" value="Service" checked={formData.choose === 'Service'} onChange={(e) => this.handleInputChange('choose', e.target.value)} />
                            <label className="form-check-label" htmlFor="chooseService">Service</label>
                          </div>
                          <div className="form-check">
                            <input type="radio" className="form-check-input" name="choose" id="chooseProduct" value="Product" checked={formData.choose === 'Product'} onChange={(e) => this.handleInputChange('choose', e.target.value)} />
                            <label className="form-check-label" htmlFor="chooseProduct">Product</label>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-3">
                        <label>Currency</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.currency}
                          readOnly
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Conversion Rate</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.conversionRate}
                          onChange={(e) => this.handleInputChange('conversionRate', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Tax Amount</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxAmount}
                          onChange={(e) => this.handleInputChange('taxAmount', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Value</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.quoteValue}
                          onChange={(e) => this.handleInputChange('quoteValue', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="col-lg-12 grid-margin stretch-card">
                      <div className="card shadow">
                        <div className="card-body">
                          <div className="d-flex justify-content-between align-items-center mb-3">
                            <h4 className="card-title">Line Item</h4>
                            <button type="button" className="btn btn-primary btn-sm" onClick={() => this.setState({ productOverlayVisible: true })}>
                              + Add Product
                            </button>
                          </div>
                          <div className="table-responsive">
                            <table className="table table-bordered">
                              <thead>
                                <tr>
                                  <th> Item Code </th>
                                  <th> Item Desc </th>
                                  <th> Cust Part No </th>
                                  <th> HSN No </th>
                                  <th> On Hand </th>
                                  <th> Unit Price </th>
                                  <th> Qty</th>
                                  <th> Total </th>
                                </tr>
                              </thead>
                              <tbody>
                                {formData.lineItems.map((item, idx) => (
                                  <tr key={item.id}>
                                    <td>{item.itemCode}</td>
                                    <td>{item.itemDescription}</td>
                                    <td>{item.custPartNo}</td>
                                    <td>{item.hsnNo}</td>
                                    <td>{item.onHand}</td>
                                    <td>
                                      <input
                                        type="number"
                                        value={item.unitPrice}
                                        onChange={e => this.handleLineItemChange(idx, 'unitPrice', e.target.value)}
                                        style={{ width: 80 }}
                                      />
                                    </td>
                                    <td>
                                      <input
                                        type="number"
                                        min="1"
                                        value={item.qty}
                                        onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                                        style={{ width: 60 }}
                                      />
                                    </td>
                                    <td>{item.total}</td>
                                  </tr>
                                ))}
                                {formData.lineItems.length === 0 && (
                                  <tr>
                                    <td colSpan="9" className="text-center">No items</td>
                                  </tr>
                                )}
                              </tbody>
                            </table>
                          </div>
                          {/* Add pagination for line items here if needed */}
                          <div className="d-flex justify-content-between align-items-center mt-2">
                            <span>Page 1 of 1</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </>
                )}
                {this.state.activeTab === 'commercial' && (
                  <>
                    <div className="form-row">
                      <div className="form-group col-md-6">
                        <label>Bill To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.billTo}
                          onChange={(e) => this.handleInputChange('billTo', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-6">
                        <label>Ship To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.shipTo}
                          onChange={(e) => this.handleInputChange('shipTo', e.target.value)}
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Despatch Mode</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.despatchMode}
                          onChange={(e) => this.handleInputChange('despatchMode', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Payment Terms</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.paymentTerms}
                          onChange={(e) => this.handleInputChange('paymentTerms', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Freight Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.freightCharges}
                          onChange={(e) => this.handleInputChange('freightCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Tax %</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxPercent}
                          onChange={(e) => this.handleInputChange('taxPercent', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Tax Amount</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxAmount}
                          onChange={(e) => this.handleInputChange('taxAmount', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Packing Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.packingCharges}
                          onChange={(e) => this.handleInputChange('packingCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="text-right mt-3">
                      <button type="submit" className="btn btn-success">Save All Details</button>
                    </div>
                  </>
                )}
              </div>
            </form>
            {overlayType && this.renderOverlay()}
            {productOverlayVisible && this.renderProductOverlay()}
          </div>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderQuoteForm() : this.renderQuoteTable()}
      </div>
    );
  }
}

export default Quote;
