import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Collapse } from 'react-bootstrap';
import { Dropdown } from 'react-bootstrap';
import { Trans } from 'react-i18next';
import { db } from '../../firebase';
import { collection,getDocs } from 'firebase/firestore';


class Sidebar extends Component {
  state = {
    businessGroup: null,
  };

  toggleMenuState(menuState) {
    if (this.state[menuState]) {
      this.setState({[menuState] : false});
    } else if(Object.keys(this.state).length === 0) {
      this.setState({[menuState] : true});
    } else {
      Object.keys(this.state).forEach(i => {
        this.setState({[i]: false});
      });
      this.setState({[menuState] : true});
    }
  }


  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged();
    }
  }

  onRouteChanged() {
    document.querySelector('#sidebar').classList.remove('active');
    Object.keys(this.state).forEach(i => {
      this.setState({[i]: false});
    });

    const dropdownPaths = [
      {path:'/apps', state: 'appsMenuOpen'},
      {path:'/forms', state: 'formsOpen'},
      {path:'/inventory', state: 'inventoryOpen'},
      {path:'/basic-ui', state: 'basicUiMenuOpen'},
      {path:'/sales',state:'salesMenuOpen'},
      {path:'/administrator',state:'adminMenuOpen'},
      {path:'/form-elements', state: 'formElementsMenuOpen'},
      {path:'/tables', state: 'tablesMenuOpen'},
      {path:'/icons', state: 'iconsMenuOpen'},
      {path:'/charts', state: 'chartsMenuOpen'},
      {path:'/user-pages', state: 'userPagesMenuOpen'},
      {path:'/error-pages', state: 'errorPagesMenuOpen'},
    ];

    dropdownPaths.forEach((obj => {
      if (this.isPathActive(obj.path)) {
        this.setState({[obj.state] : true})
      }
    }));

  } 
  render () {
    return (
      <nav className="sidebar sidebar-offcanvas" id="sidebar">
        <div className="text-center sidebar-brand-wrapper d-flex align-items-center">

<div className="text-center sidebar-brand-wrapper d-flex align-items-center">
  {this.state.businessGroup && this.state.businessGroup.logoUrl ? (
  <a className="sidebar-brand brand-logo" href="index.html">
    <img
      src={this.state.businessGroup.logoUrl}
      alt="logo"
      style={{ height: 48, width: 48, objectFit: 'contain', borderRadius: 8 }}
    />
    <span style={{ fontWeight: 600, fontSize: 18, marginLeft: 10 ,color: '#fff'}}>
      {this.state.businessGroup.shortName}
    </span>
  </a>
) : (
  <a className="sidebar-brand brand-logo" href="index.html">
    <img src={require("../../assets/images/logo.svg")} alt="logo" />
  </a>
)}

{/* mini logo */}
{this.state.businessGroup && this.state.businessGroup.logoUrl ? (
  <a className="sidebar-brand brand-logo-mini pt-3" href="index.html">
    <img
      src={this.state.businessGroup.logoUrl}
      alt="mini-logo"
      style={{ height: 32, width: 32, objectFit: 'contain', borderRadius: 6 }}
    />
  </a>
) : (
  <a className="sidebar-brand brand-logo-mini pt-3" href="index.html">
    <img src={require("../../assets/images/logo-mini.svg")} alt="logo" />
  </a>
)}

</div>

          <a className="sidebar-brand brand-logo-mini pt-3" href="index.html"><img src={require("../../assets/images/logo-mini.svg" )} alt="logo" /></a>
        </div>
        <ul className="nav">

          <li className={ this.isPathActive('/dashboard') ? 'nav-item active' : 'nav-item' }>
            <Link className="nav-link" to="/dashboard">
              <i className="mdi mdi-television menu-icon"></i>
              <span className="menu-title"><Trans>Dashboard</Trans></span>
            </Link>
          </li>
          <li className={ this.isPathActive('/inventory') ? 'nav-item active' : 'nav-item' }>
            <div className={ this.state.inventoryOpen ? 'nav-link menu-expanded' : 'nav-link' } onClick={ () => this.toggleMenuState('inventoryOpen') } data-toggle="collapse">
              <i className="mdi mdi-poll menu-icon"></i>
              <span className="menu-title"><Trans>Inventory</Trans></span>
              <i className="menu-arrow"></i>
            </div>
            <Collapse in={ this.state.inventoryOpen}>
              <ul className="nav flex-column sub-menu">
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/ProductMaster') ? 'nav-link active' : 'nav-link' } to="/inventory/ProductMaster"><Trans>Product Master</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/UOMPage') ? 'nav-link active' : 'nav-link' } to="/inventory/UOMPage"><Trans>UOM</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/PaymentTerms') ? 'nav-link active' : 'nav-link' } to="/inventory/PaymentTerms"><Trans>Payment Terms</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/LocationPage') ? 'nav-link active' : 'nav-link' } to="/inventory/LocationPage"><Trans>Location</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/WarehousePage') ? 'nav-link active' : 'nav-link' } to="/inventory/WarehousePage"><Trans>Warehouse</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/Locator') ? 'nav-link active' : 'nav-link' } to="/inventory/Locator"><Trans>Locator</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/Taxgroup') ? 'nav-link active' : 'nav-link' } to="/inventory/Taxgroup"><Trans>Tax Group</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/TaxComponent') ? 'nav-link active' : 'nav-link' } to="/inventory/TaxComponent"><Trans>Tax Component</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/CurrencyPage') ? 'nav-link active' : 'nav-link' } to="/inventory/CurrencyPage"><Trans>Currencies</Trans></Link></li>
                <li className="nav-item"> <Link className={ this.isPathActive('/inventory/DespatchMode') ? 'nav-link active' : 'nav-link' } to="/inventory/DespatchMode"><Trans>Despatch Mode</Trans></Link></li>

              </ul>
            </Collapse>
          </li>
          <li className={ this.isPathActive('/sales') ? 'nav-item active' : 'nav-item' }>
            <div className={ this.state.salesOpen ? 'nav-link menu-expanded' : 'nav-link' } onClick={ () => this.toggleMenuState('salesOpen') } data-toggle="collapse">
              <i className="mdi mdi-account-card-details menu-icon"></i>
              <span className="menu-title"><Trans>Sales</Trans></span>
              <i className="menu-arrow"></i>
            </div>
            <Collapse in={ this.state.salesOpen}>
              <ul className="nav flex-column sub-menu">
                <li className="nav-item"> <Link className={ this.isPathActive('/sales/CustomerPage') ? 'nav-link active' : 'nav-link' } to="/sales/CustomerPage"><Trans>Customer</Trans></Link></li>
              </ul>
            </Collapse>
          </li>
          <li className={ this.isPathActive('/administrator') ? 'nav-item active' : 'nav-item' }>
            <div className={ this.state.adminOpen ? 'nav-link menu-expanded' : 'nav-link' } onClick={ () => this.toggleMenuState('adminOpen') } data-toggle="collapse">
              <i className="mdi mdi-account-card-details menu-icon"></i>
              <span className="menu-title"><Trans>Administrator</Trans></span>
              <i className="menu-arrow"></i>
            </div>
            <Collapse in={ this.state.adminOpen}>
              <ul className="nav flex-column sub-menu">
                <li className="nav-item"> <Link className={ this.isPathActive('/administrator/OrganizationPage') ? 'nav-link active' : 'nav-link' } to="/administrator/OrganizationPage"><Trans>Business Group</Trans></Link></li>
              </ul>
            </Collapse>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/panelone/LeadForm">
              <i className="mdi mdi-account-multiple-plus menu-icon"></i>
              <span className="menu-title">Lead</span>
            </Link>
          </li>

          <li className="nav-item">
            <Link className="nav-link" to="/panelone/ProspectForm">
              <i className="mdi mdi-lightbulb menu-icon"></i>
              <span className="menu-title">Prospect</span>
            </Link>
          </li>
           <li className="nav-item">
            <Link className="nav-link" to="/panelone/Quote">
              <i className="mdi mdi-percent menu-icon"></i>
              <span className="menu-title">Quote</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/panelone/Order">
              <i className="mdi mdi-cart menu-icon"></i>
              <span className="menu-title">Order</span>
            </Link>
          </li>

          <li className={ this.isPathActive('/icons') ? 'nav-item active' : 'nav-item' }>
            <div className={ this.state.iconsMenuOpen ? 'nav-link menu-expanded' : 'nav-link' } onClick={ () => this.toggleMenuState('iconsMenuOpen') } data-toggle="collapse">
              <i className="mdi mdi-account-box-outline menu-icon"></i>
              <span className="menu-title"><Trans>Icons</Trans></span>
              <i className="menu-arrow"></i>
            </div>
            <Collapse in={ this.state.iconsMenuOpen }>
              <ul className="nav flex-column sub-menu">
                <li className="nav-item"> <Link className={ this.isPathActive('/icons/mdi') ? 'nav-link active' : 'nav-link' } to="/icons/mdi">Material</Link></li>
              </ul>
            </Collapse>
          </li>
        </ul>
      </nav>
    );
  }

  isPathActive(path) {
    return this.props.location.pathname.startsWith(path);
  }

async componentDidMount() {
  this.onRouteChanged();

  // Load from localStorage first
  const storedBG = localStorage.getItem('businessGroup');
  if (storedBG) {
    this.setState({ businessGroup: JSON.parse(storedBG) });
    window.addEventListener('businessGroupUpdated', this.handleBusinessGroupUpdate);
  } else {
    // Fallback: fetch from Firestore
    const snap = await getDocs(collection(db, 'businessGroups'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ businessGroup: data[0] || null });
    if (data[0]) {
      localStorage.setItem('businessGroup', JSON.stringify(data[0]));
    }
  }

  // Sidebar hover behavior
  const body = document.querySelector('body');
  document.querySelectorAll('.sidebar .nav-item').forEach((el) => {
    el.addEventListener('mouseover', function () {
      if (body.classList.contains('sidebar-icon-only')) {
        el.classList.add('hover-open');
      }
    });
    el.addEventListener('mouseout', function () {
      if (body.classList.contains('sidebar-icon-only')) {
        el.classList.remove('hover-open');
      }
    });
  });
}
omponentWillUnmount() {
  window.removeEventListener('businessGroupUpdated', this.handleBusinessGroupUpdate);
}

handleBusinessGroupUpdate = () => {
  const storedBG = localStorage.getItem('businessGroup');
  if (storedBG) {
    this.setState({ businessGroup: JSON.parse(storedBG) });
  }
};
}

export default withRouter(Sidebar);
