import React, { Component } from "react";
import { Table, Button, Card, Offcanvas, Form } from "react-bootstrap";
import { db } from "../../firebase";
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

class Activity extends Component {
  state = {
    activities: [],
    accounts: [],
    showDrawer: false,
    editingId: null,
    formData: {
      date: new Date().toISOString().split("T")[0],
      acname: "",
      contact: "",
      phone: "",
      email: "",
      source: "",
      remarks: "",
    },
    contactOptions: [],
  };

  componentDidMount() {
    this.fetchActivities();
    this.fetchAccounts();
  }

  fetchActivities = async () => {
    const snap = await getDocs(collection(db, "activities"));
    const activities = snap.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
    this.setState({ activities });
  };

  fetchAccounts = async () => {
    const snap = await getDocs(collection(db, "accounts"));
    const accounts = snap.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
    this.setState({ accounts });
  };

  openDrawer = (activity = null) => {
    if (activity) {
      // editing existing row
      this.setState({
        showDrawer: true,
        editingId: activity.id,
        formData: { ...activity },
      });
    } else {
      // new row
      this.setState({
        showDrawer: true,
        editingId: null,
        formData: {
          date: new Date().toISOString().split("T")[0],
          acname: "",
          contact: "",
          phone: "",
          email: "",
          source: "",
          remarks: "",
        },
      });
    }
  };

  closeDrawer = () => {
    this.setState({ showDrawer: false, editingId: null });
  };

  handleChange = (field, value) => {
    let updated = { ...this.state.formData, [field]: value };

    if (field === "acname") {
      const acc = this.state.accounts.find((a) => a.acname === value);
      let contactOptions = [];
      if (acc) {
        if (acc.contact) {
          contactOptions.push({
            name: acc.contact,
            phone: acc.phone || "",
            email: acc.email || "",
          });
        }
        if (acc.contactPersons && acc.contactPersons.length > 0) {
          contactOptions = contactOptions.concat(acc.contactPersons);
        }
      }
      updated.contact = "";
      updated.phone = "";
      updated.email = "";
      this.setState({ contactOptions });
    }

    if (field === "contact") {
      const cp = this.state.contactOptions.find((c) => c.name === value);
      if (cp) {
        updated.phone = cp.phone || "";
        updated.email = cp.email || "";
      }
    }

    if (field === "remarks" && value.length > 500) return;

    this.setState({ formData: updated });
  };

  saveActivity = async () => {
    const { formData, editingId } = this.state;

    if (editingId) {
      // update existing â†’ replace with updateDoc
      // await updateDoc(doc(db, "activities", editingId), formData);
    } else {
      await addDoc(collection(db, "activities"), formData);
    }

    this.closeDrawer();
    this.fetchActivities();
  };

  deleteRow = async (id) => {
    await deleteDoc(doc(db, "activities", id));
    this.fetchActivities();
  };

  render() {
    const { activities, accounts, showDrawer, formData, contactOptions } =
      this.state;

    return (
      <div className="container-fluid mt-4">
        <Card className="p-3 shadow-sm bg-white border-0">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h3 className="mb-0">Activities</h3>
            <Button variant="primary" onClick={() => this.openDrawer()}>
              + Add Activity
            </Button>
          </div>

          <div className="table-responsive">
            <Table bordered hover className="w-100">
              <thead className="thead-light">
                <tr>
                  <th>Date</th>
                  <th>Account</th>
                  <th>Contact</th>
                  <th>Phone</th>
                  <th>Email</th>
                  <th>Source</th>
                  <th>Remarks</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {activities.map((act) => (
                  <tr key={act.id}>
                    <td>{act.date}</td>
                    <td>{act.acname}</td>
                    <td>{act.contact}</td>
                    <td>{act.phone}</td>
                    <td>{act.email}</td>
                    <td>{act.source}</td>
                    <td style={{ maxWidth: "300px", whiteSpace: "pre-wrap" }}>
                      {act.remarks}
                    </td>
                    <td>
                      <Button
                        size="sm"
                        variant="secondary"
                        onClick={() => this.openDrawer(act)}
                      >
                        Edit
                      </Button>{" "}
                      <Button
                        size="sm"
                        variant="danger"
                        onClick={() => this.deleteRow(act.id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </Card>

        {/* Side Drawer */}
        <Offcanvas
          show={showDrawer}
          onHide={this.closeDrawer}
          placement="end"
          style={{ width: "500px" }}
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>
              {this.state.editingId ? "Edit Activity" : "Add Activity"}
            </Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Date</Form.Label>
                <Form.Control
                  type="date"
                  value={formData.date}
                  onChange={(e) => this.handleChange("date", e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Account</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.acname}
                  onChange={(e) => this.handleChange("acname", e.target.value)}
                >
                  <option value="">Select Account</option>
                  {accounts.map((acc) => (
                    <option key={acc.id} value={acc.acname}>
                      {acc.acname}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Contact</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.contact}
                  onChange={(e) => this.handleChange("contact", e.target.value)}
                  disabled={!formData.acname}
                >
                  <option value="">Select Contact</option>
                  {contactOptions.map((cp, i) => (
                    <option key={i} value={cp.name}>
                      {cp.name}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Phone</Form.Label>
                <Form.Control type="text" value={formData.phone} disabled />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control type="text" value={formData.email} disabled />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Source</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.source}
                  onChange={(e) => this.handleChange("source", e.target.value)}
                >
                  <option value="">Select Source</option>
                  <option value="Call">Call</option>
                  <option value="Meeting">Meeting</option>
                  <option value="Demo">Demo</option>
                  <option value="Mail">Mail</option>
                  <option value="Whatsapp">Whatsapp</option>
                </Form.Control>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Remarks (max 500)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  value={formData.remarks}
                  onChange={(e) => this.handleChange("remarks", e.target.value)}
                />
                <small>{formData.remarks.length}/500</small>
              </Form.Group>

              <div className="d-flex justify-content-end">
                <Button variant="secondary" onClick={this.closeDrawer}>
                  Cancel
                </Button>{" "}
                <Button variant="primary" onClick={this.saveActivity}>
                  Save
                </Button>
              </div>
            </Form>
          </Offcanvas.Body>
        </Offcanvas>
      </div>
    );
  }
}

export default Activity;
