import React, { Component } from 'react';
import { db } from '../../../firebase';
import { collection, addDoc, getDocs, updateDoc, deleteDoc, doc } from 'firebase/firestore';

class CurrencyPage extends Component {
  state = {
    currencies: [],
    formData: { name: '', code: '', conversionRate: '' },
    editingId: null,
    loading: false,
  };

  componentDidMount = async () => {
    this.fetchCurrencies();
  };

  fetchCurrencies = async () => {
    const snap = await getDocs(collection(db, 'currencies'));
    const currencies = snap.docs.map(d => ({ id: d.id, ...d.data() }));
    this.setState({ currencies });
  };

  /** ðŸ” Sync live rates from exchangerate.host */
  syncLiveRates = async () => {
    try {
      this.setState({ loading: true });
      const res = await fetch('https://api.exchangerate.host/latest?base=INR');
      const data = await res.json();

      if (!data.rates) {
        alert('Failed to fetch live rates');
        return;
      }

      // convert API data to array
      const allRates = Object.entries(data.rates).map(([code, rate]) => ({
        code,
        name: code, // you can map names later if needed
        conversionRate: rate,
        base: 'INR',
      }));

      // save to Firestore
      for (const cur of allRates) {
        await addDoc(collection(db, 'currencies'), cur);
      }

      alert('Live rates synced successfully!');
      this.fetchCurrencies();
    } catch (err) {
      console.error(err);
      alert('Error syncing live rates.');
    } finally {
      this.setState({ loading: false });
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ formData: { ...this.state.formData, [name]: value } });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { name, code, conversionRate } = this.state.formData;
    if (!name || !code || !conversionRate) return alert('Fill all fields');

    if (this.state.editingId) {
      await updateDoc(doc(db, 'currencies', this.state.editingId), {
        name, code, conversionRate: parseFloat(conversionRate), base: 'INR'
      });
      alert('Updated!');
    } else {
      await addDoc(collection(db, 'currencies'), {
        name, code, conversionRate: parseFloat(conversionRate), base: 'INR'
      });
      alert('Added!');
    }

    this.setState({ formData: { name: '', code: '', conversionRate: '' }, editingId: null });
    this.fetchCurrencies();
  };

  handleEdit = (cur) => {
    this.setState({ formData: { name: cur.name, code: cur.code, conversionRate: cur.conversionRate }, editingId: cur.id });
  };

  handleDelete = async (id) => {
    if (window.confirm('Delete this currency?')) {
      await deleteDoc(doc(db, 'currencies', id));
      this.fetchCurrencies();
    }
  };

  render() {
    const { currencies, formData, loading } = this.state;

    return (
      <div className="container mt-3">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4>Currency Master (Base: INR)</h4>
          <button
            onClick={this.syncLiveRates}
            className="btn btn-success btn-sm"
            disabled={loading}
          >
            {loading ? 'Syncing...' : 'Sync Live Rates'}
          </button>
        </div>

        {/* Manual Form */}
        <form onSubmit={this.handleSubmit} className="mb-3">
          <div className="row g-2">
            <div className="col-md-3">
              <input
                type="text"
                className="form-control"
                name="name"
                placeholder="Currency Name"
                value={formData.name}
                onChange={this.handleChange}
              />
            </div>
            <div className="col-md-2">
              <input
                type="text"
                className="form-control"
                name="code"
                placeholder="Code (USD)"
                value={formData.code}
                onChange={this.handleChange}
              />
            </div>
            <div className="col-md-2">
              <input
                type="number"
                step="0.0001"
                className="form-control"
                name="conversionRate"
                placeholder="Rate vs INR"
                value={formData.conversionRate}
                onChange={this.handleChange}
              />
            </div>
            <div className="col-md-2">
              <button className="btn btn-primary w-100">
                {this.state.editingId ? 'Update' : 'Add'}
              </button>
            </div>
          </div>
        </form>

        {/* Table */}
        <table className="table table-sm table-bordered">
          <thead>
            <tr>
              <th>#</th>
              <th>Currency</th>
              <th>Code</th>
              <th>Rate (1 unit = ? INR)</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {currencies.length > 0 ? (
              currencies.map((c, i) => (
                <tr key={c.id}>
                  <td>{i + 1}</td>
                  <td>{c.name}</td>
                  <td>{c.code}</td>
                  <td>{c.conversionRate}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-info me-2"
                      onClick={() => this.handleEdit(c)}
                    >Edit</button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => this.handleDelete(c.id)}
                    >Delete</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr><td colSpan="5">No data found</td></tr>
            )}
          </tbody>
        </table>
      </div>
    );
  }
}

export default CurrencyPage;
