import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';

class Quote extends Component {
  state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    products: [],
    showForm: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    formData: {
      quoteNo: '',
      quoteDate: new Date().toISOString().split('T')[0],
      quoteType: 'Standard',
      customer: '',
      status: 'Entered',
      choose: 'Service',
      refNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      quoteValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();
    this.fetchProducts();

    // Prefill from prospect if navigated
    const params = new URLSearchParams(window.location.search);
    const org = params.get('org');
    const client = params.get('client');
    const prospect = params.get('prospect');
    if (org || client || prospect) {
      this.setState(prev => ({
        showForm: true,
        formData: {
          ...prev.formData,
          customer: org || '',
          refNo: prospect || '',
        }
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = org || '';
        }
      });
    }
  }

  fetchQuotes = async () => {
    const snap = await getDocs(collection(db, 'quotes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ quotes: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };

  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleLineItemChange = (idx, field, value) => {
    this.setState(prev => {
      const lineItems = [...prev.formData.lineItems];
      lineItems[idx][field] = value;
      if (field === 'qty' || field === 'unitPrice') {
        const qty = parseFloat(lineItems[idx].qty) || 1;
        const unitPrice = parseFloat(lineItems[idx].unitPrice) || 0;
        lineItems[idx].amount = qty * unitPrice;
      }
      return { formData: { ...prev.formData, lineItems } };
    });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, quotes } = this.state;
    if (!formData.customer) return alert("Customer is required");
    if (formData.lineItems.length === 0) return alert("At least one line item is required");

    if (editingId) {
      await updateDoc(doc(db, 'quotes', editingId), formData);
    } else {
      formData.quoteNo = `QT${1000 + quotes.length}`;
      await addDoc(collection(db, 'quotes'), formData);
    }
    this.setState({ showForm: false, editingId: null });
    this.fetchQuotes();
  };

  loadQuoteForEdit = (quote) => {
    this.setState({
      formData: { ...quote },
      editingId: quote.id,
      showForm: true,
      activeTab: 'co'
    }, () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = quote.customer;
      }
    });
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

 selectOverlayValue = (value) => {
  if (this.state.overlayType === 'customer') {
    // Format billTo/shipTo if they are objects
    const formatAddress = (addr) => {
      if (!addr || typeof addr === 'string') return addr || '';
      return [
        ...(addr.address || []),
        [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
        addr.zip
      ].filter(Boolean).join('\n');
    };
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        customer: value.name || value.company || '',
        billTo: formatAddress(value.billTo),
        shipTo: formatAddress(value.shipTo)
      },
      overlayType: '',
      overlaySearch: ''
    }), () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = value.name || value.company || '';
      }
    });
  }
};

  renderOverlay = () => {
    const { overlayType, overlaySearch, customers } = this.state;
    if (overlayType === 'customer') {
      return (
        <div className="custom-overlay">
          <div className="custom-overlay-content">
            <div className="custom-overlay-title">Select Customer</div>
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Search customer..."
              value={overlaySearch}
              onChange={e => this.setState({ overlaySearch: e.target.value })}
            />
            <div style={{ maxHeight: 300, overflowY: 'auto' }}>
              <table className="table table-bordered table-sm">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Code</th>
                    <th>Short Name</th>
                  </tr>
                </thead>
                <tbody>
                  {customers
                    .filter(c =>
                      (c.name || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
                    )
                    .map((c, i) => (
                      <tr key={c.id || i} onClick={() => this.selectOverlayValue(c)} style={{ cursor: 'pointer' }}>
                        <td>{c.name}</td>
                        <td>{c.code}</td>
                        <td>{c.shortName}</td>
                      </tr>
                    ))}
                  {customers.length === 0 && (
                    <tr>
                      <td colSpan="3" className="text-center">No records found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <button className="btn btn-secondary btn-sm mt-2" onClick={this.hideOverlay}>Cancel</button>
          </div>
        </div>
      );
    }
    return null;
  };

  renderProductOverlay = () => {
    const { products, productOverlaySearch, selectedProductIds } = this.state;
    const filtered = products.filter(p =>
      (p.name || '').toLowerCase().includes(productOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="custom-overlay-title">Select Products</div>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Search products..."
            value={productOverlaySearch}
            onChange={e => this.setState({ productOverlaySearch: e.target.value })}
          />
          <div style={{ maxHeight: 300, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm">
              <thead>
                <tr>
                  <th></th>
                  <th>Name</th>
                  <th>UOM</th>
                  <th>Unit Price</th>
                </tr>
              </thead>
              <tbody>
                {filtered.map(p => (
                  <tr key={p.id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={selectedProductIds.includes(p.id)}
                        onChange={e => {
                          const checked = e.target.checked;
                          this.setState(prev => ({
                            selectedProductIds: checked
                              ? [...prev.selectedProductIds, p.id]
                              : prev.selectedProductIds.filter(id => id !== p.id)
                          }));
                        }}
                      />
                    </td>
                    <td>{p.name}</td>
                    <td>{p.uom}</td>
                    <td>{p.unitPrice}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <button
            className="btn btn-success btn-sm mt-2"
            onClick={() => {
              const selectedProducts = products.filter(p => this.state.selectedProductIds.includes(p.id));
              this.setState(prev => ({
                formData: {
                  ...prev.formData,
                  lineItems: [
                    ...prev.formData.lineItems,
                    ...selectedProducts
                      .filter(p => !prev.formData.lineItems.some(item => item.id === p.id))
                      .map(p => ({
                        id: p.id,
                        name: p.name,
                        uom: p.uom,
                        qty: 1,
                        unitPrice: p.unitPrice,
                        taxPercent: 0,
                        amount: p.unitPrice
                      }))
                  ]
                },
                productOverlayVisible: false,
                selectedProductIds: []
              }));
            }}
          >Add Selected</button>
          <button className="btn btn-secondary btn-sm mt-2 ml-2" onClick={() => this.setState({ productOverlayVisible: false, selectedProductIds: [] })}>Cancel</button>
        </div>
      </div>
    );
  };

  showQuotePDF = (quote) => {
    const subtotal = (quote.lineItems || []).reduce((sum, i) => sum + (parseFloat(i.amount) || 0), 0);
    const tax = parseFloat(quote.taxAmount) || 0;
    const total = parseFloat(quote.quoteValue) || (subtotal + tax);
    const win = window.open('', '_blank');
    win.document.write(`
      <html>
      <head>
        <title>Quote Preview</title>
        <style>
          body { font-family: Arial; padding: 24px; }
          .header { background: #2ecc40; color: #fff; padding: 10px 20px; font-size: 20px; font-weight: bold; }
          .quote-title { float: right; font-size: 18px; font-weight: bold; }
          .section { margin-top: 20px; }
          .flex { display: flex; justify-content: space-between; }
          .box { width: 45%; }
          table { border-collapse: collapse; width: 100%; margin-top: 16px; }
          th, td { border: 1px solid #ccc; padding: 8px; text-align: left; }
          th { background: #f8f8f8; }
          .total-row td { font-weight: bold; }
          .right { text-align: right; }
        </style>
      </head>
      <body>
        <div class="header">
          Your Company Name
          <span class="quote-title">QUOTE</span>
        </div>
        <div class="section flex">
          <div class="box">
            <b>Bill To</b><br/>
            ${quote.billTo || ''}
          </div>
          <div class="box">
            <b>Ship To</b><br/>
            ${quote.shipTo || ''}
          </div>
        </div>
        <div class="section flex">
          <div class="box">
            <b>Quote #:</b> ${quote.quoteNo || ''}<br/>
            <b>Quote Date:</b> ${quote.quoteDate || ''}<br/>
            <b>P.O.#:</b> ${quote.refNo || ''}
          </div>
        </div>
        <div class="section">
          <table>
            <thead>
              <tr>
                <th>QTY</th>
                <th>DESCRIPTION</th>
                <th>UNIT PRICE</th>
                <th>AMOUNT</th>
              </tr>
            </thead>
            <tbody>
              ${(quote.lineItems || []).map((item, idx) => `
                <tr>
                  <td>${item.qty}</td>
                  <td>${item.name}</td>
                  <td class="right">${item.unitPrice}</td>
                  <td class="right">${item.amount}</td>
                </tr>
              `).join('')}
              <tr>
                <td colspan="3" class="right">Subtotal</td>
                <td class="right">${subtotal.toFixed(2)}</td>
              </tr>
              <tr>
                <td colspan="3" class="right">Sales Tax</td>
                <td class="right">${tax.toFixed(2)}</td>
              </tr>
              <tr class="total-row">
                <td colspan="3" class="right">TOTAL</td>
                <td class="right">${total.toFixed(2)}</td>
              </tr>
            </tbody>
          </table>
        </div>
        <div class="section">
          <b>Terms & Conditions</b><br/>
          Payment is due within 15 days.<br/>
          Please make checks payable to: Your Company Name.
        </div>
      </body>
      </html>
    `);
    win.document.close();
  };

  renderQuoteTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Quotes</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Quote</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr style={{ fontSize: '14px' }}>
                <th>Quote No</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Quote Value</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.quotes.map((q, i) => (
                <tr key={i} style={{ fontSize: '14px' }}>
                  <td>
                    <button
                      className="btn btn-link p-0"
                      onClick={() => this.loadQuoteForEdit(q)}
                    >
                      {q.quoteNo}
                    </button>
                  </td>
                  <td>{q.customer}</td>
                  <td>{q.quoteDate}</td>
                  <td>{q.quoteValue}</td>
                  <td><label className="badge badge-info">{q.status}</label></td>
                  <td>
                    <button className="btn btn-sm btn-outline-info" onClick={() => this.showQuotePDF(q)}>Show PDF</button>
                  </td>
                </tr>
              ))}
              {this.state.quotes.length === 0 && (
                <tr><td colSpan="6" className="text-center">No quotes found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderQuoteForm = () => {
    const { formData, overlayType, productOverlayVisible } = this.state;
    return (
      <div>
        <div className="col-12 grid-margin">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Quote Form</h4>
              <ul className="nav nav-tabs" role="tablist">
                <li className="nav-item">
                  <button type="button" className={`nav-link ${this.state.activeTab === 'co' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'co' })}>CO Details</button>
                </li>
                <li className="nav-item">
                  <button type="button" className={`nav-link ${this.state.activeTab === 'commercial' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'commercial' })}>Commercial Terms</button>
                </li>
              </ul>
              <form className="form-sample" onSubmit={this.handleSubmit}>
                <div className="tab-content pt-3">
                  {this.state.activeTab === 'co' && (
                    <>
                      <div className="form-row">
                        <div className="form-group col-md-3">
                          <label>Quote No</label>
                          <Form.Control
                            type="text"
                            value={formData.quoteNo}
                            onChange={(e) => this.handleInputChange('quoteNo', e.target.value)}
                          />
                        </div>
                        <div className="form-group col-md-3">
                          <label>Quote Date</label>
                          <Form.Control
                            type="date"
                            value={formData.quoteDate}
                            onChange={(e) => this.handleInputChange('quoteDate', e.target.value)}
                          />
                        </div>
                        <div className="form-group col-md-3">
                          <label>Quote Type</label>
                          <Form.Control
                            as="select"
                            value={formData.quoteType}
                            onChange={(e) => this.handleInputChange('quoteType', e.target.value)}
                          >
                            <option>Standard</option>
                            <option>Manual</option>
                          </Form.Control>
                        </div>
                        <div className="form-group col-md-3">
                          <label>Status</label>
                          <Form.Control
                            as="select"
                            value={formData.status}
                            onChange={(e) => this.handleInputChange('status', e.target.value)}
                          >
                            <option>Entered</option>
                            <option>On Process</option>
                            <option>Closed</option>
                          </Form.Control>
                        </div>
                      </div>
                      <div className="form-row">
                        <div className="form-group col-md-6">
                          <label>Customer</label>
                          <div className="input-group">
                            <Form.Control
                              type="text"
                              className="form-control"
                              placeholder="Choose Customer"
                              ref={this.customerInputRef}
                              value={formData.customer}
                              readOnly
                            />
                            <div className="input-group-append">
                              <button
                                className="btn btn-sm btn-primary"
                                type="button"
                                onClick={() => this.showOverlay('customer')}
                              >
                                Select
                              </button>
                            </div>
                          </div>
                        </div>
                        <div className="form-group col-md-6">
                          <label>Choose</label>
                          <div className="d-flex align-items-center">
                            <div className="form-check mr-4">
                              <input
                                type="radio"
                                className="form-check-input"
                                name="choose"
                                id="chooseService"
                                value="Service"
                                checked={formData.choose === 'Service'}
                                onChange={(e) => this.handleInputChange('choose', e.target.value)}
                              />
                              <label className="form-check-label" htmlFor="chooseService">
                                Service
                              </label>
                            </div>
                            <div className="form-check">
                              <input
                                type="radio"
                                className="form-check-input"
                                name="choose"
                                id="chooseProduct"
                                value="Product"
                                checked={formData.choose === 'Product'}
                                onChange={(e) => this.handleInputChange('choose', e.target.value)}
                              />
                              <label className="form-check-label" htmlFor="chooseProduct">
                                Product
                              </label>
                            </div>
                          </div>
                        </div>
                      </div>
                      <p className="card-description"> Cost Details </p>
                      <div className="row">
                        <div className="col">
                          <Form.Group>
                            <label >Ref No.</label>
                            <div>
                              <Form.Control type="text" value={formData.refNo} onChange={(e) => this.handleInputChange('refNo', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                        <div className="col">
                          <Form.Group >
                            <label>IMP/EXP</label>
                            <div>
                              <select className="form-control" value={formData.impExp} onChange={(e) => this.handleInputChange('impExp', e.target.value)} >
                                <option>None</option>
                                <option>COB</option>
                                <option>FIP</option>
                              </select>
                            </div>
                          </Form.Group>
                        </div>
                        <div className="col">
                          <Form.Group >
                            <label >Currency</label>
                            <div >
                              <Form.Control type="text" value={formData.currency} onChange={(e) => this.handleInputChange('currency', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Conversion Rate</label>
                            <div>
                              <Form.Control type="text" value={formData.conversionRate} onChange={(e) => this.handleInputChange('conversionRate', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Tax Amount</label>
                            <div>
                              <Form.Control type="number" value={formData.taxAmount} onChange={(e) => this.handleInputChange('taxAmount', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Quote Value</label>
                            <div>
                              <Form.Control type="number" value={formData.quoteValue} onChange={(e) => this.handleInputChange('quoteValue', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                      </div>
                      <div className="col-lg-12 grid-margin stretch-card">
                        <div className="card shadow">
                          <div className="card-body">
                            <h4 className="card-title">Line Item </h4>
                            <button type="button" className="btn btn-primary btn-sm mb-2" onClick={() => this.setState({ productOverlayVisible: true })}>
                              + Add Product
                            </button>
                            <div className="table-responsive">
                              <table className="table table-bordered">
                                <thead>
                                  <tr>
                                    <th> ID </th>
                                    <th> Name </th>
                                    <th> UOM </th>
                                    <th> Qty</th>
                                    <th> Unit Price </th>
                                    <th> Tax% </th>
                                    <th>Amount </th>
                                    <th> Actions</th>
                                  </tr>
                                </thead>
                                <tbody>
                                  {formData.lineItems.map((item, idx) => (
                                    <tr key={item.id}>
                                      <td>{item.id}</td>
                                      <td>{item.name}</td>
                                      <td>{item.uom}</td>
                                      <td>
                                        <input
                                          type="number"
                                          min="1"
                                          value={item.qty}
                                          onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                                          style={{ width: 60 }}
                                        />
                                      </td>
                                      <td>
                                        <input
                                          type="number"
                                          value={item.unitPrice}
                                          onChange={e => this.handleLineItemChange(idx, 'unitPrice', e.target.value)}
                                          style={{ width: 80 }}
                                        />
                                      </td>
                                      <td>
                                        <input
                                          type="number"
                                          value={item.taxPercent}
                                          onChange={e => this.handleLineItemChange(idx, 'taxPercent', e.target.value)}
                                          style={{ width: 60 }}
                                        />
                                      </td>
                                      <td>{item.amount}</td>
                                      <td>
                                        <button
                                          type="button"
                                          className="btn btn-danger btn-sm"
                                          onClick={() => {
                                            this.setState(prev => ({
                                              formData: {
                                                ...prev.formData,
                                                lineItems: prev.formData.lineItems.filter((_, i) => i !== idx)
                                              }
                                            }));
                                          }}
                                        >Remove</button>
                                      </td>
                                    </tr>
                                  ))}
                                  {formData.lineItems.length === 0 && (
                                    <tr>
                                      <td colSpan="8" className="text-center">No items</td>
                                    </tr>
                                  )}
                                </tbody>
                              </table>
                            </div>
                          </div>
                        </div>
                      </div>
                    </>
                  )}
                  {this.state.activeTab === 'commercial' && (
                    <>
                      <div className="form-row">
                        <div className='col-6'>
                          <Form.Group>
                            <label>Bill To</label>
                            <div >
                              <Form.Control as="textarea" value={formData.billTo} onChange={(e) => this.handleInputChange('billTo', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                        <div className='col-6'>
                          <Form.Group>
                            <label>Ship To</label>
                            <div>
                              <Form.Control as="textarea" value={formData.shipTo} onChange={(e) => this.handleInputChange('shipTo', e.target.value)} />
                            </div>
                          </Form.Group>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Despatch Mode</label>
                            <Form.Control
                              as="select"
                              value={formData.despatchMode}
                              onChange={(e) => this.handleInputChange('despatchMode', e.target.value)}
                            >
                              <option>By Air</option>
                              <option>By Sea</option>
                              <option>By Road</option>
                            </Form.Control>
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Payment Terms</label>
                            <Form.Control
                              as="select"
                              value={formData.paymentTerms}
                              onChange={(e) => this.handleInputChange('paymentTerms', e.target.value)}
                            >
                              <option>Net 15</option>
                              <option>Net 30</option>
                              <option>Net 60</option>
                            </Form.Control>
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Freight Charges</label>
                            <Form.Control
                              type="number"
                              value={formData.freightCharges}
                              onChange={(e) => this.handleInputChange('freightCharges', e.target.value)}
                            />
                          </Form.Group>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Tax %</label>
                            <Form.Control
                              type="number"
                              value={formData.taxPercent}
                              onChange={(e) => this.handleInputChange('taxPercent', e.target.value)}
                            />
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Tax Amount</label>
                            <Form.Control
                              type="number"
                              value={formData.taxAmount}
                              onChange={(e) => this.handleInputChange('taxAmount', e.target.value)}
                            />
                          </Form.Group>
                        </div>
                        <div className="col-6 col-md-4">
                          <Form.Group>
                            <label>Packing Charges</label>
                            <Form.Control
                              type="number"
                              value={formData.packingCharges}
                              onChange={(e) => this.handleInputChange('packingCharges', e.target.value)}
                            />
                          </Form.Group>
                        </div>
                      </div>
                      <div className="text-right mt-3">
                        <button type="submit" className="btn btn-success">Save All Details</button>
                      </div>
                    </>
                  )}
                </div>
              </form>
              {overlayType && this.renderOverlay()}
              {productOverlayVisible && this.renderProductOverlay()}
            </div>
          </div>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderQuoteForm() : this.renderQuoteTable()}
      </div>
    );
  }
}

export default Quote;
