import React, { Component } from 'react';
import { db } from '../../firebase';
import { Dropdown } from 'react-bootstrap';
import { collection, getDocs, addDoc, deleteDoc, doc, setDoc } from 'firebase/firestore';

class ProspectForm extends Component {
  state = {
    prospects: [],
    leads: [],
    showForm: false,
    overlayType: '',
    overlaySearch: '',
    formVisible: false,
    formData: {
      prospectNo: '',
      name: '',
      organization: '',
      client: '',
      amount: '',
      representative: '',
      timeline: '',
    }
  };

  componentDidMount() {
    const params = new URLSearchParams(window.location.search);
    const org = params.get('org');
    const client = params.get('client');
    if (org || client) {
      this.setState(prev => ({
        formVisible: true,
        formData: {
          ...prev.formData,
          organization: decodeURIComponent(org || ''),
          client: decodeURIComponent(client || '')
        }
      }));
    }
    this.fetchProspects();
    this.fetchLeads();
  }

  fetchProspects = async () => {
    const snap = await getDocs(collection(db, 'prospects'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ prospects: data });
  };

  fetchLeads = async () => {
    const snap = await getDocs(collection(db, 'leads'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ leads: data });
  };

  toggleForm = (edit = null) => {
    if (edit) {
      this.setState({ showForm: true, editingId: edit.id, formData: edit });
    } else {
      this.setState({
        showForm: true,
        editingId: null,
        formData: {
          prospectNo: '', name: '', organization: '', client: '', amount: '',
          representative: '', timeline: '',
        },
      });
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({ formData: { ...prev.formData, [field]: value } }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, prospects } = this.state;
    if (!formData.organization || !formData.client) return alert("Organization and Client must be filled from Lead");

    if (editingId) {
      await setDoc(doc(db, 'prospects', editingId), formData);
    } else {
      formData.prospectNo = `PN${1000 + prospects.length}`;
      await addDoc(collection(db, 'prospects'), formData);
    }

    this.setState({ formVisible: false, editingId: null });
    this.fetchProspects();
  };

  handleDelete = async (id) => {
    await deleteDoc(doc(db, 'prospects', id));
    this.fetchProspects();
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    if (this.state.overlayType === 'organization') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          organization: value.company,
          client: value.name
        },
        overlayType: '',
        overlaySearch: ''
      }));
    } else if (this.state.overlayType === 'lead') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          organization: value.organization,
          client: value.client
        },
        overlayType: '',
        overlaySearch: ''
      }));
    }
  };

  convertToQuote = (prospect) => {
    const params = new URLSearchParams();
    params.set('org', prospect.organization);
    params.set('client', prospect.client);
    params.set('prospect', prospect.name);
    window.location.href = `/panelone/Quote?${params.toString()}`;
  };

  renderOverlay = () => {
    const { overlayType, overlaySearch, leads } = this.state;
    let data = [];
    let label = '';
    let columns = [];
    if (overlayType === 'lead') {
      data = leads;
      label = 'Lead';
      columns = ['organization', 'client'];
    }
    // You can add organization overlay if needed, similar to LeadForm
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="custom-overlay-title">Select {label}</div>
          <div className="custom-search-bar">
            <input
              type="text"
              className="form-control"
              placeholder={`Search ${label.toLowerCase()}...`}
              value={overlaySearch}
              onChange={e => this.setState({ overlaySearch: e.target.value })}
            />
          </div>
          <div style={{ flex: 1, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm" style={{ cursor: 'pointer' }}>
              <thead>
                <tr>
                  {columns.map(col => (
                    <th key={col}>{col.charAt(0).toUpperCase() + col.slice(1)}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data
                  .filter(d =>
                    columns.some(col =>
                      (d[col] || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
                    )
                  )
                  .map((d, i) => (
                    <tr
                      key={d.id || i}
                      onClick={() => this.selectOverlayValue(d)}
                    >
                      {columns.map(col => (
                        <td key={col}>{d[col]}</td>
                      ))}
                    </tr>
                  ))}
                {data.length === 0 && (
                  <tr>
                    <td colSpan={columns.length} className="text-center">
                      No records found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          <button className="btn btn-secondary mt-3 align-self-end" onClick={this.hideOverlay}>
            Cancel
          </button>
        </div>
      </div>
    );
  };

  renderForm = () => {
    const { formData, overlayType } = this.state;
    return (
      <div className="card mt-4">
        <div className="card-body">
          <h4 className="card-title">Prospect Form</h4>
          <form onSubmit={this.handleSubmit}>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Organization</label>
                <div className="input-group">
                  <input className="form-control" value={formData.organization} readOnly />
                  <div className="input-group-append">
                    <button className="btn btn-sm btn-primary" type="button" onClick={() => this.showOverlay('lead')}>Select</button>
                  </div>
                </div>
              </div>
              <div className="form-group col-md-4">
                <label>Client</label>
                <input className="form-control" value={formData.client} readOnly />
              </div>
              <div className="form-group col-md-4">
                <label>Prospect Name</label>
                <input className="form-control" value={formData.name} onChange={(e) => this.handleChange('name', e.target.value)} />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Amount</label>
                <input className="form-control" value={formData.amount} onChange={(e) => this.handleChange('amount', e.target.value)} />
              </div>
              <div className="form-group col-md-4">
                <label>Representative</label>
                <input className="form-control" value={formData.representative} onChange={(e) => this.handleChange('representative', e.target.value)} />
              </div>
              <div className="form-group col-md-4">
                <label>Timeline</label>
                <input className="form-control" value={formData.timeline} onChange={(e) => this.handleChange('timeline', e.target.value)} />
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Save</button>
              <button type="button" className="btn btn-secondary" onClick={() => this.setState({ formVisible: false })}>Cancel</button>
            </div>
            {overlayType && this.renderOverlay()}
          </form>
        </div>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Prospects</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ formVisible: true })}>+ Add Prospect</button>
        </div>
        <table className="table table-bordered">
          <thead className="thead-light">
            <tr>
              <th>Prospect No</th>
              <th>Prospect Name</th>
              <th>Organization</th>
              <th>Client</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.prospects.map((p, i) => (
              <tr key={i}>
                <td>{p.prospectNo}</td>
                <td>{p.name}</td>
                <td>{p.organization}</td>
                <td>{p.client}</td>
                <td>{p.amount}</td>
                <td>
                  <Dropdown>
                    <Dropdown.Toggle variant="outline-primary" size="sm">Action</Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => this.toggleForm(p)}>Edit</Dropdown.Item>
                      <Dropdown.Item onClick={() => this.handleDelete(p.id)}>Delete</Dropdown.Item>
                      <Dropdown.Item onClick={() => this.convertToQuote(p)}>Convert to Quote</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </td>
              </tr>
            ))}
            {this.state.prospects.length === 0 && (
              <tr><td colSpan="6" className="text-center">No prospects found.</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  render() {
    return (
      <div className="container-fluid">
        {this.state.formVisible ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default ProspectForm;
