import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';
import html2canvas from 'html2canvas';
import { jsPDF } from 'jspdf'; 
import { withRouter } from 'react-router-dom';


class Order extends Component {
  state = {
    activeTab: 'co',
    orders: [],
    customers: [],
    products: [],
    despatchModes: [],
    paymentTerms: [],
    showForm: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    taxGroups: [],
    showTaxOverlay: false,
    currentTaxIdx: null,
    formData: {
      orderNo: '',
      orderDate: new Date().toISOString().split('T')[0],
      orderType: 'Standard',
      customer: '',
      status: 'Entered', // Static 'Entered'
      choose: 'Service',
      qrefNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      orderValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      freighttaxAmount: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  // Helper to format address for Bill To / Ship To
  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchorders();
    this.fetchCustomers();
    this.fetchProducts();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();
    this.calculateOrderTotals();
    this.fetchTaxGroups();

    const quoteFromSession = sessionStorage.getItem('quoteToConvert');
    if (quoteFromSession && !this.state.showForm) {
      const quote = JSON.parse(quoteFromSession);
      sessionStorage.removeItem('quoteToConvert');

      this.setState(prev => ({
        formData: {
          ...prev.formData,
          ...quote,
          orderNo: `PO${1000 + prev.orders.length}`,
          orderDate: new Date().toISOString().split('T')[0],
          status: 'Entered',
          orderType: 'Standard',
          orderValue: quote.quoteValue,
          quoteNo: quote.quoteNo
        },
        showForm: true
      }));
    }

  }
calculateOrderTotals = () => {
  this.setState(prev => {
    const formData = { ...prev.formData };
    const items = formData.lineItems.map(item => {
      const price = parseFloat(item.unitPrice || 0);
      const qty = parseFloat(item.quantity || 0);
      const lineTotal = price * qty;
      const percent = parseFloat(item.taxPercent || 0);
      const taxAmt = (lineTotal * percent) / 100;

      return {
        ...item,
        total: lineTotal.toFixed(2),
        taxAmount: taxAmt.toFixed(2)
      };
    });

    const subtotal = items.reduce((acc, item) => acc + parseFloat(item.total || 0), 0);
    const totalTax = items.reduce((acc, item) => acc + parseFloat(item.taxAmount || 0), 0);
    const total = subtotal + totalTax;

    const amountWords = `INR ${total.toFixed(2)} Only`;

    return {
      formData: {
        ...formData,
        lineItems: items,
        subtotal: subtotal.toFixed(2),
        taxAmount: totalTax.toFixed(2),
        orderValue: total.toFixed(2),
        amountWords
      }
    };
  });
};


  componentDidUpdate(prevProps, prevState) {
    // Customer details auto-fill
    if (
      this.state.showForm &&
      this.state.customers.length > 0 &&
      this.state.formData.customer &&
      (prevState.formData.customer !== this.state.formData.customer ||
       (!this.state.formData.billTo && !this.state.formData.shipTo))
    ) {
      const customerObj = this.state.customers.find(
        c => c.custname === this.state.formData.customer || c.custcode === this.state.formData.customer
      );
      if (customerObj) {
        this.setState(prev => ({
          formData: {
            ...prev.formData,
            billTo: this.formatAddress(customerObj.billTo),
            shipTo: this.formatAddress(customerObj.shipTo),
            currency: customerObj.currency || '',
            // paymentTerms: customerObj.paymentTerms || '', // No auto-fill for payment terms
            // despatchMode: customerObj.despatchMode || '' // No auto-fill for despatch mode
          }
        }));
      }
    }

    // Freight and Tax calculation based on impExp
    const { impExp, freightCharges, taxPercent,packingCharges, lineItems } = this.state.formData;
    if (['None', 'CIF'].includes(impExp)) {
      const freight = parseFloat(freightCharges) || 0;
      const tax = parseFloat(taxPercent) || 0;
      const packing = parseFloat(packingCharges) || 0;

      const taxAmount = (freight * tax) / 100;

      // Calculate total from line items
      const lineTotal = lineItems.reduce((sum, item) => {
        const total = parseFloat(item.total) || 0;
        return sum + total;
      }, 0);

      const orderValue = lineTotal + freight + taxAmount + packing;

      if (
    this.state.formData.taxAmount !== taxAmount.toFixed(2) ||
    this.state.formData.orderValue !== orderValue.toFixed(2)
  ) {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        taxAmount: taxAmount.toFixed(2),
        orderValue: orderValue.toFixed(2)
      }
    }));
  }
} else if (impExp === 'FOB') {
  if (
    this.state.formData.freightCharges !== '' ||
    this.state.formData.taxPercent !== '' ||
    this.state.formData.taxAmount !== '' ||
    this.state.formData.packingCharges !== ''
  ) {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        freightCharges: '',
        taxPercent: '',
        taxAmount: '',
        packingCharges: '',
        orderValue: ''
      }
    }));
  }

    }
  }
fetchTaxGroups = async () => {
  const snap = await getDocs(collection(db, 'taxGroups'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ taxGroups: data });
};

  fetchorders = async () => {
    const snap = await getDocs(collection(db, 'orders'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ orders: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };
  fetchDespatchModes = async () => {
  const snap = await getDocs(collection(db, 'modes'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ despatchModes: data });
};

fetchPaymentTerms = async () => {
  const snap = await getDocs(collection(db, 'paymentTerms'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ paymentTerms: data });
};


  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

 handleLineItemChange = (idx, field, value) => {
  this.setState(prev => {
    const lineItems = [...prev.formData.lineItems];
    lineItems[idx][field] = value;

    // Recalculate total if qty or unitPrice changed
    if (field === 'qty' || field === 'unitPrice') {
      const qty = parseFloat(lineItems[idx].qty) || 0;
      const unitPrice = parseFloat(lineItems[idx].unitPrice) || 0;
      lineItems[idx].total = (qty * unitPrice).toFixed(2);
    }

    // ✅ Recalculate subtotal, tax, and total
    const subtotal = lineItems.reduce((sum, item) => sum + (parseFloat(item.total) || 0), 0);
    const tax = +(subtotal * 0.18).toFixed(2); // assuming 18% IGST
    const total = +(subtotal + tax).toFixed(2);

    // ✅ Convert total to words (simple fallback)
    const amountWords = `INR ${total} Only`; 
    return {
      formData: {
        ...prev.formData,
        lineItems,
        orderValue: total,
        taxAmount: tax,
        amountWords
      }
    };
  });
};


  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, orders } = this.state;
    if (!formData.customer) return alert("Customer is required");
    if (formData.lineItems.length === 0) return alert("At least one line item is required");

    if (editingId) {
      await updateDoc(doc(db, 'orders', editingId), formData);
    } else {
      formData.orderNo = `PO${1000 + orders.length}`;
      await addDoc(collection(db, 'orders'), formData);
    }
    this.setState({ showForm: false, editingId: null });
    this.fetchorders();
  };

  loadorderForEdit = (order) => {
    this.setState({
      formData: {
        ...order,
        lineItems: Array.isArray(order.lineItems) ? order.lineItems : [] // Ensure array
      },
      editingId: order.id,
      showForm: true,
      activeTab: 'co'
    }, () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = order.customer;
      }
    });
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    if (this.state.overlayType === 'customer') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          customer: value.custname || value.custcode || '', // Use custname or custcode
          billTo: this.formatAddress(value.billTo),
          shipTo: this.formatAddress(value.shipTo),
          currency: value.currency || '',
          // despatchMode: value.despatchMode || '', // No auto-fill for despatch mode
          // paymentTerms: value.paymentTerms || '' // No auto-fill for payment terms
        },
        overlayType: '',
        overlaySearch: ''
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = value.custname || value.custcode || '';
        }
      });
    }
  };
  
showOrderPDFWithOrg = async (order) => {
  if (!order || !Array.isArray(order.lineItems) || order.lineItems.length === 0) {
    alert("Order data is incomplete.");
    return;
  }

  const orgSnap = await getDocs(collection(db, 'businessGroups'));
  const org = orgSnap.docs[0]?.data() || {};

  const customer = this.state.customers.find(c => c.custshortName === order.customer) || {};

  const subtotal = order.lineItems.reduce((sum, i) => sum + (parseFloat(i.total) || 0), 0);
  const tax = parseFloat(order.taxAmount || 0);
  const total = subtotal + tax;
  const amountWords = `INR ${numberToWords(Math.floor(total))} Only`;

  const enrichedItems = order.lineItems.map(item => {
    const product = this.state.products.find(p => p.productId === item.itemCode) || {};
    return {
      ...item,
      ptshortName: product.ptshortName || '',
      hsnCode: product.hsnCode || '',
      uom: product.stockingUOM || ''
    };
  });
  const taxSummary = {};
    order.lineItems.forEach(item => {
      const group = item.taxGroup || 'Nill';
      const percent = parseFloat(item.taxPercent || 0);
      const total = parseFloat(item.total || 0);
      const taxAmt = (percent * total) / 100;
      if (!taxSummary[group]) taxSummary[group] = 0;
      taxSummary[group] += taxAmt;
    });


  const container = document.createElement('div');
  container.id = 'pdf-preview-container';
  container.style.width = '794px';
  container.style.padding = '40px';
  container.style.fontFamily = 'Arial';
  container.innerHTML = `
    <div style="display:flex; justify-content:space-between; align-items:center;">
      <img src="${org.logoUrl || ''}" style="height:50px;" />
      <div style="font-size:18px; font-weight:bold;">SALES ORDER</div>
    </div>
    <hr/>
    <div style="display:flex; justify-content:space-between; font-size:11px;">
      <div style="line-height:1.5; width:50%;">
        <b>${org.bgName || ''}</b><br/>
        ${org.address || ''}<br/>
        <b>Email:</b> ${org.email || ''}<br/>
        <b>Website:</b> ${org.website || ''}<br/>
        <b>GSTIN:</b> ${org.gstin || ''}
      </div>
      <table style="font-size:11px; text-align:left;">
        <tr><td><b>Order No</b></td><td>: ${order.orderNo}</td></tr>
        <tr><td><b>Order Date</b></td><td>: ${order.orderDate}</td></tr>
        <tr><td><b>Currency</b></td><td>: ${order.currency || 'INR'}</td></tr>
        <tr><td><b>Despatch</b></td><td>: ${order.despatchMode || ''}</td></tr>
      </table>
    </div>

    <div style="margin-top:15px; display:flex; justify-content:space-between; font-size:11px;">
      <div style="width:48%;">
        <b style="background:#011b56; color:#fff; display:block; padding:4px;">Bill To</b>
        <div style="border:1px solid #ccc; padding:6px;">
          ${order.billTo?.replace(/\n/g, '<br/>') || ''}
          
        </div>
        <div style="font-size:10px; margin-top:6px;">
            <b>GSTIN:</b> ${customer.gstin || ''}<br/>
            <b>Email:</b> ${customer.email || ''}<br/>
            <b>Contact:</b> ${customer.contactPerson || ''}<br/>
            <b>Phone:</b> ${customer.phone || ''}
          </div>
      </div>
      <div style="width:48%;">
        <b style="background:#011b56; color:#fff; display:block; padding:4px;">Ship To</b>
        <div style="border:1px solid #ccc; padding:6px;">
          ${order.shipTo?.replace(/\n/g, '<br/>') || ''}
        </div>
      </div>
    </div>

    <table style="width:100%; border-collapse:collapse; margin-top:20px; font-size:10px;">
      <thead>
        <tr style="background:#f4f6fa;">
          <th style="border:1px solid #011b56;">Item Code</th>
          <th style="border:1px solid #011b56;">Short</th>
          <th style="border:1px solid #011b56;">Description</th>
          <th style="border:1px solid #011b56;">HSN</th>
          <th style="border:1px solid #011b56;">UOM</th>
          <th style="border:1px solid #011b56;">Qty</th>
          <th style="border:1px solid #011b56;">Unit Price</th>
          <th style="border:1px solid #011b56;">Total</th>
        </tr>
      </thead>
      <tbody>
        ${enrichedItems.map(item => `
          <tr>
            <td style="border:1px solid #011b56;">${item.itemCode}</td>
            <td style="border:1px solid #011b56;">${item.ptshortName}</td>
            <td style="border:1px solid #011b56;">${item.itemDescription}</td>
            <td style="border:1px solid #011b56;">${item.hsnCode}</td>
            <td style="border:1px solid #011b56;">${item.uom}</td>
            <td style="border:1px solid #011b56;">${item.qty}</td>
            <td style="border:1px solid #011b56;">${item.unitPrice}</td>
            <td style="border:1px solid #011b56;">${item.total}</td>
          </tr>`).join('')}
        <tr><td colspan="7" style="text-align:right; border:1px solid #011b56;">Subtotal</td><td style="border:1px solid #011b56;">${subtotal.toFixed(2)}</td></tr>${Object.entries(taxSummary).map(([group, amt]) => `
  <tr>
    <td colspan="7" style="text-align:right; border:1px solid #011b56;">Tax - ${group}</td>
    <td style="border:1px solid #011b56;">${amt.toFixed(2)}</td>
  </tr>
`).join('')}

        <tr><td colspan="7" style="text-align:right; border:1px solid #011b56;"><b>Total</b></td><td style="border:1px solid #011b56;"><b>${total.toFixed(2)}</b></td></tr>
      </tbody>
    </table>

    <div style="margin-top:8px; font-size:11px;"><b>Amount in Words:</b> ${amountWords}</div>
    <div style="text-align:right; font-size:11px; margin-top:30px;">For ${org.bgName}<br/><br/><br/>Authorized Signatory</div>
    <div style="margin-top:20px; text-align:right; font-size:10px;">Printed on ${new Date().toLocaleString()}</div>
  `;

  document.body.appendChild(container);

  const canvas = await html2canvas(container, { scale: 2, useCORS: true });
  const imgData = canvas.toDataURL('image/png');
  const pdf = new jsPDF('p', 'pt', 'a4');
  const width = 595.28;
  const height = canvas.height * width / canvas.width;
  pdf.addImage(imgData, 'PNG', 0, 0, width, height);

  const blob = pdf.output('blob');
  const url = URL.createObjectURL(blob);
  const newWin = window.open();
  newWin.document.write(`
    <html><head><title>PDF Preview</title></head>
    <body style="margin:0;">
      <iframe width="100%" height="100%" style="border:none;" src="${url}"></iframe>
      <button onclick="document.querySelector('iframe').contentWindow.print()" style="position:fixed;top:10px;right:110px;"></button>
      <a href="${url}" download="Sales_Order_${order.orderNo || 'PDF'}.pdf" style="position:fixed;top:10px;right:10px;"></a>
    </body></html>
  `);
  newWin.document.close();

  document.body.removeChild(container);
};



 renderOverlay = () => {
  const { overlayType, overlaySearch, customers, despatchModes, paymentTerms } = this.state;

  const getFilteredRows = (list, nameKey = 'name', codeKey = 'shortName') =>
    list.filter(item =>
      (item[nameKey] || '').toLowerCase().includes((overlaySearch || '').toLowerCase()) ||
      (item[codeKey] || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
    );

  const handleSelect = (item) => {
    if (overlayType === 'customer') {
      this.selectOverlayValue(item);
    } else if (overlayType === 'despatchMode') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          despatchMode: item.name
        },
        overlayType: '',
        overlaySearch: ''
      }));
    } else if (overlayType === 'paymentTerms') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          paymentTerms: item.name
        },
        overlayType: '',
        overlaySearch: ''
      }));
    }
  };

  let title = '';
  let headers = [];
  let rows = [];

  if (overlayType === 'customer') {
    title = 'Select Customer';
    headers = ['Name', 'Code', 'Short Name'];
    rows = getFilteredRows(customers, 'custname', 'custcode');
  } else if (overlayType === 'despatchMode') {
    title = 'Select Despatch Mode';
    headers = ['Name'];
    rows = getFilteredRows(despatchModes);
  } else if (overlayType === 'paymentTerms') {
    title = 'Select Payment Terms';
    headers = ['Name'];
    rows = getFilteredRows(paymentTerms);
  } else {
    return null;
  }

  return (
    <div className="custom-overlay">
      <div className="custom-overlay-content">
        <div className="custom-overlay-title">{title}</div>
        <input
          type="text"
          className="form-control mb-2"
          placeholder={`Search ${overlayType}...`}
          value={overlaySearch}
          onChange={e => this.setState({ overlaySearch: e.target.value })}
        />
        <div style={{ maxHeight: 300, overflowY: 'auto' }}>
          <table className="table table-bordered table-sm">
            <thead>
              <tr>
                {headers.map((h, i) => <th key={i}>{h}</th>)}
              </tr>
            </thead>
            <tbody>
              {rows.map((item, i) => (
                <tr key={item.id || i} onClick={() => handleSelect(item)} style={{ cursor: 'pointer' }}>
                  {headers.map((h, j) => (
                    <td key={j}>
                      {overlayType === 'customer'
                        ? h === 'Name' ? item.custname
                          : h === 'Code' ? item.custcode
                          : item.custshortName
                        : h === 'Name' ? item.name
                          : item.shortName}
                    </td>
                  ))}
                </tr>
              ))}
              {rows.length === 0 && (
                <tr>
                  <td colSpan={headers.length} className="text-center">No records found</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        <button className="btn btn-secondary btn-sm mt-2" onClick={this.hideOverlay}>Cancel</button>
      </div>
    </div>
  );
};
renderTaxOverlay = () => {
  const { taxGroups, currentTaxIdx } = this.state;

  return (
    <div className="custom-overlay">
      <div className="custom-overlay-content">
        <div className="custom-overlay-title">Select Tax Group</div>
        <div style={{ maxHeight: 300, overflowY: 'auto' }}>
          <table className="table table-bordered table-sm">
            <thead>
              <tr>
                <th>Group Name</th>
                <th>Classification</th>
              </tr>
            </thead>
            <tbody>
              {taxGroups.map((group) => {
                // Compute total percentage from lineItems
                let totalPercent = 0;
                if (group.lineItems && group.lineItems.length) {
                  totalPercent = group.lineItems
                    .filter(i => i.type === 'Percentage')
                    .reduce((acc, i) => acc + parseFloat(i.percentOrAmt || 0), 0);
                }

                return (
                  <tr
                    key={group.groupName}
                    style={{ cursor: 'pointer' }}
                    onClick={() => {
                      const formData = { ...this.state.formData };
                      const items = [...formData.lineItems];

                      items[currentTaxIdx].taxGroup = group.groupName;
                      items[currentTaxIdx].taxPercent = totalPercent;

                      formData.lineItems = items;

                      this.setState({
                        formData,
                        showTaxOverlay: false,
                        currentTaxIdx: null
                      }, this.calculateOrderTotals);
                    }}
                  >
                    <td>{group.groupName}</td>
                    <td>{group.classification}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <button className="btn btn-sm btn-secondary mt-2" onClick={() => this.setState({ showTaxOverlay: false })}>
          Cancel
        </button>
      </div>
    </div>
  );
};


  renderProductOverlay = () => {
    const { products, productOverlaySearch, selectedProductIds } = this.state;
    const filtered = products.filter(p =>
      (p.ptshortName || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.ptdescription || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.itemCode || '').toLowerCase().includes(productOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="d-flex justify-content-between align-items-center mb-2">
            <div className="custom-overlay-title">Select Products</div>
            <button
              className="btn btn-success btn-sm"
              onClick={() => {
                const selectedProducts = products.filter(p => this.state.selectedProductIds.includes(p.id));
                this.setState(prev => ({
                  formData: {
                    ...prev.formData,
                    lineItems: [
                      ...prev.formData.lineItems,
                      ...selectedProducts
                        .filter(p => !prev.formData.lineItems.some(item => item.id === p.id))
                        .map(p => ({
                          id: p.id,
                          itemCode: p.productId || '',
                          itemDescription: p.ptdescription || '',
                          itemType: p.itemType || '',
                          materialType: p.materialType || '',
                          onHand: p.onHand || 0,
                          taxGroup: p.taxGroup || '',
                          custPartNo: p.custPartNo || '', // Assuming this is cust part table
                          hsnNo: p.hsnNo || '', // Assuming HSN No.
                          unitPrice: p.unitPrice || 0,
                          qty: 1,
                          total: (p.unitPrice || 0).toFixed(2)
                        }))
                    ]
                  },
                  productOverlayVisible: false,
                  selectedProductIds: []
                }));
              }}
