// LeadForm.js (Full Updated Version)
import React, { Component } from 'react';
import { Form, Dropdown } from 'react-bootstrap';
import { db } from '../../firebase';
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
} from 'firebase/firestore';

class LeadForm extends Component {
  state = {
    leads: [],
    products: [],
    orgs: [],
    showForm: false,
    showProductOverlay: false,
    showOrgOverlay: false,
    formData: {
      leadNo: '',
      organization: '',
      client: '',
      product: '',
      status: 'Open',
      source: 'Call'
    }
  };

  componentDidMount() {
    this.fetchLeads();
    this.fetchProducts();
    this.fetchOrganizations();
  }

  fetchLeads = async () => {
    const snap = await getDocs(collection(db, 'leads'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ leads: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => doc.data());
    this.setState({ products: data });
  };

  fetchOrganizations = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => doc.data());
    this.setState({ orgs: data });
  };

  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, 'leads'), this.state.formData);
    this.setState({
      formData: {
        leadNo: '', organization: '', client: '', product: '', status: 'Open', source: 'Call'
      },
      showForm: false
    });
    this.fetchLeads();
  };

  deleteLead = async (id) => {
    await deleteDoc(doc(db, 'leads', id));
    this.fetchLeads();
  };

  convertToProspect = (lead) => {
      const encodedOrg = encodeURIComponent(lead.organization);
      const encodedClient = encodeURIComponent(lead.client);
      window.location.href = `/panelone/ProspectForm?org=${encodedOrg}&client=${encodedClient}`;

  };

  showOrgOverlay = () => this.setState({ showOrgOverlay: true });
  hideOrgOverlay = () => this.setState({ showOrgOverlay: false });
  showProductOverlay = () => this.setState({ showProductOverlay: true });
  hideProductOverlay = () => this.setState({ showProductOverlay: false });

  selectProduct = (productName) => {
    this.setState(prev => ({
      formData: { ...prev.formData, product: productName },
      showProductOverlay: false
    }));
  };

  selectOrganization = (org) => {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        organization: org.company,
        client: org.name // assuming "name" is contact person
      },
      showOrgOverlay: false
    }));
  };

  renderForm = () => {
    const { formData } = this.state;
    return (
       <div className="card mt-4 full-height">
      <div className="card-body">
        <h4 className="card-title">Lead Form</h4>
      <form className="form-sample" onSubmit={this.handleSubmit}>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Lead No</label>
            <Form.Control type="text" value={formData.leadNo} onChange={(e) => this.handleInputChange('leadNo', e.target.value)} />
          </div>

          <div className="form-group col-md-4">
            <label>Organization</label>
            <div className="input-group">
              <input className="form-control" type="text" value={formData.organization} readOnly />
              <div className="input-group-append">
                <button className="btn btn-sm btn-primary" type="button" onClick={this.showOrgOverlay}>Select</button>
              </div>
            </div>
          </div>

          <div className="form-group col-md-4">
            <label>Client</label>
            <input className="form-control" type="text" value={formData.client} readOnly />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Product</label>
            <div className="input-group">
              <input className="form-control" type="text" value={formData.product} readOnly />
              <div className="input-group-append">
                <button className="btn btn-sm btn-primary" type="button" onClick={this.showProductOverlay}>Select</button>
              </div>
            </div>
          </div>

          <div className="form-group col-md-4">
            <label>Status</label>
            <select className="form-control" value={formData.status} onChange={(e) => this.handleInputChange('status', e.target.value)}>
              <option>Open</option>
              <option>In Progress</option>
              <option>Closed</option>
            </select>
          </div>

          <div className="form-group col-md-4">
            <label>Source</label>
            <select className="form-control" value={formData.source} onChange={(e) => this.handleInputChange('source', e.target.value)}>
              <option>Call</option>
              <option>Direct Meeting</option>
              <option>Others</option>
            </select>
          </div>
        </div>

        <div className="d-flex justify-content-between">
          <button type="submit" className="btn btn-success">Save</button>
          <button type="button" className="btn btn-secondary" onClick={() => this.setState({ showForm: false })}>Cancel</button>
        </div>

        {/* Product Overlay */}
        {this.state.showProductOverlay && (
          <div style={{position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,backgroundColor: 'rgba(0,0,0,0.6)',zIndex: 9999,display: 'flex',alignItems: 'center',justifyContent: 'center'}}>
          <div style={{background: '#fff',padding: 20,borderRadius: 8,width: '80vw',height: '80vh',display: 'flex',flexDirection: 'column',overflow: 'hidden'}}>
              <h5>Select a Product</h5>
              <ul className="list-group">
                {this.state.products.map((prod, i) => (
                  <li key={i} className="list-group-item list-group-item-action" onClick={() => this.selectProduct(prod.productName)}>{prod.productName}</li>
                ))}
              </ul>
              
              <button className="btn btn-secondary mt-3" onClick={this.hideProductOverlay}>Cancel</button>
            </div>
          </div>

        )}

        {/* Organization Overlay */}
        {this.state.showOrgOverlay && (
          <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.6)', zIndex: 9999, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <div style={{ background: '#fff', padding: 20, borderRadius: 8, width: '90%', maxWidth: 500, maxHeight: '80%', overflowY: 'auto' }}>
              <h5>Select an Organization</h5>
              <ul className="list-group">
                {this.state.orgs.map((org, i) => (
                  <li key={i} className="list-group-item list-group-item-action" onClick={() => this.selectOrganization(org)}>{org.company}</li>
                ))}
              </ul>
              <button className="btn btn-secondary mt-3" onClick={this.hideOrgOverlay}>Cancel</button>
            </div>
          </div>
          
        )}
      </form>
      </div>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Leads</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Lead</button>
        </div>
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th>Lead No</th>
                <th>Organization</th>
                <th>Client</th>
                <th>Product</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.leads.map((lead, i) => (
                <tr key={i}>
                  <td>{lead.leadNo}</td>
                  <td>{lead.organization}</td>
                  <td>{lead.client}</td>
                  <td>{lead.product}</td>
                  <td>{lead.status}</td>
                  <td>
                    <Dropdown>
                      <Dropdown.Toggle variant="outline-primary" size="sm">
                        Action
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item onClick={() => this.deleteLead(lead.id)}>Delete</Dropdown.Item>
                        <Dropdown.Item onClick={() => this.convertToProspect(lead)}>Convert to Prospect</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </td>
                </tr>
              ))}
              {this.state.leads.length === 0 && (
                <tr><td colSpan="6" className="text-center">No leads found.</td></tr>
              )}
            </tbody>
          </table>
        
      </div>
    </div>
  );

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default LeadForm;
