// src/app/sales/salestransactions/servicecontract/contractfinalbilling.js
import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { db } from "../../../../../firebase";
import {
  collection,
  getDocs,
  addDoc,
  updateDoc,
  doc,
  serverTimestamp,
} from "firebase/firestore";

class ContractFinalBilling extends Component {
  state = {
    contracts: [],
    taxGroups: [],
    bills: [],
    showForm: false,
    editingId: null,
    activeTab: 0,
    showContractOverlay: false,
    contractOverlaySearch: "",
    formData: this.getEmptyForm(),
    notes: "",
  };

  getEmptyForm() {
    return {
      billType: "Standard",
      billNo: "",
      billDate: new Date().toISOString().split("T")[0],
      status: "Entered",
      refNo: "",
      contractId: "",
      contractNo: "",
      customer: "",
      currency: "",
      conversionRate: "",
      stdDays: 30,
      changeInvUnitPrice: false,
      repeat: false,
      amtAgreed: "",
      durationFrom: "",
      durationTo: "",
      scheduleDate: "",
      taxAmt: "",
      billValue: "",
      billTo: "",
      shipTo: "",
      placeOfDelivery: "",
      despatchMode: "",
      paymentTerms: "",
      freightCharges: "",
      freightPercent: "",
      freightTaxAmt: "",
      packingCharges: "",
      lineItems: [],
    };
  }

  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
    this.fetchTaxGroups();
  }

  fetchContracts = async () => {
    const snap = await getDocs(collection(db, "contracts"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    this.setState({ contracts: data });
  };

  fetchBills = async () => {
    const snap = await getDocs(collection(db, "finalBills"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    this.setState({ bills: data.reverse() });
  };

  fetchTaxGroups = async () => {
    const snap = await getDocs(collection(db, "taxGroups"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    this.setState({ taxGroups: data });
  };

  handleTabChange = (i) => this.setState({ activeTab: i });

  handleInputChange = (field, value) => {
    this.setState((prev) => ({
      formData: { ...prev.formData, [field]: value },
    }));
  };

  selectContract = (c) => {
    this.setState((prev) => ({
      formData: {
        ...prev.formData,
        contractId: c.id,
        contractNo: c.contractNo,
        customer: c.customer,
        currency: c.currency,
        conversionRate: c.conversionRate,
        stdDays: c.stdDays,
        amtAgreed: c.amtAgreed,
        durationFrom: c.contrDurationFrom,
        durationTo: c.contrDurationTo,
        billTo: c.billTo,
        shipTo: c.shipTo,
        despatchMode: c.despatchMode,
        paymentTerms: c.paymentTerms,
        lineItems: (c.lineItems || []).map((li) => ({
          itemCode: li.itemCode,
          itemDesc: li.itemDesc,
          hsnNo: li.hsnNo,
          uom: li.uom,
          locator: li.locator || "",
          onHand: li.onHand || "",
          billQty: li.qty,
          unitDayOrWk: li.unitDayOrWk,
          unitPrice: li.unitPrice,
          months: li.months,
          taxGroupNames: li.taxGroupNames || [],
          taxAmt: li.taxAmt,
          itemTotal: li.itemTotal,
          schedule: "",
        })),
      },
      showContractOverlay: false,
    }));
  };

  handleLineItemChange = (idx, field, value) => {
    const items = [...this.state.formData.lineItems];
    items[idx] = { ...items[idx], [field]: value };
    this.setState((prev) => ({
      formData: { ...prev.formData, lineItems: items },
    }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, bills } = this.state;
    const saveData = {
      ...formData,
      notes: this.state.notes,
      createdAt: serverTimestamp(),
    };
    if (editingId) {
      await updateDoc(doc(db, "finalBills", editingId), saveData);
    } else {
      saveData.billNo = `BILL${1000 + bills.length}`;
      await addDoc(collection(db, "finalBills"), saveData);
    }
    this.setState({
      showForm: false,
      editingId: null,
      formData: this.getEmptyForm(),
      notes: "",
    });
    this.fetchBills();
  };

  /** UI Renderers */
  renderTabs = () => {
    const tabs = ["Billing Details", "Port & Terms", "Notes"];
    return (
      <ul className="nav nav-tabs mb-3">
        {tabs.map((t, i) => (
          <li key={i} className="nav-item">
            <button
              type="button"
              className={`nav-link ${this.state.activeTab === i ? "active" : ""}`}
              onClick={() => this.handleTabChange(i)}
            >
              {t}
            </button>
          </li>
        ))}
      </ul>
    );
  };

  renderBillingDetailsTab = () => {
    const f = this.state.formData;
    return (
      <div>
        <div className="form-row">
          <div className="form-group col-md-2">
            <label>Bill Type</label>
            <select
              className="form-control"
              value={f.billType}
              onChange={(e) => this.handleInputChange("billType", e.target.value)}
            >
              <option value="Standard">Standard</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="form-group col-md-2">
            <label>Bill No</label>
            <input
              className="form-control"
              value={f.billNo}
              readOnly
              placeholder="Auto"
            />
          </div>
          <div className="form-group col-md-2">
            <label>Bill Date</label>
            <input
              type="date"
              className="form-control"
              value={f.billDate}
              onChange={(e) => this.handleInputChange("billDate", e.target.value)}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Status</label>
            <input className="form-control" value={f.status} readOnly />
          </div>
          <div className="form-group col-md-2">
            <label>Ref No</label>
            <input
              className="form-control"
              value={f.refNo}
              onChange={(e) => this.handleInputChange("refNo", e.target.value)}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Contract</label>
            <input
              className="form-control"
              placeholder="Select Contract"
              value={f.contractNo}
              readOnly
              onClick={() => this.setState({ showContractOverlay: true })}
            />
          </div>
        </div>
      </div>
    );
  };

  renderTermsTab = () => {
    const f = this.state.formData;
    return (
      <div>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Bill To</label>
            <textarea className="form-control" value={f.billTo} readOnly />
          </div>
          <div className="form-group col-md-6">
            <label>Ship To</label>
            <textarea className="form-control" value={f.shipTo} readOnly />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Despatch Mode</label>
            <input className="form-control" value={f.despatchMode} readOnly />
          </div>
          <div className="form-group col-md-4">
            <label>Payment Terms</label>
            <input className="form-control" value={f.paymentTerms} readOnly />
          </div>
        </div>
      </div>
    );
  };

  renderNotesTab = () => (
    <div>
      <label>Notes</label>
      <textarea
        className="form-control"
        rows="4"
        value={this.state.notes}
        onChange={(e) => this.setState({ notes: e.target.value })}
      />
    </div>
  );

  renderLineItems = () => {
    const items = this.state.formData.lineItems || [];
    return (
      <div className="table-responsive">
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Item Code</th>
              <th>Description</th>
              <th>HSN/SAC</th>
              <th>UOM</th>
              <th>Locator</th>
              <th>On Hand</th>
              <th>Bill Qty</th>
              <th>Unit/Day</th>
              <th>Unit Price</th>
              <th>Months</th>
              <th>Tax</th>
              <th>Item Total</th>
              <th>Schedule</th>
            </tr>
          </thead>
          <tbody>
            {items.map((it, i) => (
              <tr key={i}>
                <td>{it.itemCode}</td>
                <td>{it.itemDesc}</td>
                <td>{it.hsnNo}</td>
                <td>{it.uom}</td>
                <td>{it.locator}</td>
                <td>{it.onHand}</td>
                <td>
                  <input
                    type="number"
                    className="form-control"
                    value={it.billQty}
                    onChange={(e) =>
                      this.handleLineItemChange(i, "billQty", e.target.value)
                    }
                  />
                </td>
                <td>{it.unitDayOrWk}</td>
                <td>{it.unitPrice}</td>
                <td>{it.months}</td>
                <td>{it.taxAmt}</td>
                <td>{it.itemTotal}</td>
                <td>
                  <input
                    className="form-control"
                    value={it.schedule}
                    onChange={(e) =>
                      this.handleLineItemChange(i, "schedule", e.target.value)
                    }
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  renderForm = () => {
    return (
      <div className="card">
        <form onSubmit={this.handleSubmit}>
          <div className="card-body">
            {this.renderTabs()}
            {this.state.activeTab === 0 && (
              <>
                {this.renderBillingDetailsTab()}
                {this.renderLineItems()}
              </>
            )}
            {this.state.activeTab === 1 && this.renderTermsTab()}
            {this.state.activeTab === 2 && this.renderNotesTab()}
          </div>
          <div className="card-footer text-right">
            <button className="btn btn-success" type="submit">
              {this.state.editingId ? "Update" : "Create"}
            </button>
            <button
              className="btn btn-secondary ml-2"
              type="button"
              onClick={() =>
                this.setState({
                  showForm: false,
                  editingId: null,
                  formData: this.getEmptyForm(),
                  notes: "",
                })
              }
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    );
  };

  renderTable = () => (
    <div className="card">
      <div className="card-body">
        <div className="d-flex justify-content-between">
          <h4>Final Bills</h4>
          <button
            className="btn btn-primary"
            onClick={() => this.setState({ showForm: true })}
          >
            + New Bill
          </button>
        </div>
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Bill No</th>
              <th>Date</th>
              <th>Contract</th>
              <th>Customer</th>
              <th>Currency</th>
              <th>Bill Value</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {this.state.bills.map((b, i) => (
              <tr key={i}>
                <td>
                  <button
                    className="btn btn-link"
                    onClick={() =>
                      this.setState({
                        showForm: true,
                        editingId: b.id,
                        formData: { ...b },
                        notes: b.notes || "",
                      })
                    }
                  >
                    {b.billNo}
                  </button>
                </td>
                <td>{b.billDate}</td>
                <td>{b.contractNo}</td>
                <td>{b.customer}</td>
                <td>{b.currency}</td>
                <td>{b.billValue}</td>
                <td>{b.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  renderContractOverlay = () => {
    const { contracts, contractOverlaySearch } = this.state;
    const filtered = contracts.filter((c) =>
      (c.contractNo || "")
        .toLowerCase()
        .includes(contractOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <h5>Select Contract</h5>
          <input
            className="form-control mb-2"
            value={contractOverlaySearch}
            onChange={(e) =>
              this.setState({ contractOverlaySearch: e.target.value })
            }
          />
          <table className="table table-sm table-bordered">
            <thead>
              <tr>
                <th>Contract No</th>
                <th>Customer</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((c) => (
                <tr
                  key={c.id}
                  style={{ cursor: "pointer" }}
                  onClick={() => this.selectContract(c)}
                >
                  <td>{c.contractNo}</td>
                  <td>{c.customer}</td>
                  <td>{c.contractValue}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            className="btn btn-secondary btn-sm"
            onClick={() => this.setState({ showContractOverlay: false })}
          >
            Close
          </button>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderForm() : this.renderTable()}
        {this.state.showContractOverlay && this.renderContractOverlay()}
      </div>
    );
  }
}

export default ContractFinalBilling;
