// ProspectForm.js (Updated for conversion from Lead)
import React, { Component } from 'react';
import { db } from '../../firebase';
import { Dropdown } from 'react-bootstrap';
import { collection, getDocs, addDoc, deleteDoc, doc, setDoc } from 'firebase/firestore';

class ProspectForm extends Component {
  state = {
    prospects: [],
    showForm: false,
    editingId: null,
    formVisible: false,
    formData: {
      prospectNo: '',
      name: '',
      organization: '',
      client: '',
      amount: '',
      representative: '',
      timeline: '',
    }
  };

  componentDidMount() {
    const params = new URLSearchParams(window.location.search);
    const org = params.get('org'); // ✅ fix param name
    const client = params.get('client'); // ✅ fix param name

    if (org || client) {
      this.setState(prev => ({
        formVisible: true, // ✅ show form immediately if converting
        formData: {
          ...prev.formData,
          organization: decodeURIComponent(org || ''),
          client: decodeURIComponent(client || '')
        }
      }));
    }
    this.fetchProspects();
  }

  fetchProspects = async () => {
    const snap = await getDocs(collection(db, 'prospects'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ prospects: data });
  };

  toggleForm = (edit = null) => {
    if (edit) {
      this.setState({ showForm: true, editingId: edit.id, formData: edit });
    } else {
      this.setState({
        showForm: true,
        editingId: null,
        formData: {
          prospectNo: '', name: '', organization: '', client: '', amount: '',
          representative: '', timeline: '',
        },
      });
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({ formData: { ...prev.formData, [field]: value } }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, prospects } = this.state;
    if (!formData.organization || !formData.client) return alert("Organization and Client must be filled from Lead");

    if (editingId) {
      await setDoc(doc(db, 'prospects', editingId), formData);
    } else {
      formData.prospectNo = `PN${1000 + prospects.length}`;
      await addDoc(collection(db, 'prospects'), formData);
    }

    this.setState({ formVisible: false, editingId: null });
    this.fetchProspects();
  };

  handleDelete = async (id) => {
    await deleteDoc(doc(db, 'prospects', id));
    this.fetchProspects();
  };

  renderForm = () => {
    const { formData } = this.state;
    return (
      <div className="card mt-4">
        <div className="card-body">
          <h4 className="card-title">Prospect Form</h4>
          <form onSubmit={this.handleSubmit}>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Organization</label>
                <input className="form-control" value={formData.organization} readOnly />
              </div>
              <div className="form-group col-md-4">
                <label>Client</label>
                <input className="form-control" value={formData.client} readOnly />
              </div>
              <div className="form-group col-md-4">
                <label>Prospect Name</label>
                <input className="form-control" value={formData.name} onChange={(e) => this.handleChange('name', e.target.value)} />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Amount</label>
                <input className="form-control" value={formData.amount} onChange={(e) => this.handleChange('amount', e.target.value)} />
              </div>
              <div className="form-group col-md-4">
                <label>Representative</label>
                <input className="form-control" value={formData.representative} onChange={(e) => this.handleChange('representative', e.target.value)} />
              </div>
              <div className="form-group col-md-4">
                <label>Timeline</label>
                <input className="form-control" value={formData.timeline} onChange={(e) => this.handleChange('timeline', e.target.value)} />
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Save</button>
              <button type="button" className="btn btn-secondary" onClick={() => this.setState({ formVisible: false })}>Cancel</button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Prospects</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ formVisible: true })}>+ Add Prospect</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th>Prospect No</th>
                <th>Organization</th>
                <th>Client</th>
                <th>Amount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.prospects.map((p, i) => (
                <tr key={i}>
                  <td>{p.prospectNo}</td>
                  <td>{p.organization}</td>
                  <td>{p.client}</td>
                  <td>{p.amount}</td>
                  <td>
                    <Dropdown>
                      <Dropdown.Toggle variant="outline-primary" size="sm">Action</Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item onClick={() => this.toggleForm(p)}>Edit</Dropdown.Item>
                        <Dropdown.Item onClick={() => this.handleDelete(p.id)}>Delete</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </td>
                </tr>
              ))}
              {this.state.prospects.length === 0 && (
                <tr><td colSpan="5" className="text-center">No prospects found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  render() {
    return (
      <div className="container-fluid">
        {this.state.formVisible ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default ProspectForm;
