import React, { Component } from 'react';
import { Form, Dropdown } from 'react-bootstrap';
import { db } from '../../firebase';
import '../../assets/styles/components/_overlay.scss';
import { collection, getDocs, addDoc, deleteDoc, doc } from 'firebase/firestore';

class LeadForm extends Component {
  state = {
    leads: [],
    products: [],
    orgs: [],
    showForm: false,
    overlayType: '',
    overlaySearch: '',
    formData: {
      leadNo: '',
      customer: '',
      product: '',
      status: 'Open',
      source: 'Call'
    }
  };

  componentDidMount() {
    this.fetchLeads();
    this.fetchProducts();
    this.fetchCustomers();
  }

  fetchLeads = async () => {
    const snap = await getDocs(collection(db, 'leads'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ leads: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ orgs: data });
  };

  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, 'leads'), this.state.formData);
    this.setState({
      formData: {
        leadNo: '', customers: '', product: '', status: 'Open', source: 'Call'
      },
      showForm: false
    });
    this.fetchLeads();
  };

  deleteLead = async (id) => {
    await deleteDoc(doc(db, 'leads', id));
    this.fetchLeads();
  };

  convertToProspect = (lead) => {
    const params = new URLSearchParams();
    params.set('cust', lead.customers);
    window.location.href = `/panelone/ProspectForm?${params.toString()}`;
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    if (this.state.overlayType === 'customers') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          customers: value.name,
        },
        overlayType: '',
        overlaySearch: ''
      }));
    } else if (this.state.overlayType === 'product') {
      this.setState(prev => ({
        formData: { ...prev.formData, product: value.productName },
        overlayType: '',
        overlaySearch: ''
      }));
    }
  };

  renderOverlay = () => {
    const { overlayType, overlaySearch, orgs, products } = this.state;
    let data = [];
    let label = '';
    let columns = [];
    if (overlayType === 'customers') {
      data = orgs;
      label = 'Customer';
      columns = ['code','name', 'shortName'];
    } else if (overlayType === 'product') {
      data = products;
      label = 'Product';
      columns = ['productName', 'category'];
    }
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="custom-overlay-title">Select {label}</div>
          <div className="custom-search-bar">
            <input
              type="text"
              className="form-control"
              placeholder={`Search ${label.toLowerCase()}...`}
              value={overlaySearch}
              onChange={e => this.setState({ overlaySearch: e.target.value })}
            />
          </div>
          <div style={{ flex: 1, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm" style={{ cursor: 'pointer' }}>
              <thead>
                <tr>
                  {columns.map(col => (
                    <th key={col}>{col.charAt(0).toUpperCase() + col.slice(1)}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data
                  .filter(d =>
                    columns.some(col =>
                      (d[col] || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
                    )
                  )
                  .map((d, i) => (
                    <tr
                      key={d.id || i}
                      onClick={() => this.selectOverlayValue(d)}
                    >
                      {columns.map(col => (
                        <td key={col}>{d[col]}</td>
                      ))}
                    </tr>
                  ))}
                {data.length === 0 && (
                  <tr>
                    <td colSpan={columns.length} className="text-center">
                      No records found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          <button className="btn btn-secondary mt-3 align-self-end" onClick={this.hideOverlay}>
            Cancel
          </button>
        </div>
      </div>
    );
  };

  renderForm = () => {
    const { formData, overlayType } = this.state;
    return (
      <div className="card mt-4 full-height">
        <div className="card-body">
          <h4 className="card-title">Lead Form</h4>
          <form className="form-sample" onSubmit={this.handleSubmit}>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Lead No</label>
                <Form.Control type="text" value={formData.leadNo} onChange={(e) => this.handleInputChange('leadNo', e.target.value)} />
              </div>
              <div className="form-group col-md-4">
                <label>Customer</label>
                <div className="input-group">
                  <input className="form-control" type="text" value={formData.name} readOnly />
                  <div className="input-group-append">
                    <button className="btn btn-sm btn-primary" type="button" onClick={() => this.showOverlay('customers')}>Select</button>
                  </div>
                </div>
              </div>
            </div>
            <div className="form-row">
              <div className="form-group col-md-4">
                <label>Product</label>
                <div className="input-group">
                  <input className="form-control" type="text" value={formData.product} readOnly />
                  <div className="input-group-append">
                    <button className="btn btn-sm btn-primary" type="button" onClick={() => this.showOverlay('product')}>Select</button>
                  </div>
                </div>
              </div>
              <div className="form-group col-md-4">
                <label>Status</label>
                <select className="form-control" value={formData.status} onChange={(e) => this.handleInputChange('status', e.target.value)}>
                  <option>Open</option>
                  <option>In Progress</option>
                  <option>Closed</option>
                </select>
              </div>
              <div className="form-group col-md-4">
                <label>Source</label>
                <select className="form-control" value={formData.source} onChange={(e) => this.handleInputChange('source', e.target.value)}>
                  <option>Call</option>
                  <option>Direct Meeting</option>
                  <option>Others</option>
                </select>
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Save</button>
              <button type="button" className="btn btn-secondary" onClick={() => this.setState({ showForm: false })}>Cancel</button>
            </div>
            {overlayType && this.renderOverlay()}
          </form>
        </div>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Leads</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Lead</button>
        </div>
        <table className="table table-bordered">
          <thead className="thead-light">
            <tr>
              <th>Lead No</th>
              <th>Customers</th>
              <th>Product</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.leads.map((lead, i) => (
              <tr key={i}>
                <td>{lead.leadNo}</td>
                <td>{lead.customers}</td>
                <td>{lead.product}</td>
                <td>{lead.status}</td>
                <td>
                  <Dropdown>
                    <Dropdown.Toggle variant="outline-primary" size="sm">
                      Action
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => this.deleteLead(lead.id)}>Delete</Dropdown.Item>
                      <Dropdown.Item onClick={() => this.convertToProspect(lead)}>Convert to Prospect</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </td>
              </tr>
            ))}
            {this.state.leads.length === 0 && (
              <tr><td colSpan="6" className="text-center">No leads found.</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default LeadForm;
