import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db ,auth} from '../../firebase';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { Form } from 'react-bootstrap';

export class Login extends Component {
  state = { email: '', password: '', error: '' };

  handleLogin = async (e) => {
    e.preventDefault();
    const { email, password } = this.state;

    try {
      await signInWithEmailAndPassword(auth, email, password);
      window.location.href = "/dashboard";  // redirect to app
    } catch (err) {
      this.setState({ error: "Invalid email or password" });
    }
  };

  render() {
    return (
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-4 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <h4>Hello! let's get started</h4>
              <Form className="pt-3" onSubmit={this.handleLogin}>
                <Form.Control type="email" placeholder="Email" size="lg" className="mb-3"
                  onChange={e => this.setState({ email: e.target.value })} />
                <Form.Control type="password" placeholder="Password" size="lg" className="mb-3"
                  onChange={e => this.setState({ password: e.target.value })} />
                {this.state.error && <p style={{ color: "red" }}>{this.state.error}</p>}
                <button type="submit" className="btn btn-primary btn-block">SIGN IN</button>
                <div className="text-center mt-4 font-weight-light">
                  Don't have an account? <Link to="/user-pages/register">Create</Link>
                </div>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
