import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';
import { withRouter } from 'react-router-dom';
import html2canvas from 'html2canvas';
import { jsPDF } from 'jspdf';


const numberToWords = (num) => {
  const a = ['', 'One', 'Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight', 'Nine',
    'Ten', 'Eleven', 'Twelve', 'Thirteen', 'Fourteen', 'Fifteen',
    'Sixteen', 'Seventeen', 'Eighteen', 'Nineteen'];
  const b = ['', '', 'Twenty', 'Thirty', 'Forty', 'Fifty', 'Sixty', 'Seventy', 'Eighty', 'Ninety'];

  if (num === 0) return 'Zero';
  if (num < 20) return a[num];
  if (num < 100) return b[Math.floor(num / 10)] + ' ' + a[num % 10];
  if (num < 1000)
    return a[Math.floor(num / 100)] + ' Hundred ' + numberToWords(num % 100);
  if (num < 100000)
    return numberToWords(Math.floor(num / 1000)) + ' Thousand ' + numberToWords(num % 1000);
  return 'Amount too large';
};


class Quote extends Component {
  state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    products: [],
    despatchModes: [],
    paymentTerms: [],
    showForm: false,
    showTaxOverlay: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    taxComponents: [],
    taxGroups:[],
    previewMode: false,
    selectedQuote: null,
    formData: {
      quoteNo: '',
      quoteDate: new Date().toISOString().split('T')[0],
      quoteType: 'Standard',
      customer: '',
      status: 'Entered', // Static 'Entered'
      choose: 'Service',
      qrefNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      quoteValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      freighttaxAmount: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  // Helper to format address for Bill To / Ship To
  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();
    this.fetchProducts();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();
    this.fetchTaxGroups();


  }

getTaxDetailsFromGroup(groupName) {
  if (!groupName || groupName === 'nill' || groupName === 'more') return { totalPercent: 0, totalAmount: 0 };

  const group = this.state.taxGroups.find(tg => tg.groupName === groupName);
  if (!group || !Array.isArray(group.lineItems)) return { totalPercent: 0, totalAmount: 0 };

  let totalPercent = 0;
  let totalAmount = 0;

  group.lineItems.forEach(item => {
    if (item.type === 'Percentage') {
      totalPercent += parseFloat(item.percentOrAmt || 0);
    } else if (item.type === 'Amount') {
      totalAmount += parseFloat(item.percentOrAmt || 0);
    }
  });

  return { totalPercent, totalAmount };
}


handleLineItemChange = (idx, field, value) => {
  const updatedItems = [...this.state.formData.lineItems];
  const item = { ...updatedItems[idx] };

  if (field === 'taxId') {
    if (value === 'more') {
      this.setState({ showTaxOverlay: true });
      return;
    }
    item.taxId = value;
  } else {
    item[field] = value;
  }

  updatedItems[idx] = item;

  this.setState(prev => ({
    formData: {
      ...prev.formData,
      lineItems: updatedItems
    }
  }), this.recalculateQuoteTotals);
};

recalculateQuoteTotals() {
  const { impExp, freightCharges, taxPercent, packingCharges, lineItems } = this.state.formData;
  let freight = parseFloat(freightCharges) || 0;
  let packing = parseFloat(packingCharges) || 0;
  let taxOnFreight = 0;
  if (['None', 'CIF'].includes(impExp)) {
    taxOnFreight = (freight * (parseFloat(taxPercent) || 0)) / 100;
  }

  let lineTotal = 0;
  let itemTaxTotal = 0;

  const updatedLineItems = lineItems.map(item => {
    const baseTotal = (parseFloat(item.unitPrice || 0) * parseFloat(item.qty || 1));
    const taxDetails = this.getTaxDetailsFromGroup(item.taxGroupName);
    const taxAmt = (baseTotal * taxDetails.totalPercent / 100) + (baseTotal * taxDetails.totalAmount);

    const total = baseTotal + taxAmt;
    item.total = total.toFixed(2);
    item.taxAmt = taxAmt.toFixed(2);

    lineTotal += baseTotal;
    itemTaxTotal += taxAmt;

    return item;
  });

  const totalTaxAmount = itemTaxTotal + taxOnFreight;
  const quoteValue = lineTotal + freight + packing + itemTaxTotal;

  this.setState(prev => ({
    formData: {
      ...prev.formData,
      taxAmount: totalTaxAmount.toFixed(2),
      quoteValue: quoteValue.toFixed(2),
      lineItems: updatedLineItems
    }
  }));
}


resetFOBFields = () => {
  const { freightCharges, taxPercent, taxAmount, packingCharges, quoteValue } = this.state.formData;
  const needsReset = [freightCharges, taxPercent, taxAmount, packingCharges, quoteValue]
    .some(val => val !== '' && val !== null && val !== undefined);

  if (needsReset) {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        freightCharges: '',
        taxPercent: '',
        taxAmount: '',
        packingCharges: '',
        quoteValue: ''
      }
    }));
  }
};

componentDidUpdate(prevProps, prevState) {
  if (JSON.stringify(prevState.formData.lineItems) !== JSON.stringify(this.state.formData.lineItems)) {
    this.recalculateQuoteTotals();
  }
}
