// src/app/sales/salestransactions/servicecontract/contractfinalbilling.js
import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { db } from "../../../../../firebase";
import {
  collection,
  getDocs,
  addDoc,
  updateDoc,
  doc,
  serverTimestamp,
} from "firebase/firestore";
import html2canvas from 'html2canvas';
import { jsPDF } from 'jspdf';
import { toWords } from 'number-to-words';

class ContractFinalBilling extends Component {
  state = {
    contracts: [],
    taxGroups: [],
    bills: [],
    showForm: false,
    editingId: null,
    activeTab: 0,
    showContractOverlay: false,
    contractOverlaySearch: "",
    formData: this.getEmptyForm(),
    notes: "",
    billingPeriod: { start: "", end: "" }, 

  };

  getEmptyForm() {
    return{
      billType: "Standard",
      billNo: "",
      billDate: new Date().toISOString().split("T")[0],
      status: "Entered",
      refNo: "",
      contractId: "",
      contractNo: "",
      nameofwrk: "",
      customer: "",
      currency: "",
      conversionRate: "",
      amtAgreed: "",
      durationFrom: "",
      durationTo: "",
      scheduleDate: "",
      taxAmt: "",
      billValue: "",
      billTo: "",
      shipTo: "",
      placeOfDelivery: "",
      despatchMode: "",
      paymentTerms: "",
      freightCharges: "",
      freightPercent: "",
      freightTaxAmt: "",
      packingCharges: "",
      lineItems: [],
    };
  }

  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
    this.fetchTaxGroups();
  }

  fetchContracts = async () => {
    const snap = await getDocs(collection(db, "contracts"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    const approved =data.filter(c => c.status === 'Approved');
    this.setState({ contracts: approved });
  };

  fetchBills = async () => {
    const snap = await getDocs(collection(db, "finalBills"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    this.setState({ bills: data.reverse() });
  };

  fetchTaxGroups = async () => {
    const snap = await getDocs(collection(db, "taxGroups"));
    const data = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
    this.setState({ taxGroups: data });
  };

  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

selectContract = (c) => {
    // Calculate next billing milestone (e.g., every 6 months)
    const freqMonths = 6;
    const start = new Date(c.contrDurationFrom);
    const end = new Date(c.contrDurationTo);
    // Find which milestone is next (based on existing bills)
    const billsForContract = this.state.bills.filter(b => b.contractId === c.id);
    let milestoneStart = new Date(start);
    let milestoneEnd = new Date(start);
    let milestoneIdx = 0;
    while (milestoneEnd < end && milestoneIdx < billsForContract.length) {
      milestoneStart.setMonth(milestoneStart.getMonth() + freqMonths);
      milestoneEnd = new Date(milestoneStart);
      milestoneIdx++;
    }
    // Set period for this bill
    const periodStart = milestoneStart;
    const periodEnd = new Date(milestoneStart);
    periodEnd.setMonth(periodEnd.getMonth() + freqMonths - 1);
    if (periodEnd > end) periodEnd.setTime(end.getTime());

     const months = Math.ceil((periodEnd.getFullYear() - periodStart.getFullYear()) * 12 + (periodEnd.getMonth() - periodStart.getMonth()) + 1);

    this.setState((prev) => ({
      formData: {
        ...prev.formData,
        contractId: c.id,
        contractNo: c.contractNo,
        nameofwrk: c.nameofwrk || "",
        customer: c.customer,
        currency: c.currency,
        conversionRate: c.conversionRate,
        amtAgreed: c.amtAgreed,
        durationFrom: c.contrDurationFrom,
        durationTo: c.contrDurationTo,
        billTo: this.formatAddress(c.billTo),
        shipTo: this.formatAddress(c.shipTo),
        despatchMode: c.despatchMode,
        paymentTerms: c.paymentTerms,
        freightCharges: c.freightCharges || "",
        freightPercent: c.freightTaxPercent || "",
        freightTaxAmt: c.freightTaxAmt || "",
        packingCharges: c.packingCharges || "",
        lineItems: (c.lineItems || []).map((li) => ({
          itemCode: li.itemCode,
          itemDesc: li.itemDesc,
          hsnCode: li.hsnCode,
          uom: li.uom,
          billQty: li.qty,
          unitPrice: li.unitPrice,
          months: months,
           taxGroupNames: li.taxGroupNames || [],
          taxAmt: 0,
          itemTotal: 0,
        })),
      },
      showContractOverlay: false,
      billingPeriod: {
        start: periodStart.toISOString().split("T")[0],
        end: periodEnd.toISOString().split("T")[0],
      }
    }), this.recalculateBillTotals);
  };


  handleInputChange = (field, value) => {
    this.setState((prev) => ({
      formData: { ...prev.formData, [field]: value },
    }), () => {
      if ([
        'freightCharges', 'freightPercent', 'packingCharges', 'conversionRate',
        'stdDays', 'amtAgreed'
      ].includes(field)) {
        this.recalculateBillTotals();
      }
    });
  };

 handleLineItemChange = (idx, field, value) => {
    const items = [...this.state.formData.lineItems];
    items[idx] = { ...items[idx], [field]: value };

    // Calculate taxAmt and itemTotal for this line
    let percent = 0;
    let amount = 0;
    (items[idx].taxGroupNames || []).forEach(groupName => {
      const group = this.state.taxGroups.find(t => t.groupName === groupName);
      if (group && Array.isArray(group.lineItems)) {
        group.lineItems.forEach(comp => {
          const val = parseFloat(comp.percentOrAmt || 0);
          if (comp.type === 'Percentage') percent += val;
          if (comp.type === 'Amount') amount += val;
        });
      }
    });
    const qty = parseFloat(items[idx].billQty || 0);
    const unitPrice = parseFloat(items[idx].unitPrice || 0);
    const months = parseFloat(items[idx].months || 1);
    const baseTotal = qty * unitPrice * months;
    const taxAmt = ((baseTotal * percent) / 100 + amount);
    items[idx].taxAmt = taxAmt.toFixed(2);
    items[idx].itemTotal = (baseTotal + taxAmt).toFixed(2);

    this.setState((prev) => ({
      formData: { ...prev.formData, lineItems: items },
    }), this.recalculateBillTotals);
  };

recalculateBillTotals = () => {
    const { lineItems, freightCharges, freightPercent, packingCharges, conversionRate } = this.state.formData;
    let billValue = 0;
    let taxAmt = 0;
    (lineItems || []).forEach(item => {
      billValue += parseFloat(item.itemTotal || 0);
      taxAmt += parseFloat(item.taxAmt || 0);
    });
    const freightTaxAmt = (parseFloat(freightCharges || 0) * parseFloat(freightPercent || 0)) / 100;
    billValue += parseFloat(freightCharges || 0) + parseFloat(packingCharges || 0) + freightTaxAmt;

    // Apply conversion rate if needed
    let finalValue = billValue;
    if (conversionRate) {
      finalValue = billValue * parseFloat(conversionRate);
    }

    this.setState(prev => ({
      formData: {
        ...prev.formData,
        billValue: finalValue.toFixed(2),
        taxAmt: taxAmt.toFixed(2),
        freightTaxAmt: freightTaxAmt.toFixed(2)
      }
    }));
  };

  handleTabChange = (i) => this.setState({ activeTab: i });
handleSubmit = async (e) => {
  e.preventDefault();
  this.setState({ loading: true, error: null });
  try {
    const { editingId, formData, bills } = this.state;
    const saveData = {
      ...formData,
      notes: this.state.notes,
      createdAt: serverTimestamp(),
    };
    if (editingId) {
      await updateDoc(doc(db, "finalBills", editingId), saveData);
    } else {
      saveData.billNo = `CB${1000 + bills.length}`;
      await addDoc(collection(db, "finalBills"), saveData);
    }
    await this.fetchBills(); // Ensure bills are refreshed before hiding form
    this.setState({
      showForm: false,
      editingId: null,
      formData: this.getEmptyForm(),
      notes: "",
      loading: false,
    });
  } catch (err) {
    this.setState({ error: err.message, loading: false });
    alert("Error saving bill: " + err.message);
  }
};

