  }));

  // 3. Totals
  const subtotal = enrichedItems.reduce((sum, item) =>
    sum + ((parseFloat(item.unitPrice) || 0) * (parseFloat(item.billQty) || 0) * (parseFloat(item.unitDayOrWk) || 1) * (parseFloat(item.months) || 1) * (parseFloat(bill.stdDays) || 30)), 0
