              <th>UOM</th>
              <th>Bill Qty</th>
              <th>Unit Price</th>
              <th>Months</th>
              <th>Tax Group</th>
              <th>Tax Amount</th>
              <th>Item Total</th>
            </tr>
          </thead>
            <tbody>
            {items.map((it, i) => (
              <tr key={i}>
                <td>{it.itemCode}</td>
                <td>{it.itemDesc}</td>
                <td>{it.hsnCode}</td>
                <td>{it.uom}</td>
                <td>
                  <input
                    type="number"
                    className="form-control"
                    value={it.billQty}
                    onChange={(e) =>
                      this.handleLineItemChange(i, "billQty", e.target.value)
                    }
                  />
                </td>
                 <td>
                  <input
                    type="number"
                    className="form-control"
                    value={it.unitPrice}
                    onChange={(e) =>
                      this.handleLineItemChange(i, "unitPrice", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    className="form-control"
                    value={it.months}
                    onChange={(e) =>
                      this.handleLineItemChange(i, "months", e.target.value)
                    }
                  />
                </td>
                 <td>
                  <select
                    className="form-control"
                    value={it.taxGroupNames[0] || ''}
                    onChange={e => {
                      const val = e.target.value;
                      const arr = val ? [val] : [];
                      this.handleLineItemChange(i, "taxGroupNames", arr);
                    }}
                  >
                    <option value="">-Select-</option>
                    {this.state.taxGroups.map((tg, idx) => (
                      <option key={idx} value={tg.groupName}>{tg.groupName}</option>
                    ))}
                  </select>
                </td>
                <td>{it.taxAmt}</td>
                <td>{it.itemTotal}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  renderTabs = () => {
    const tabs = ["Billing Details", "Port & Terms", "Notes"];
    return (
      <ul className="nav nav-tabs mb-3">
        {tabs.map((t, i) => (
          <li key={i} className="nav-item">
            <button
              type="button"
              className={`nav-link ${this.state.activeTab === i ? "active" : ""}`}
              onClick={() => this.handleTabChange(i)}
            >
              {t}
            </button>
          </li>
        ))}
      </ul>
    );
  };
renderNotesTab = () => (
  <div>
    <div className="form-group">
      <label>Notes / Remarks</label>
      <textarea
        className="form-control form-control-sm"
        rows={2}
        value={this.state.notes}
        onChange={e => this.setState({ notes: e.target.value })}
        style={{ resize: "vertical", minHeight: "40px", maxHeight: "120px" }}
      />
    </div>
  </div>
);
  renderForm = () => {
    return (
      <div className="card full-height">
        <form onSubmit={this.handleSubmit}>
          <div className="card-body">
            {this.renderTabs()}
            {this.state.activeTab === 0 && (
              <>
                {this.renderBillingDetailsTab()}
                {this.renderLineItems()}
              </>
            )}
            {this.state.activeTab === 1 && this.renderTermsTab()}
            {this.state.activeTab === 2 && this.renderNotesTab()}
          </div>
              <div className="fixed-card-footer text-right p-3 border-top bg-white">
            
            <button
              className="btn btn-secondary ml-2"
              type="button"
              onClick={() =>
                this.setState({
                  showForm: false,
                  editingId: null,
                  formData: this.getEmptyForm(),
                  notes: "",
                })
              }
            >
              Cancel
            </button>
            <button className="btn btn-success" type="submit">
              {this.state.editingId ? "Update" : "Create"}
            </button>
          </div>
        </form>
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Contract Billing</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>Create</button>
        </div>
              <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
            <tr>
              <th>Bill No</th>
              <th>Date</th>
              <th>Contract</th>
              <th>Customer</th>
              <th>Currency</th>
              <th>Bill Value</th>
              <th>Status</th>
              <th>Print</th>
            </tr>
          </thead>
          <tbody>
  {this.state.bills.map((b, i) => {
    let statusClass = "badge-secondary";
    if (b.status === "Entered") statusClass = "badge-warning";
    else if (b.status === "Submitted") statusClass = "badge-info";
    else if (b.status === "Cancelled") statusClass = "badge-danger";
    else if (b.status === "Approved") statusClass = "badge-success"; // optional extra

    return (
      <tr key={i}>
        <td>
          <button
            className="btn btn-link"
            onClick={() =>
              this.setState({
                showForm: true,
                editingId: b.id,
                formData: { ...b },
                notes: b.notes || "",
              })
            }
          >
            {b.billNo}
          </button>
        </td>
        <td>{b.billDate}</td>
        <td>{b.contractNo}</td>
        <td>{b.customer}</td>
        <td>{b.currency}</td>
        <td>{b.billValue}</td>
        <td>
          <label className={`badge ${statusClass}`} style={{ fontSize: "14px" }}>
            {b.status}
          </label>
        </td>
        <td>
          <i
            className="mdi mdi-printer menu-icon"
            onClick={() => this.showContractBillingPDFWithOrg(b)}
            style={{ fontSize: "24px", color: "#2196F3", cursor: "pointer" }}
          />
        </td>
      </tr>
    );
  })}
</tbody>

        </table>
      </div>
    </div>
    </div>
  );

  renderContractOverlay = () => {
    const { contracts, contractOverlaySearch } = this.state;
    const filtered = contracts.filter((c) =>
      (c.contractNo || "")
        .toLowerCase()
        .includes(contractOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <h5>Select Contract</h5>
          <input
            className="form-control mb-2"
            value={contractOverlaySearch}
            onChange={(e) =>
              this.setState({ contractOverlaySearch: e.target.value })
            }
          />
          <table className="table table-sm table-bordered">
            <thead>
              <tr>
                <th>Contract No</th>
                <th>Customer</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((c) => (
                <tr
                  key={c.id}
                  style={{ cursor: "pointer" }}
                  onClick={() => this.selectContract(c)}
                >
                  <td>{c.contractNo}</td>
                  <td>{c.customer}</td>
                  <td>{c.contractValue}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            className="btn btn-secondary btn-sm"
            onClick={() => this.setState({ showContractOverlay: false })}
          >
            Close
          </button>
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderForm() : this.renderTable()}
        {this.state.showContractOverlay && this.renderContractOverlay()}
      </div>
    );
  }
}

export default ContractFinalBilling;
