                  <tr key={p.id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={selectedProductIds.includes(p.id)}
                        onChange={e => {
                          const checked = e.target.checked;
                          this.setState(prev => ({
                            selectedProductIds: checked
                              ? [...prev.selectedProductIds, p.id]
                              : prev.selectedProductIds.filter(id => id !== p.id)
                          }));
                        }}
                      />
                    </td>
                    <td>{p.productId}</td>
                    <td>{p.ptdescription || ''}</td>
