import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db } from '../../firebase';
import { collection, query, where, getDocs, addDoc } from 'firebase/firestore';

export class Register extends Component {
  state = {
    username: '',
    email: '',
    password: '',
    error: ''
  };

  handleRegister = async (e) => {
    e.preventDefault();
    const { username, email, password } = this.state;

    try {
      // check if email already exists
      const q = query(collection(db, 'users'), where('email', '==', email));
      const snap = await getDocs(q);
      if (!snap.empty) {
        this.setState({ error: 'Email already in use. Please login.' });
        return;
      }

      // create new user document
      await addDoc(collection(db, 'users'), {
        username,
        email,
        password
      });

      // redirect to login
      window.location.href = "/user-pages/login";
    } catch (err) {
      this.setState({ error: err.message });
    }
  };

  render() {
    return (
      <div>
        <div className="d-flex align-items-center auth px-0">
          <div className="row w-100 mx-0">
            <div className="col-lg-4 mx-auto">
              <div className="auth-form-light text-left py-5 px-4 px-sm-5">
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                <h4>New here?</h4>
                <h6 className="font-weight-light">Signing up is easy. It only takes a few steps</h6>
                <form className="pt-3" onSubmit={this.handleRegister}>
                  <div className="form-group">
                    <input type="text" className="form-control form-control-lg" placeholder="Username"
                      onChange={(e) => this.setState({ username: e.target.value })} />
                  </div>
                  <div className="form-group">
                    <input type="email" className="form-control form-control-lg" placeholder="Email"
                      onChange={(e) => this.setState({ email: e.target.value })} />
                  </div>
                  <div className="form-group">
                    <input type="password" className="form-control form-control-lg" placeholder="Password"
                      onChange={(e) => this.setState({ password: e.target.value })} />
                  </div>
                  {this.state.error && (
                    <p style={{ color: "red" }}>{this.state.error}</p>
                  )}
                  <div className="mt-3">
                    <button type="submit" className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn">SIGN UP</button>
                  </div>
                  <div className="text-center mt-4 font-weight-light">
                    Already have an account? <Link to="/user-pages/login" className="text-primary">Login</Link>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Register;
