import React, { Component } from 'react';
import { Form } from 'react-bootstrap';

import { Dropdown } from 'react-bootstrap';
class ProspectForm extends Component {
  state = {
    prospects: [],
    formVisible: false,
    editingIndex: null,
    formData: {
      prospectNo: '',
      name: '',
      company: '',
      amount: '',
      representative: '',
      timeline: '',
    },
  };

  handleChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value },
    }));
  };

  toggleForm = (editIndex = null) => {
    const { prospects } = this.state;
    if (editIndex !== null) {
      this.setState({ formVisible: true, editingIndex: editIndex, formData: { ...prospects[editIndex] } });
    } else {
      this.setState({
        formVisible: true,
        editingIndex: null,
        formData: {
          prospectNo: '',
          name: '',
          company: '',
          amount: '',
          representative: '',
          timeline: '',
        },
      });
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { prospects, editingIndex, formData } = this.state;
    const updated = [...prospects];
    if (editingIndex !== null) {
      updated[editingIndex] = formData;
    } else {
      formData.prospectNo = `PN${1000 + prospects.length}`;
      updated.push(formData);
    }
    this.setState({ prospects: updated, formVisible: false });
  };

  renderTable = () => (
    <div className="card mt-4">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Prospect List</h4>
          <button className="btn btn-primary" onClick={() => this.toggleForm()}>+ Add Prospect</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th>No</th><th>Name</th><th>Company</th><th>Amount</th>
                <th>Rep</th><th>Timeline</th><th>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.prospects.map((p, index) => (
                <tr key={index}>
                  <td>{p.prospectNo}</td>
                  <td>{p.name}</td>
                  <td>{p.company}</td>
                  <td>{p.amount}</td>
                  <td>{p.representative}</td>
                  <td>{p.timeline}</td>
                  <td>
                    <Dropdown>
                    <Dropdown.Toggle variant="btn btn-outline-primary" id="dropdownMenuOutlineButton1">
                      Action
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => this.toggleForm(index)}>Edit</Dropdown.Item>
                      <Dropdown.Item onClick={() => this.handleDelete(i)}>Delete</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                  </td>
                </tr>
              ))}
              {this.state.prospects.length === 0 && (
                <tr><td colSpan="7" className="text-center">No prospects available.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderForm = () => {
    const { name, company, amount, representative, timeline } = this.state.formData;
    return (
      <form className="card p-4 mt-4" onSubmit={this.handleSubmit}>
        <h4>{this.state.editingIndex !== null ? 'Edit Prospect' : 'New Prospect'}</h4>
        <div className="form-row">
          <div className="form-group col-md-4">
            <label>Prospect Name</label>
            <input className="form-control" value={name} onChange={(e) => this.handleChange('name', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Company</label>
            <input className="form-control" value={company} onChange={(e) => this.handleChange('company', e.target.value)} />
          </div>
          <div className="form-group col-md-4">
            <label>Amount</label>
            <input className="form-control" value={amount} onChange={(e) => this.handleChange('amount', e.target.value)} />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Representative</label>
            <input className="form-control" value={representative} onChange={(e) => this.handleChange('representative', e.target.value)} />
          </div>
          <div className="form-group col-md-6">
            <label>Timeline</label>
            <input className="form-control" type="date" value={timeline} onChange={(e) => this.handleChange('timeline', e.target.value)} />
          </div>
        </div>
        <div className="d-flex justify-content-between">
          <button type="submit" className="btn btn-success">Save</button>
          <button type="button" className="btn btn-secondary" onClick={() => this.setState({ formVisible: false })}>Cancel</button>
        </div>
      </form>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.formVisible ? this.renderForm() : this.renderTable()}
      </div>
    );
  }
}

export default ProspectForm;
