// src/pages/CustomerPage.js
import React, { Component } from 'react';
import { db } from '../../firebase';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { Tabs, Tab, Form, Button } from 'react-bootstrap';

class CustomerPage extends Component {
  state = {
    customers: [],
    activeTab: 'address',
    formData: {
      code: 'Auto', shortName: '', name: '', classification: '',
      introducedDate: new Date().toISOString().split('T')[0],
      introducedBy: '', effectiveFrom: new Date().toISOString().split('T')[0], effectiveTo: '',
      coHold: false, invHold: false,
      address: ['', '', ''], country: '', state: '', city: '', zip: '',
      website: '', mail: '', linkedin: '', skype: '',
      despatchMode: '', paymentTerms: '', currency: '', gstin: '', cin: '',
      contacts: [{ name: '', phone: '', mail: '', skype: '' }, {}, {}]
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({ formData: { ...prev.formData, [field]: value } }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const collectionRef = collection(db, 'customers');
    await addDoc(collectionRef, this.state.formData);
    alert('Customer saved');
  };

  render() {
    const { formData } = this.state;
    return (
      <div className="container-fluid card p-4">
        <h4>Customer Creation/Update</h4>
        <Form onSubmit={this.handleSubmit}>
          <div className="form-row">
            <Form.Group className="col-md-2"><Form.Label>Customer Code</Form.Label><Form.Control value={formData.code} readOnly /></Form.Group>
            <Form.Group className="col-md-2"><Form.Label>Short Name *</Form.Label><Form.Control value={formData.shortName} onChange={e => this.handleChange('shortName', e.target.value)} required /></Form.Group>
            <Form.Group className="col-md-4"><Form.Label>Customer Name *</Form.Label><Form.Control value={formData.name} onChange={e => this.handleChange('name', e.target.value)} required /></Form.Group>
            <Form.Group className="col-md-2"><Form.Label>Classification</Form.Label><Form.Control as="select" value={formData.classification} onChange={e => this.handleChange('classification', e.target.value)}>
              <option>-Select-</option><option>Manufacturer</option><option>Subcontract</option><option>Trader</option><option>Government Service</option>
            </Form.Control></Form.Group>
            <Form.Group className="col-md-2"><Form.Label>Introduced Date</Form.Label><Form.Control type="date" value={formData.introducedDate} onChange={e => this.handleChange('introducedDate', e.target.value)} /></Form.Group>
          </div>
          <div className="form-row">
            <Form.Group className="col-md-2"><Form.Label>Effective From</Form.Label><Form.Control type="date" value={formData.effectiveFrom} onChange={e => this.handleChange('effectiveFrom', e.target.value)} /></Form.Group>
            <Form.Group className="col-md-2"><Form.Label>Effective To</Form.Label><Form.Control type="date" value={formData.effectiveTo} onChange={e => this.handleChange('effectiveTo', e.target.value)} /></Form.Group>
            <Form.Group className="col-md-4"><Form.Label>Introduced By</Form.Label><Form.Control value={formData.introducedBy} onChange={e => this.handleChange('introducedBy', e.target.value)} /></Form.Group>
            <Form.Group className="col-md-2 d-flex align-items-center">
              <Form.Check type="checkbox" label="Customer Order Hold" checked={formData.coHold} onChange={e => this.handleChange('coHold', e.target.checked)} className="mr-3" />
              <Form.Check type="checkbox" label="Invoice Hold" checked={formData.invHold} onChange={e => this.handleChange('invHold', e.target.checked)} />
            </Form.Group>
          </div>

          <Tabs activeKey={this.state.activeTab} onSelect={k => this.setState({ activeTab: k })} className="mb-3">
            <Tab eventKey="address" title="Address">
              <Form.Group><Form.Label>Address</Form.Label>
                <Form.Control value={formData.address[0]} onChange={e => this.handleChange('address', [e.target.value, formData.address[1], formData.address[2]])} className="mb-1" required />
                <Form.Control value={formData.address[1]} onChange={e => this.handleChange('address', [formData.address[0], e.target.value, formData.address[2]])} className="mb-1" />
                <Form.Control value={formData.address[2]} onChange={e => this.handleChange('address', [formData.address[0], formData.address[1], e.target.value])} />
              </Form.Group>
              <div className="form-row">
                <Form.Group className="col-md-3"><Form.Label>Country</Form.Label><Form.Control value={formData.country} onChange={e => this.handleChange('country', e.target.value)} required /></Form.Group>
                <Form.Group className="col-md-3"><Form.Label>Province/State</Form.Label><Form.Control value={formData.state} onChange={e => this.handleChange('state', e.target.value)} required /></Form.Group>
                <Form.Group className="col-md-3"><Form.Label>City</Form.Label><Form.Control value={formData.city} onChange={e => this.handleChange('city', e.target.value)} required /></Form.Group>
                <Form.Group className="col-md-3"><Form.Label>Zipcode/Pincode</Form.Label><Form.Control value={formData.zip} onChange={e => this.handleChange('zip', e.target.value)} required /></Form.Group>
              </div>
              <div className="form-row">
                <Form.Group className="col-md-4"><Form.Label>Website</Form.Label><Form.Control value={formData.website} onChange={e => this.handleChange('website', e.target.value)} /></Form.Group>
                <Form.Group className="col-md-4"><Form.Label>Mail</Form.Label><Form.Control value={formData.mail} onChange={e => this.handleChange('mail', e.target.value)} /></Form.Group>
                <Form.Group className="col-md-4"><Form.Label>LinkedIn</Form.Label><Form.Control value={formData.linkedin} onChange={e => this.handleChange('linkedin', e.target.value)} /></Form.Group>
              </div>
              <Form.Group><Form.Label>Skype</Form.Label><Form.Control value={formData.skype} onChange={e => this.handleChange('skype', e.target.value)} /></Form.Group>
            </Tab>
            <Tab eventKey="terms" title="Commercial Terms">
              <Form.Group><Form.Label>Despatch Mode</Form.Label><Form.Control value={formData.despatchMode} onChange={e => this.handleChange('despatchMode', e.target.value)} /></Form.Group>
              <Form.Group><Form.Label>Payment Terms</Form.Label><Form.Control value={formData.paymentTerms} onChange={e => this.handleChange('paymentTerms', e.target.value)} /></Form.Group>
              <Form.Group><Form.Label>Currency</Form.Label><Form.Control value={formData.currency} onChange={e => this.handleChange('currency', e.target.value)} /></Form.Group>
              <Form.Group><Form.Label>GSTIN</Form.Label><Form.Control value={formData.gstin} onChange={e => this.handleChange('gstin', e.target.value)} /></Form.Group>
              <Form.Group><Form.Label>CIN</Form.Label><Form.Control value={formData.cin} onChange={e => this.handleChange('cin', e.target.value)} /></Form.Group>
            </Tab>
            <Tab eventKey="contacts" title="Contact Details">
              {formData.contacts.map((c, i) => (
                <div className="form-row" key={i}>
                  <Form.Group className="col-md-3"><Form.Label>Contact Person</Form.Label><Form.Control value={c.name || ''} onChange={e => {
                    const updated = [...formData.contacts];
                    updated[i].name = e.target.value;
                    this.handleChange('contacts', updated);
                  }} /></Form.Group>
                  <Form.Group className="col-md-3"><Form.Label>Phone</Form.Label><Form.Control value={c.phone || ''} onChange={e => {
                    const updated = [...formData.contacts];
                    updated[i].phone = e.target.value;
                    this.handleChange('contacts', updated);
                  }} /></Form.Group>
                  <Form.Group className="col-md-3"><Form.Label>Mail</Form.Label><Form.Control value={c.mail || ''} onChange={e => {
                    const updated = [...formData.contacts];
                    updated[i].mail = e.target.value;
                    this.handleChange('contacts', updated);
                  }} /></Form.Group>
                  <Form.Group className="col-md-3"><Form.Label>Skype</Form.Label><Form.Control value={c.skype || ''} onChange={e => {
                    const updated = [...formData.contacts];
                    updated[i].skype = e.target.value;
                    this.handleChange('contacts', updated);
                  }} /></Form.Group>
                </div>
              ))}
            </Tab>
          </Tabs>

          <div className="text-right">
            <Button variant="primary" type="submit">Create</Button>
          </div>
        </Form>
      </div>
    );
  }
}

export default CustomerPage;
