import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';

class Quote extends Component {
    state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    showForm: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    formData: {
      quoteNo: '', quoteDate: new Date().toISOString().split('T')[0], quoteType: 'Standard', customer: '', status: 'Entered', choose: 'Service', refNo: '', impExp: 'None', currency: '', conversionRate: '', taxAmount: '', quoteValue: '', billTo: '', shipTo: '', despatchMode: 'By Air', paymentTerms: '', freightCharges: '', taxPercent: '', packingCharges: ''
    }
  };
   customerInputRef = React.createRef();

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();

    // Prefill from prospect if navigated
    const params = new URLSearchParams(window.location.search);
    const org = params.get('org');
    const client = params.get('client');
    const prospect = params.get('prospect');
    if (org || client || prospect) {
      this.setState(prev => ({
        showForm: true,
        formData: {
          ...prev.formData,
          customer: org || '',
          refNo: prospect || '',
        }
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = org || '';
        }
      });
    }
  }
  fetchQuotes = async () => {
    const snapshot = await getDocs(collection(db, 'quotes'));
    const quotes = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ quotes: quotes.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };
handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  loadQuoteForEdit = (quote) => {
    this.setState({
      formData: { ...quote },
      editingId: quote.id,
      showForm: true,
      activeTab: 'co'
    }, () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = quote.customer;
      }
    });
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

   selectOverlayValue = (value) => {
    if (this.state.overlayType === 'customer') {
      this.setState(prev => ({
        formData: { ...prev.formData, customer: value.name || value.company || '' },
        overlayType: '',
        overlaySearch: ''
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = value.name || value.company || '';
        }
      });
    }
  };

  handleSubmit = async (e) => {
    if (e) e.preventDefault();
    try {
      if (this.state.editingId) {
        const docRef = doc(db, 'quotes', this.state.editingId);
        await updateDoc(docRef, this.state.formData);
      } else {
        await addDoc(collection(db, 'quotes'), this.state.formData);
      }
      this.setState({ editingId: null, showForm: false });
      this.fetchQuotes();
    } catch (error) {
      console.error('Error saving:', error);
    }
  };

  renderQuoteTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Quotes</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Quote</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr style={{ fontSize: '14px' }}>
                <th>Quote No</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Quote Value</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.quotes.map((q, i) => (
                <tr key={i} style={{ fontSize: '14px' }}>
                  <td>
                    <button
                      className="btn btn-link p-0"
                      onClick={() => this.loadQuoteForEdit(q)}
                    >
                      {q.quoteNo}
                    </button>
                  </td>
                  <td>{q.customer}</td>
                  <td>{q.quoteDate}</td>
                  <td>{q.quoteValue}</td>
                  <td><label className="badge badge-info">{q.status}</label></td>
                </tr>
              ))}
              {this.state.quotes.length === 0 && (
                <tr><td colSpan="5" className="text-center">No quotes found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderQuoteForm = () => (
    <div>
      <div className="col-12 grid-margin">
        <div className="card">
          <div className="card-body">
            <h4 className="card-title">Quote Form</h4>

            <ul className="nav nav-tabs" role="tablist">
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'co' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'co' })}>CO Details</button>
              </li>
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'commercial' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'commercial' })}>Commercial Terms</button>
              </li>
            </ul>

            <form className="form-sample" onSubmit={this.handleSubmit}>
              <div className="tab-content pt-3">
                {this.state.activeTab === 'co' && (
                  <>
                  <div className="form-row">
  <div className="form-group col-md-3">
    <label>Quote No</label>
    <Form.Control
      type="text"
      onChange={(e) => this.handleInputChange('quoteNo', e.target.value)}
    />
  </div>

  <div className="form-group col-md-3">
    <label>Quote Date</label>
    <Form.Control
      type="date"
      defaultValue={this.formData.quoteDate}
      onChange={(e) => this.handleInputChange('quoteDate', e.target.value)}
    />
  </div>

  <div className="form-group col-md-3">
    <label>Quote Type</label>
    <Form.Control
      as="select"
      defaultValue={this.formData.quoteType}
      onChange={(e) => this.handleInputChange('quoteType', e.target.value)}
    >
      <option>Standard</option>
      <option>Manual</option>
    </Form.Control>
  </div>

  <div className="form-group col-md-3">
    <label>Status</label>
    <Form.Control
      as="select"
      defaultValue={this.formData.status}
      onChange={(e) => this.handleInputChange('status', e.target.value)}
    >
      <option>Entered</option>
      <option>On Process</option>
      <option>Closed</option>
    </Form.Control>
  </div>
</div>

<div className="form-row">
  <div className="form-group col-md-6">
      <label>Customer</label>
      <div className="input-group">
        <Form.Control
          type="text"
          className="form-control"
          placeholder="Choose Customer"
          ref={this.customerInputRef}
          value={this.state.formData.customer}
          readOnly
        />
        <div className="input-group-append">
          <button
            className="btn btn-sm btn-primary"
            type="button"
            onClick={() => this.showOverlay('customer')}
          >
            Select
          </button>
        </div>
      </div>
    </div>

  <div className="form-group col-md-6">
    <label>Choose</label>
    <div className="d-flex align-items-center">
      <div className="form-check mr-4">
        <input
          type="radio"
          className="form-check-input"
          name="choose"
          id="chooseService"
          value="Service"
          defaultChecked={this.formData.choose === 'Service'}
          onChange={(e) => this.handleInputChange('choose', e.target.value)}
        />
        <label className="form-check-label" htmlFor="chooseService">
          Service
        </label>
      </div>

      <div className="form-check">
        <input
          type="radio"
          className="form-check-input"
          name="choose"
          id="chooseProduct"
          value="Product"
          defaultChecked={this.formData.choose === 'Product'}
          onChange={(e) => this.handleInputChange('choose', e.target.value)}
        />
        <label className="form-check-label" htmlFor="chooseProduct">
          Product
        </label>
      </div>
    </div>
  </div>
</div>

                <p className="card-description"> Cost Details </p>
                <div className="row">
                  <div className="col">
                    <Form.Group>
                      <label >Ref No.</label>
                      <div>
                      <Form.Control type="text" onChange={(e) => this.handleInputChange('refNo', e.target.value)} />
                      </div>
                    </Form.Group>
                  </div>
                  <div className="col">
                    <Form.Group >
                      <label>IMP/EXP</label>
                      <div>
                      <select className="form-control" defaultValue={this.formData.impExp} onChange={(e) => this.handleInputChange('impExp', e.target.value)} >
                          <option>None</option>
                          <option>COB</option>
                          <option>FIP</option>
                        </select>
                      </div>
                    </Form.Group>
                  </div>
                  <div className="col">
                    <Form.Group >
                      <label >Currency</label>
                      <div >
                      <Form.Control type="text" onChange={(e) => this.handleInputChange('currency', e.target.value)} />
                      </div>
                    </Form.Group>
                  </div>
                </div>
                <div className="row">
                  <div className="col-6 col-md-4">
                    <Form.Group>
                      <label>Conversion Rate</label>
                      <div>
                      <Form.Control type="text" onChange={(e) => this.handleInputChange('conversionRate', e.target.value)} />
                      </div>
                    </Form.Group>
                  </div>
                  <div className="col-6 col-md-4">
                    <Form.Group>
                      <label>Tax Amount</label>
                      <div>
                      <Form.Control type="number" onChange={(e) => this.handleInputChange('taxAmount', e.target.value)} />
                      </div>
                    </Form.Group>
                  </div>
                  <div className="col-6 col-md-4">
                    <Form.Group>
                      <label>Quote Value</label>
                      <div>
                      <Form.Control type="number" onChange={(e) => this.handleInputChange('quoteValue', e.target.value)} />
                      </div>
                    </Form.Group>
                  </div>
                  </div>
                  <div className="col-lg-12 grid-margin stretch-card">
          <div className="card shadow">
            <div className="card-body">
              <h4 className="card-title">Line Item </h4>
              <p className="card-description">Item Details
              </p>
              <div className="table-responsive">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th> ID </th>
                      <th> Name </th>
                      <th> UOM </th>
                      <th> Qty</th>
                      <th> Unit Price </th>
                      <th> Tax% </th>
                      <th>Amount </th>
                      <th> Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td> 1 </td>
                      <td> Oracle ERP </td>
                      <td> NOS </td>
                      <td> 1 </td>
                      <td> 20000 </td>
                      <td>18</td>
                      <td>2000</td>
                      <td>Entered</td>
                    </tr>
                    
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
                    {/* Repeat for other CO Details fields... */}
                  </>
                )}

                {this.state.activeTab === 'commercial' && (
                  <>
                  <div className="form-row">
                    <div className='col-6'>
                    <Form.Group>
                      <label>Bill To</label>
                      <div >
                        <Form.Control as="textarea" onChange={(e) => this.handleInputChange('billTo', e.target.value)} />
                      </div>
                    </Form.Group>
                    </div>
                    <div className='col-6'>
                    <Form.Group>
                      <label>Ship To</label>
                      <div>
                        <Form.Control as="textarea" onChange={(e) => this.handleInputChange('shipTo', e.target.value)} />
                      </div>
                    </Form.Group>
                    </div>
                    </div>
                    <div className="row">
            <div className="col-6 col-md-4">
              <Form.Group>
                <label>Despatch Mode</label>
                <Form.Control
                  as="select"
                  defaultValue={this.formData.despatchMode}
                  onChange={(e) => this.handleInputChange('despatchMode', e.target.value) }
                >
                  <option>By Air</option>
                  <option>By Sea</option>
                  <option>By Road</option>
                </Form.Control>
              </Form.Group>
            </div>
            <div className="col-6 col-md-4">
              <Form.Group>
                <label>Payment Terms</label>
                <Form.Control
                  as="select"
                  defaultValue={this.formData.paymentTerms}
                  onChange={(e) => this.handleInputChange('paymentTerms', e.target.value)}
                >
                  <option>Net 15</option>
                  <option>Net 30</option>
                  <option>Net 60</option>
                </Form.Control>
              </Form.Group>
            </div>
             <div className="col-6 col-md-4">
              <Form.Group>
                <label>Freight Charges</label>
                <Form.Control
                  type="number"
                  onChange={(e) => this.handleInputChange('freightCharges', e.target.value)}
                />
              </Form.Group>
            </div>
          </div>
          <div className="row">
            <div className="col-6 col-md-4">
              <Form.Group>
                <label>Tax %</label>
                <Form.Control
                  type="number"
                  onChange={(e) => this.handleInputChange('taxPercent', e.target.value)}
                />
              </Form.Group>
            </div>
            <div className="col-6 col-md-4">
              <Form.Group>
                <label>Tax Amount</label>
                <Form.Control
                  type="number"
                  onChange={(e) => this.handleInputChange('taxAmount', e.target.value)}
                />
              </Form.Group>
            </div>
            <div className="col-6 col-md-4">
              <Form.Group>
                <label>Packing Charges</label>
                <Form.Control
                  type="number"
                  onChange={(e) => this.handleInputChange('packingCharges', e.target.value)}
                />
              </Form.Group>
            </div>
          </div>
          <div className="text-right mt-3">
                  <button type="submit" className="btn btn-success">Save All Details</button>
                </div>
                    {/* Repeat for other commercial fields... */}
                  </>
                )}

                
              </div>
                
            </form>
             {this.state.overlayType && this.renderOverlay()}
          </div>
        </div>
    </div>
    <div id="customerOverlay" style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.6)', zIndex: 9999, display: 'none', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#fff', padding: '20px', borderRadius: '8px', width: '90%', maxWidth: '500px', maxHeight: '80%', overflowY: 'auto' }}>
            <h5>Select a Customer</h5>
            <ul className="list-group">
              {['Yaanar Tech', 'QTech', 'CMW'].map((cust, i) => (
                <li key={i} className="list-group-item list-group-item-action" style={{ cursor: 'pointer' }} onClick={() => this.selectCustomer(cust)}>{cust}</li>
              ))}
            </ul>
            <button className="btn btn-secondary mt-3" onClick={this.hideCustomerOverlay}>Cancel</button>
          </div>
        </div>
        </div>
  )

render() {
    return (
      <div className="container-fluid">
        {this.state.showForm ? this.renderQuoteForm() : this.renderQuoteTable()}
      </div>
    );
  }
} 
export default Quote;
