      <div style="font-size:12px; line-height:1.6;">
        <strong>${org.bgName}</strong><br/>
        ${org.address || ''}<br/>
        <b>E-Mail:</b> ${org.email || ''}<br/>
        <b>Website:</b> ${org.website || ''}<br/>
        <b>GSTIN:</b> ${org.gstin || ''}<br/>
