      </div>
      <div style="width:48%;">
        <div style="background:#011b56; color:white; padding:5px;">Ship To</div>
        <div style="border:1px solid #011b56; border-top:none; padding:6px;">${order.shipTo?.replace(/\n/g, "<br/>") || ''}</div>
      </div>
    </div>

    <table style="width:100%; border-collapse:collapse; margin-top:20px; font-size:11px;">
      <thead>
        <tr style="background:#f4f6fa;">
          <th style="border:1px solid #011b56;">Item</th>
          <th style="border:1px solid #011b56;">Short</th>
          <th style="border:1px solid #011b56;">Description</th>
          <th style="border:1px solid #011b56;">HSN/SAC</th>
          <th style="border:1px solid #011b56;">UOM</th>
          <th style="border:1px solid #011b56;">Unit</th>
          <th style="border:1px solid #011b56;">Quantity</th>
          <th style="border:1px solid #011b56;">Item Value</th>
        </tr>
      </thead>
      <tbody>
        ${(order.lineItems || []).map(item => `
          <tr>
            <td style="border:1px solid #011b56;">${item.itemCode || ''}</td>
            <td style="border:1px solid #011b56;">${item.ptshortName || ''}</td>
