    const height = canvas.height * width / canvas.width;
    doc.addImage(imgData, 'PNG', 0, 0, width, height);
    doc.save(`Sales_Order_${order.orderNo || 'PDF'}.pdf`);
  });

  document.body.removeChild(container);
};
  }


