import React, { Component } from 'react';
import { db } from '../../firebase';
import { collection, addDoc, getDocs, deleteDoc, doc, setDoc } from 'firebase/firestore';
import { Dropdown } from 'react-bootstrap';
import '../../assets/styles/components/_overlay.scss';

const CLASSIFICATION_OPTIONS = [
  'Manufacturer', 'Subcontract', 'Trader', 'Government Service'
];

class CustomerPage extends Component {
  state = {
    customers: [],
    showForm: false,
    showDespatchOverlay: false,
    showPaymentOverlay: false,
    showCurrencyOverlay: false,
    despatchModes: [],
    paymentTerms: [],
    currencies: [],
    overlaySearch: '',
    overlayType: '',
    editingId: null,
    activeTab: 'address',
    formData: {
      code: '',
      shortName: '',
      name: '',
      classification: CLASSIFICATION_OPTIONS[0],
      effectiveFrom: new Date().toISOString().split('T')[0],
      effectiveTo: '',
      introducedDate: new Date().toISOString().split('T')[0],
      introducedBy: '',
      coHold: false,
      invHold: false,
      // Address
      address: ['', '', ''],
      country: '', state: '', city: '', zip: '',
      website: '', mail: '', linkedin: '', skype: '',
      // Commercial Terms
      despatchMode: '', paymentTerms: '', currency: '', gstin: '', cin: '',
      // Contacts
      contacts: [
        { name: '', phone: '', mail: '', skype: '' },
        { name: '', phone: '', mail: '', skype: '' },
        { name: '', phone: '', mail: '', skype: '' }
      ]
    }
  };

  componentDidMount() {
    this.fetchCustomers();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();
    this.fetchCurrencies();
  }

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchDespatchModes = async () => {
    const snap = await getDocs(collection(db, 'modes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ despatchModes: data });
  };

  fetchPaymentTerms = async () => {
    const snap = await getDocs(collection(db, 'paymentTerms'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ paymentTerms: data });
  };

  fetchCurrencies = async () => {
    const snap = await getDocs(collection(db, 'currencies'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ currencies: data });
  };

  toggleForm = (edit = null) => {
    if (edit) {
      this.setState({ showForm: true, editingId: edit.id, activeTab: 'address', formData: { ...edit } });
    } else {
      // Auto code generation
      const code = `CU${101 + this.state.customers.length}`;
      this.setState({
        showForm: true,
        editingId: null,
        activeTab: 'address',
        formData: {
          code,
          shortName: '',
          name: '',
          classification: CLASSIFICATION_OPTIONS[0],
          effectiveFrom: new Date().toISOString().split('T')[0],
          effectiveTo: '',
          introducedDate: new Date().toISOString().split('T')[0],
          introducedBy: '',
          coHold: false,
          invHold: false,
          address: ['', '', ''],
          country: '', state: '', city: '', zip: '',
          website: '', mail: '', linkedin: '', skype: '',
          despatchMode: '', paymentTerms: '', currency: '', gstin: '', cin: '',
          contacts: [
            { name: '', phone: '', mail: '', skype: '' },
            { name: '', phone: '', mail: '', skype: '' },
            { name: '', phone: '', mail: '', skype: '' }
          ]
        }
      });
    }
  };

  handleChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleContactChange = (idx, field, value) => {
    const contacts = [...this.state.formData.contacts];
    contacts[idx][field] = value;
    this.handleChange('contacts', contacts);
  };

  handleAddressChange = (idx, value) => {
    const address = [...this.state.formData.address];
    address[idx] = value;
    this.handleChange('address', address);
  };

  handleCheckbox = (field) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: !prev.formData[field] }
    }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData } = this.state;
    if (!formData.shortName || !formData.name) return alert('Short Name and Customer Name required');
    if (editingId) {
      await setDoc(doc(db, 'customers', editingId), formData);
    } else {
      await addDoc(collection(db, 'customers'), formData);
    }
    this.setState({ showForm: false, editingId: null });
    this.fetchCustomers();
  };

  handleDelete = async (id) => {
    await deleteDoc(doc(db, 'customers', id));
    this.fetchCustomers();
  };

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [prev.overlayType]: value },
      overlayType: '',
      overlaySearch: ''
    }));
  };

  renderOverlay = () => {
    const { overlayType, overlaySearch, despatchModes, paymentTerms, currencies } = this.state;
    let data = [];
    let label = '';
    if (overlayType === 'despatchMode') {
      data = despatchModes;
      label = 'Despatch Mode';
    } else if (overlayType === 'paymentTerms') {
      data = paymentTerms;
      label = 'Payment Terms';
    } else if (overlayType === 'currency') {
      data = currencies;
      label = 'Currency';
    }
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="custom-overlay-title">Select {label}</div>
          <div className="custom-search-bar">
            <input
              type="text"
              className="form-control"
              placeholder={`Search ${label.toLowerCase()}...`}
              value={overlaySearch}
              onChange={e => this.setState({ overlaySearch: e.target.value })}
            />
          </div>
          <div style={{ flex: 1, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Select</th>
                </tr>
              </thead>
              <tbody>
                {data
                  .filter(d => d.name && d.name.toLowerCase().includes((overlaySearch || '').toLowerCase()))
                  .map((d, i) => (
                    <tr key={d.id}>
                      <td>{d.name}</td>
                      <td>
                        <button className="btn btn-sm btn-primary" onClick={() => this.selectOverlayValue(d.name)}>Select</button>
                      </td>
                    </tr>
                  ))}
                {data.length === 0 && <tr><td colSpan="2" className="text-center">No records found</td></tr>}
              </tbody>
            </table>
          </div>
          <button className="btn btn-secondary mt-3 align-self-end" onClick={this.hideOverlay}>Cancel</button>
        </div>
      </div>
    );
  };

  renderForm = () => {
    const { formData, overlayType, activeTab } = this.state;
    return (
      <div className="card p-3 mt-3">
        <h4 className="mb-3">Customer Creation/Update</h4>
        <form className="form-sample" onSubmit={this.handleSubmit} autoComplete="off">
          <div className="form-row">
            <div className="form-group col-md-2">
              <label>Customer Code</label>
              <input className="form-control form-control-sm" value={formData.code} readOnly />
            </div>
            <div className="form-group col-md-2">
              <label>Short Name <span style={{ color: 'red' }}>*</span></label>
              <input className="form-control form-control-sm" value={formData.shortName} onChange={e => this.handleChange('shortName', e.target.value)} required />
            </div>
            <div className="form-group col-md-4">
              <label>Customer Name <span style={{ color: 'red' }}>*</span></label>
              <input className="form-control form-control-sm" value={formData.name} onChange={e => this.handleChange('name', e.target.value)} required />
            </div>
            <div className="form-group col-md-2">
              <label>Classification</label>
              <select className="form-control form-control-sm" value={formData.classification} onChange={e => this.handleChange('classification', e.target.value)}>
                {CLASSIFICATION_OPTIONS.map(opt => <option key={opt}>{opt}</option>)}
              </select>
            </div>
            <div className="form-group col-md-2">
              <label>Introduced Date</label>
              <input className="form-control form-control-sm" type="date" value={formData.introducedDate} onChange={e => this.handleChange('introducedDate', e.target.value)} />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group col-md-2">
              <label>Effective From</label>
              <input className="form-control form-control-sm" type="date" value={formData.effectiveFrom} onChange={e => this.handleChange('effectiveFrom', e.target.value)} />
            </div>
            <div className="form-group col-md-2">
              <label>Effective To</label>
              <input className="form-control form-control-sm" type="date" value={formData.effectiveTo} onChange={e => this.handleChange('effectiveTo', e.target.value)} />
            </div>
            <div className="form-group col-md-4">
              <label>Introduced By</label>
              <input className="form-control form-control-sm" value={formData.introducedBy} onChange={e => this.handleChange('introducedBy', e.target.value)} />
            </div>
            <div className="form-group col-md-2 d-flex align-items-center">
              <div className="form-check mr-3">
                <input className="form-check-input" type="checkbox" checked={formData.coHold} onChange={() => this.handleCheckbox('coHold')} id="coHold" />
                <label className="form-check-label" htmlFor="coHold">Customer Order Hold</label>
              </div>
              <div className="form-check">
                <input className="form-check-input" type="checkbox" checked={formData.invHold} onChange={() => this.handleCheckbox('invHold')} id="invHold" />
                <label className="form-check-label" htmlFor="invHold">Invoice Hold</label>
              </div>
            </div>
          </div>
          {/* Tabs */}
          <ul className="nav nav-tabs mt-3" role="tablist">
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'address' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'address' })}>Address</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'terms' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'terms' })}>Commercial Terms</button>
            </li>
            <li className="nav-item">
              <button type="button" className={`nav-link ${activeTab === 'contacts' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'contacts' })}>Contact Details</button>
            </li>
          </ul>
          {activeTab === 'address' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-4">
                  <label>Address Line 1</label>
                  <input className="form-control form-control-sm" value={formData.address[0]} onChange={e => this.handleAddressChange(0, e.target.value)} required />
                </div>
                <div className="form-group col-md-4">
                  <label>Address Line 2</label>
                  <input className="form-control form-control-sm" value={formData.address[1]} onChange={e => this.handleAddressChange(1, e.target.value)} />
                </div>
                <div className="form-group col-md-4">
                  <label>Address Line 3</label>
                  <input className="form-control form-control-sm" value={formData.address[2]} onChange={e => this.handleAddressChange(2, e.target.value)} />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group col-md-3"><label>Country</label><input className="form-control form-control-sm" value={formData.country} onChange={e => this.handleChange('country', e.target.value)} required /></div>
                <div className="form-group col-md-3"><label>Province/State</label><input className="form-control form-control-sm" value={formData.state} onChange={e => this.handleChange('state', e.target.value)} required /></div>
                <div className="form-group col-md-3"><label>City</label><input className="form-control form-control-sm" value={formData.city} onChange={e => this.handleChange('city', e.target.value)} required /></div>
                <div className="form-group col-md-3"><label>Zipcode/Pincode</label><input className="form-control form-control-sm" value={formData.zip} onChange={e => this.handleChange('zip', e.target.value)} required /></div>
              </div>
              <div className="form-row">
                <div className="form-group col-md-3"><label>Website</label><input className="form-control form-control-sm" value={formData.website} onChange={e => this.handleChange('website', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Mail</label><input className="form-control form-control-sm" value={formData.mail} onChange={e => this.handleChange('mail', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>LinkedIn</label><input className="form-control form-control-sm" value={formData.linkedin} onChange={e => this.handleChange('linkedin', e.target.value)} /></div>
                <div className="form-group col-md-3"><label>Skype</label><input className="form-control form-control-sm" value={formData.skype} onChange={e => this.handleChange('skype', e.target.value)} /></div>
              </div>
            </div>
          )}
          {activeTab === 'terms' && (
            <div className="mt-3">
              <div className="form-row">
                <div className="form-group col-md-4">
                  <label>Despatch Mode</label>
                  <div className="input-group input-group-sm">
                    <input className="form-control" value={formData.despatchMode} readOnly onClick={() => this.showOverlay('despatchMode')} style={{ background: '#f8f9fa', cursor: 'pointer' }} />
                    <div className="input-group-append">
                      <button className="btn btn-outline-secondary btn-sm" type="button" onClick={() => this.showOverlay('despatchMode')}>Select</button>
                    </div>
                  </div>
                </div>
                <div className="form-group col-md-4">
                  <label>Payment Terms</label>
                  <div className="input-group input-group-sm">
                    <input className="form-control" value={formData.paymentTerms} readOnly onClick={() => this.showOverlay('paymentTerms')} style={{ background: '#f8f9fa', cursor: 'pointer' }} />
                    <div className="input-group-append">
                      <button className="btn btn-outline-secondary btn-sm" type="button" onClick={() => this.showOverlay('paymentTerms')}>Select</button>
                    </div>
                  </div>
                </div>
                <div className="form-group col-md-4">
                  <label>Currency</label>
                  <div className="input-group input-group-sm">
                    <input className="form-control" value={formData.currency} readOnly onClick={() => this.showOverlay('currency')} style={{ background: '#f8f9fa', cursor: 'pointer' }} />
                    <div className="input-group-append">
                      <button className="btn btn-outline-secondary btn-sm" type="button" onClick={() => this.showOverlay('currency')}>Select</button>
                    </div>
                  </div>
                </div>
              </div>
              <div className="form-row">
                <div className="form-group col-md-4"><label>GSTIN</label><input className="form-control form-control-sm" value={formData.gstin} onChange={e => this.handleChange('gstin', e.target.value)} /></div>
                <div className="form-group col-md-4"><label>CIN</label><input className="form-control form-control-sm" value={formData.cin} onChange={e => this.handleChange('cin', e.target.value)} /></div>
              </div>
            </div>
          )}
          {activeTab === 'contacts' && (
            <div className="mt-3">
              {[0, 1, 2].map(i => (
                <div className="form-row" key={i}>
                  <div className="form-group col-md-3"><label>Contact Person</label><input className="form-control form-control-sm" value={formData.contacts[i].name} onChange={e => this.handleContactChange(i, 'name', e.target.value)} /></div>
                  <div className="form-group col-md-3"><label>Phone</label><input className="form-control form-control-sm" value={formData.contacts[i].phone} onChange={e => this.handleContactChange(i, 'phone', e.target.value)} /></div>
                  <div className="form-group col-md-3"><label>Mail</label><input className="form-control form-control-sm" value={formData.contacts[i].mail} onChange={e => this.handleContactChange(i, 'mail', e.target.value)} /></div>
                  <div className="form-group col-md-3"><label>Skype</label><input className="form-control form-control-sm" value={formData.contacts[i].skype} onChange={e => this.handleContactChange(i, 'skype', e.target.value)} /></div>
                </div>
              ))}
            </div>
          )}
          <div className="d-flex justify-content-between mt-3">
            <button type="submit" className="btn btn-success btn-sm">Save</button>
            <button type="button" className="btn btn-secondary btn-sm" onClick={() => this.setState({ showForm: false })}>Cancel</button>
          </div>
        </form>
        {overlayType && this.renderOverlay()}
      </div>
    );
  };

  renderTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Customers</h4>
          <button className="btn btn-primary btn-sm" onClick={() => this.toggleForm()}>+ Add Customer</button>
        </div>
        <div className="custom-table-responsive">
          <table className="table table-bordered table-sm">
            <thead className='thead-light'>
              <tr>
                <th>Code</th>
                <th>Customer Name</th>
                <th>Short Name</th>
                <th>Classification</th>
                <th>Intro Date</th>
                <th>Intro By</th>
                <th>Effective From</th>
                <th>Effective To</th>
                <th>CO Hold</th>
                <th>Inv Hold</th>
                <th>Tax</th>
                <th>Currency</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.customers.map((c, i) => (
                <tr key={c.id}>
                  <td>{c.code}</td>
                  <td>{c.name}</td>
                  <td>{c.shortName}</td>
                  <td>{c.classification}</td>
                  <td>{c.introducedDate}</td>
                  <td>{c.introducedBy}</td>
                  <td>{c.effectiveFrom}</td>
                  <td>{c.effectiveTo}</td>
                  <td>{c.coHold ? 'Yes' : 'No'}</td>
                  <td>{c.invHold ? 'Yes' : 'No'}</td>
                  <td>{c.gstin}</td>
                  <td>{c.currency}</td>
                  <td>
                    <Dropdown>
                      <Dropdown.Toggle variant="btn btn-outline-primary btn-sm" id={`dropdownMenuOutlineButton${i}`}>
                        Action
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item onClick={() => this.toggleForm(c)}>Update</Dropdown.Item>
                        <Dropdown.Item onClick={() => this.handleDelete(c.id)}>Delete</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </td>
                </tr>
              ))}
              {this.state.customers.length === 0 && <tr><td colSpan="13" className="text-center">No records found</td></tr>}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  render() {
    return <div className="container-fluid">{this.state.showForm ? this.renderForm() : this.renderTable()}</div>;
  }
}

export default CustomerPage;
