import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import './App.scss';
import AppRoutes from './AppRoutes';
import Sidebar from './shared/Sidebar';
import Footer from './shared/Footer';
import { withTranslation } from "react-i18next";

class App extends Component {
  state = {
    isFullPageLayout: false
  };

  componentDidMount() {
    this.onRouteChanged();
  }

  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged();
    }
  }

  onRouteChanged() {
    const { i18n } = this.props;
    const fullPageLayoutRoutes = [
      '/user-pages/login-1', '/user-pages/login-2',
      '/user-pages/register-1', '/user-pages/register-2',
      '/user-pages/lockscreen', '/error-pages/error-404',
      '/error-pages/error-500', '/general-pages/landing-page'
    ];

    const isFullPage = fullPageLayoutRoutes.includes(this.props.location.pathname);
    this.setState({ isFullPageLayout: isFullPage });

    document.body.classList.toggle('rtl', this.props.location.pathname === '/layout/RtlLayout');
    i18n.changeLanguage(this.props.location.pathname === '/layout/RtlLayout' ? 'ar' : 'en');

    const wrapper = document.querySelector('.page-body-wrapper');
    if (wrapper) {
      wrapper.classList.toggle('full-page-wrapper', isFullPage);
    }

    window.scrollTo(0, 0);
  }

  render() {
    if (this.state.isFullPageLayout) {
      return (
        <div className="container-scroller">
          <div className="container-fluid page-body-wrapper">
            <div className="main-panel">
              <div className="content-wrapper">
                <AppRoutes />
              </div>
            </div>
          </div>
        </div>
      );
    }

    return (
      <div className="container-scroller">
        <div className="container-fluid page-body-wrapper">
          <Sidebar />
          <div className="main-panel">
            <div className="content-wrapper">
              <AppRoutes />
            </div>
            <Footer />
          </div>
        </div>
      </div>
    );
  }
}

export default withTranslation()(withRouter(App));
