// Register.js
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from '../../firebase';

class Register extends Component {
  state = { email: "", password: "", username: "", error: "" };

  handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(auth, this.state.email, this.state.password);
      // Firebase automatically logs them in → App.js will show Dashboard
    } catch (err) {
      this.setState({ error: err.message });
    }
  };

  render() {
    return (
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
          <div className="col-lg-4 mx-auto">
            <div className="auth-form-light text-left py-5 px-4 px-sm-5">
              <div className="brand-logo">
                <img src={require("../../assets/images/logo.svg")} alt="logo" />
              </div>
              <h4>New here?</h4>
              <form className="pt-3" onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <input type="text" className="form-control form-control-lg"
                    placeholder="Username"
                    onChange={(e) => this.setState({ username: e.target.value })}
                  />
                </div>
                <div className="form-group">
                  <input type="email" className="form-control form-control-lg"
                    placeholder="Email"
                    onChange={(e) => this.setState({ email: e.target.value })}
                  />
                </div>
                <div className="form-group">
                  <input type="password" className="form-control form-control-lg"
                    placeholder="Password"
                    onChange={(e) => this.setState({ password: e.target.value })}
                  />
                </div>
                {this.state.error && <p className="text-danger">{this.state.error}</p>}
                <div className="mt-3">
                  <button className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" type="submit">
                    SIGN UP
                  </button>
                </div>
                <div className="text-center mt-4 font-weight-light">
                  Already have an account? <Link to="/user-pages/login" className="text-primary">Login</Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Register;
