                          </div>
                          <div className="table-responsive">
                            <table className="table table-bordered">
                              <thead className="thead-light">
                                <tr >
                                  <th> Item Code </th>
                                  <th> Item Desc </th>
                                  <th> Cust Part No </th>
                                  <th> HSN No </th>
                                  <th> On Hand </th> 
                                  <th>Unit Price</th>
                                  <th>Quantity</th>
                                  <th>Tax Id</th>
                                  <th>Item Total</th>
                                  <th>Tax Amt</th>
                                </tr>
                              </thead>
                              <tbody>
                                {formData.lineItems.map((item, idx) => (
                                  <tr key={item.id || idx}>
                                    <td>{item.itemCode}</td>
                                    <td>{item.itemDescription}</td>
                                    <td>{item.custPartNo}</td>
                                    <td>{item.hsnNo}</td>
                                    <td>{item.onHand}</td>
                                    <td>
                                      <input
                                        type="number"
                                        value={item.unitPrice}
                                        onChange={e => this.handleLineItemChange(idx, 'unitPrice', e.target.value)}
                                        style={{ width: 80 }}
                                      />
                                    </td>
                                    <td>
                                      <input
                                        type="number"
                                        min="1"
                                        value={item.qty}
                                        onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                                        style={{ width: 60 }}
                                      />
                                    </td>
                                    <td style={{ verticalAlign: 'middle' }}>
                                      <button
                                        type="button"
                                        className="btn btn-sm btn-outline-primary"
                                        onClick={() => this.setState({ showTaxOverlay: true, currentTaxIdx: idx })}
                                      >
                                        Select Tax
                                      </button>
                                      <div style={{ fontSize: '10px', marginTop: '4px' }}>
                                        {(item.taxGroupNames || []).join(', ') || '-'}
                                      </div>
                                    </td>
                                      <td>{item.taxAmount || '0.00'}</td>
                                      <td>{item.total || '0.00'}</td>
                                  </tr>
                                ))}
                                {formData.lineItems.length === 0 && (
                                  <tr>
                                    <td colSpan="9" className="text-center">No items</td>
                                  </tr>
                                )}
                              </tbody>
                            </table>
                          </div>
                          {/* Add pagination for line items here if needed */}
                          <div className="d-flex justify-content-between align-items-center mt-2">
                            <span>Page 1 of 1</span>
                          </div>
                      
                  </>
                )}
                {this.state.activeTab === 'commercial' && (
                  <>
                    <div className="form-row">
                      <div className="form-group col-md-6">
                        <label>Bill To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.billTo}
                          onChange={(e) => this.handleInputChange('billTo', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-6">
                        <label>Ship To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.shipTo}
                          onChange={(e) => this.handleInputChange('shipTo', e.target.value)}
                        />
                      </div>
                    </div>
                    <div className="form-row">
                    <div className="form-group col-md-4">
  <label>Despatch Mode</label>
  <div className="input-group input-group-sm">
    <input
      type="text"
      className="form-control"
      value={formData.despatchMode}
      readOnly
      onClick={() => !isFOB && this.showOverlay('despatchMode')}
      style={{ cursor: isFOB ? 'not-allowed' : 'pointer', backgroundColor: '#fff' }}
    />
    <div className="input-group-append">
      <button
        className="btn btn-outline-secondary btn-sm"
        type="button"
        disabled={isFOB}
        onClick={() => this.showOverlay('despatchMode')}
      >
        Select
      </button>
    </div>
  </div>
</div>

<div className="form-group col-md-4">
  <label>Payment Terms</label>
  <div className="input-group input-group-sm">
    <input
      type="text"
      className="form-control"
      value={formData.paymentTerms}
      readOnly
      onClick={() => !isFOB && this.showOverlay('paymentTerms')}
      style={{ cursor: isFOB ? 'not-allowed' : 'pointer', backgroundColor: '#fff' }}
    />
    <div className="input-group-append">
      <button
        className="btn btn-outline-secondary btn-sm"
        type="button"
        disabled={isFOB}
        onClick={() => this.showOverlay('paymentTerms')}
      >
        Select
      </button>
    </div>
  </div>
</div>

                      <div className="form-group col-md-4">
                        <label>Freight Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.freightCharges}
                          onChange={(e) => this.handleInputChange('freightCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Tax %</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxPercent}
                          onChange={(e) => this.handleInputChange('taxPercent', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Freight Tax Amount</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxAmount}
                          onChange={(e) => this.handleInputChange('freighttaxAmount', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Packing Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.packingCharges}
                          onChange={(e) => this.handleInputChange('packingCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="text-right mt-3">
                      <button type="submit" className="btn btn-success">Save All Details</button>
                    </div>
                  </>
                )}
                 {this.state.activeTab === 'breakdown' && (
                  <>
                    <h5>Milestone Breakdown</h5>

                    <div className="row mb-2">
                      <div className="col"><b>Quote Value:</b> {formData.quoteValue}</div>
                      <div className="col"><b>Tax Amount:</b> {formData.taxAmount}</div>
                      <div className="col"><b>Sum of Item Totals:</b> {sumOfItemTotals.toFixed(2)}</div>
                    </div>

                    <div className="row mb-2">
                      <div className="col-md-3">
                        <label>Subproduct Mode</label>
                        <select
                          className="form-control form-control-sm"
                          value={breakdownType}
                          onChange={e => this.handleBreakdownTypeChange(e.target.value)}
                        >
                          <option value="Amount">Amount</option>
                          <option value="Percentage">Percentage</option>
                        </select>
                      </div>

                      <div className="col-md-9 text-right">
                        <button type="button" className="btn btn-primary btn-sm" onClick={() => {
                          // Add breakdown item for each product automatically if none exist (convenience)
                          if ((formData.lineItems || []).length > 0) {
                            this.setState(prev => {
                              const bi = [...prev.breakdownItems];
                              // create breakdown entries from lineItems if not present
                              formData.lineItems.forEach((li, i) => {
                                const exists = bi.some(b => b.productId === li.itemCode);
                                if (!exists) {
                                  bi.push({ sno: bi.length + 1, productId: li.itemCode, desc: li.itemDescription || '', subProducts: [] });
                                }
                              });
                              return { breakdownItems: bi };
                            });
                          } else {
                            this.setState(prev => ({ breakdownItems: [...prev.breakdownItems, { sno: prev.breakdownItems.length + 1, productId: '', desc: '', subProducts: [] }] }));
                          }
                        }}>+ Add Breakdown</button>
                      </div>
                    </div>

                    <table className="table table-bordered">
                      <thead className="thead-light">
                        <tr>
                          <th>S.No</th>
                          <th>ID</th>
                          <th>Desc / Subproduct name</th>
                          <th>SubProduct value ({breakdownType === 'Percentage' ? '%' : 'â‚¹'})</th>
                          <th>Total (â‚¹)</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {breakdownItems.map((item, idx) => {
                          const mainItemTotal = itemTotals[item.productId] || 0;
                          const subSum = this.getSubProductsSum(item);
                          const remaining = (mainItemTotal - subSum);
                          const subs = Array.isArray(item.subProducts) ? item.subProducts : [];

                          return (
                            <React.Fragment key={idx}>
                              {/* parent product row */}
                              <tr style={{ background: '#eef4ff' }}>
                                <td>{item.sno}</td>
                                <td>{item.productId}</td>
                                <td>{item.desc}</td>

                                {/* SubProduct value column now shows subproducts sum & remaining */}
                                <td>
                                  <div>
                                    <div><small>Subproducts sum: <b>{subSum.toFixed(2)}</b></small></div>
                                    <div><small>Remaining: <b style={{ color: remaining > 0.01 ? 'red' : 'green' }}>{remaining.toFixed(2)}</b></small></div>
                                  </div>
                                </td>

                                {/* Total column: main item total */}
                                <td>{mainItemTotal.toFixed(2)}</td>

                                {/* Action column: add / autofill buttons */}
                                <td>
                                  <div className="d-flex">
                                    <button
                                      type="button"
                                      className="btn btn-sm btn-outline-primary mr-2"
                                      onClick={() => this.handleAddInlineSubProduct(idx)}
                                    >+ Add</button>

                                    <button
                                      type="button"
                                      className="btn btn-sm btn-outline-secondary"
                                      onClick={() => {
                                        if (remaining <= 0) return;
                                        this.handleAddInlineSubProduct(idx, {
                                          name: 'Remainder',
                                          value: breakdownType === 'Percentage'
                                            ? ( (remaining * 100) / (mainItemTotal || 1) ).toFixed(2)
                                            : remaining.toFixed(2)
                                        });
                                      }}
                                    >Auto-fill</button>
                                  </div>
                                </td>
                              </tr>

                              {/* subproduct rows (inline editable) */}
                              {subs.length === 0 && (
                                <tr key={`notice-${idx}`}>
                                  <td colSpan="6" className="text-center text-muted">No subproducts added for this item.</td>
                                </tr>
                              )}

                              {subs.map((sp, spIdx) => {
                                const spId = `${item.productId}_${spIdx + 1}`;
                                const rawVal = parseFloat(sp.value || 0) || 0;
                                const spTotal = breakdownType === 'Percentage'
                                  ? (mainItemTotal * rawVal / 100)
                                  : rawVal;

                                return (
                                  <tr key={spId} style={{ background: '#f9f9f9' }}>
                                    {/* Disabled / greyed SNo cell for sub-rows */}
                                    <td className="text-muted" aria-disabled="true">â€”</td>

                                    {/* ID column */}
                                    <td>{spId}</td>

                                    {/* Desc / Subproduct name with indentation to show hierarchy */}
                                    <td>
                                      <input
                                        type="text"
                                        className="form-control form-control-sm"
                                        value={sp.name || ''}
                                        onChange={(e) => this.handleSubProductChange(idx, spIdx, 'name', e.target.value)}
                                        placeholder="Enter name"
                                        style={{ paddingLeft: '18px' }} // indentation
                                      />
                                    </td>

                                    {/* SubProduct value (amount or percent input) */}
                                    <td>
                                      <input
                                        type="number"
                                        step="any"
                                        min="0"
                                        className="form-control form-control-sm"
                                        value={sp.value || ''}
                                        onChange={(e) => this.handleSubProductChange(idx, spIdx, 'value', e.target.value)}
                                        style={{ width: '120px' }}
                                      />
                                    </td>

                                    {/* Total computed from value */}
                                    <td>{spTotal.toFixed(2)}</td>

                                    {/* Remove button */}
                                    <td>
                                      <button
                                        type="button"
                                        className="btn btn-sm btn-danger"
                                        onClick={() => this.handleRemoveSubProduct(idx, spIdx)}
                                      >-</button>
                                    </td>
                                  </tr>
                                );
                              })}
                            </React.Fragment>
                          );
                        })}
                      </tbody>
                    </table>

                    {!this.isBreakdownValid() && (
                      <div className="alert alert-danger">
                        Breakdown incomplete â€” for each product the sum of subproduct totals must equal the product total. Save disabled until fixed.
                      </div>
                    )}
                  </>
                )}
              </div>

             <div className="fixed-card-footer text-right p-3 border-top bg-white">
                <button type="submit" className="btn btn-success mr-2" disabled={!this.isBreakdownValid()}>Save All Details</button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => this.setState({ showForm: false, editingId: null })}
                >
                  Cancel
                </button>
              </div>
            </form>
            {overlayType && this.renderOverlay()}
            {productOverlayVisible && this.renderProductOverlay()}
            {this.state.showTaxOverlay && this.renderTaxOverlay()}
          </div>
           
        </div>
      </div>
    );
  };

  render() {
    return (
      <div className="container-fluid">
        {this.state.previewOrderMode ? this.renderOrderPreview() : (this.state.showForm ? this.renderorderForm() : this.renderorderTable())}
      </div>
    );
  }
}

export default withRouter(Order);
