import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';
import { withRouter } from 'react-router-dom';
import html2canvas from 'html2canvas';
import { jsPDF } from 'jspdf';
import { recalculateTotals, getTaxDetailsFromGroup } from './calculation';

const numberToWords = (num) => {
  const a = ['', 'One', 'Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight', 'Nine',
    'Ten', 'Eleven', 'Twelve', 'Thirteen', 'Fourteen', 'Fifteen',
    'Sixteen', 'Seventeen', 'Eighteen', 'Nineteen'];
  const b = ['', '', 'Twenty', 'Thirty', 'Forty', 'Fifty', 'Sixty', 'Seventy', 'Eighty', 'Ninety'];

  if (num === 0) return 'Zero';
  if (num < 20) return a[num];
  if (num < 100) return b[Math.floor(num / 10)] + ' ' + a[num % 10];
  if (num < 1000)
    return a[Math.floor(num / 100)] + ' Hundred ' + numberToWords(num % 100);
  if (num < 100000)
    return numberToWords(Math.floor(num / 1000)) + ' Thousand ' + numberToWords(num % 1000);
  return 'Amount too large';
};


class Quote extends Component {
  state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    products: [],
    despatchModes: [],
    paymentTerms: [],
    showForm: false,
    showTaxOverlay: false,
    currentTaxLineIdx: null,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    selectedTaxGroups: [],
    taxComponents: [],
    taxGroups:[],
    previewMode: false,
    selectedQuote: null,
    breakdownItems: [], 
    breakdownType: 'Percentage',
    breakdownSelectAll: false,
    showSubProductDialog: false,
    currentBreakdownIdx: null,
    subProductForm: { name: '', value: '', type: 'Amount', dueDate: '' },
    formData: {
      quoteNo: '',
      quoteDate: new Date().toISOString().split('T')[0],
      quoteType: 'Standard',
      customer: '',
      status: 'Entered', // Static 'Entered'
      choose: 'Service',
      qrefNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      quoteValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      freighttaxAmount: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  // Helper to format address for Bill To / Ship To
  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();
    this.fetchProducts();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();
    this.fetchTaxGroups();
  }
handleLineItemChange = (idx, field, value) => {
  const updatedItems = [...this.state.formData.lineItems];
  updatedItems[idx] = { ...updatedItems[idx], [field]: value };
  const { updatedLineItems, freighttaxAmount, taxAmount, quoteValue } = recalculateTotals({
    lineItems: updatedItems,
    freightCharges: this.state.formData.freightCharges,
    packingCharges: this.state.formData.packingCharges,
    taxPercent: this.state.formData.taxPercent,
    taxGroups: this.state.taxGroups
  });
  this.setState(prev => ({
    formData: {
      ...prev.formData,
      lineItems: updatedLineItems,
      freighttaxAmount,
      taxAmount,
      quoteValue
    }
  }));
};

componentDidUpdate(prevProps, prevState) {
  // Only update if the number of lineItems changes (add/remove product)
  const prevLineItems = prevState.formData.lineItems || [];
  const currLineItems = this.state.formData.lineItems || [];
  if (prevLineItems.length !== currLineItems.length) {
    // Try to preserve subProducts and values
    const prevBreakdownMap = {};
    (this.state.breakdownItems || []).forEach(bi => {
      prevBreakdownMap[bi.productId] = bi;
    });
    this.setState({
      breakdownItems: currLineItems.map((li, idx) => ({
        sno: idx + 1,
        productId: li.itemCode,
        desc: li.itemDescription,
        value: prevBreakdownMap[li.itemCode]?.value || '',
        lastDate: prevBreakdownMap[li.itemCode]?.lastDate || '',
        subProducts: prevBreakdownMap[li.itemCode]?.subProducts || []
      }))
    });
  }
}

fetchTaxGroups = async () => {
  const snap = await getDocs(collection(db, 'taxGroups'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ taxGroups: data });
};


  fetchQuotes = async () => {
    const snap = await getDocs(collection(db, 'quotes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ quotes: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };
  fetchDespatchModes = async () => {
  const snap = await getDocs(collection(db, 'modes'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ despatchModes: data });
};

fetchPaymentTerms = async () => {
  const snap = await getDocs(collection(db, 'paymentTerms'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ paymentTerms: data });
};

handleInputChange = (field, value) => {
  this.setState(prev => ({
    formData: { ...prev.formData, [field]: value }
  }), () => {
    if (['freightCharges', 'taxPercent', 'packingCharges'].includes(field)) {
      const { updatedLineItems, freighttaxAmount, taxAmount, quoteValue } = recalculateTotals({
        lineItems: this.state.formData.lineItems,
        freightCharges: this.state.formData.freightCharges,
        packingCharges: this.state.formData.packingCharges,
        taxPercent: this.state.formData.taxPercent,
        taxGroups: this.state.taxGroups
      });
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          lineItems: updatedLineItems,
          freighttaxAmount,
          taxAmount,
          quoteValue
        }
      }));
    }
  });
};

