                onChange={e => this.handleBreakdownTypeChange(e.target.value)}
                className="form-control form-control-sm font-weight-bold"
                style={{ fontWeight: 'bold', display: 'inline-block', width: 'auto', marginLeft: 8 }}
              >
                <option value="Percentage">%</option>
                <option value="Amount">₹</option>
              </select>
            </th>
            <th>Due Date</th>
            <th>
              <b>Item Total</b>
            </th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {breakdownItems.map((item, idx) => {
            // Main item total (with tax) from lineItems
            const mainItemTotal = itemTotals[item.productId] || 0;

            // Sub-product sum logic
            const subTotal = (item.subProducts || []).reduce((sum, sp) => {
              if (sp.type === 'Amount') return sum + parseFloat(sp.value || 0);
              if (sp.type === 'Percentage') return sum + (mainItemTotal * parseFloat(sp.value || 0) / 100);
              return sum;
            }, 0);

            const isSatisfied = Math.abs(mainItemTotal - subTotal) < 0.01 && subTotal > 0;

            return (
              <React.Fragment key={idx}>
                <tr>
                  <td>{item.sno}</td>
                  <td>{item.productId}</td>
                  <td>{item.desc}</td>
                  <td>
                    <div className="input-group input-group-sm">
                      <div className="input-group-prepend">
                        <span className="input-group-text" style={{ fontWeight: 'bold' }}>
                          {breakdownType === 'Percentage' ? '%' : '₹'}
                        </span>
                      </div>
                      <input
                        type="number"
                        className="form-control"
                        value={item.value}
                        onChange={e => {
                          this.handleBreakdownValueChange(idx, e.target.value);
                          this.saveBreakdownToSession();
                        }}
                        placeholder={breakdownType === 'Percentage' ? 'Enter %' : 'Enter Amt'}
                        style={{ fontWeight: 'bold' }}
                      />
                    </div>
                  </td>
                  <td>
                    <input
                      type="date"
                      className="form-control form-control-sm"
                      value={item.lastDate}
                      onChange={e => {
                        this.handleBreakdownDateChange(idx, e.target.value);
                        this.saveBreakdownToSession();
                      }}
                    />
                  </td>
                  <td>
                    <span style={{ color: isSatisfied ? 'green' : 'red', fontWeight: 'bold' }}>
                      {mainItemTotal.toFixed(2)}
                    </span>
                  </td>
                  <td>
                    <button
                      type = "button" className="btn btn-sm btn-outline-primary"
                      onClick={() => this.setState({ showSubProductDialog: true, currentBreakdownIdx: idx })}
                    >
                      + Add Milestone
                    </button>
                  </td>
                </tr>
                {/* Sub-products */}
                {(item.subProducts || []).map((sp, spIdx) => {
                  // Sub-product item total calculation
                  let spTotal = 0;
                  if (sp.type === 'Amount') spTotal = parseFloat(sp.value || 0);
                  else if (sp.type === 'Percentage') spTotal = mainItemTotal * parseFloat(sp.value || 0) / 100;
                  return (
                    <tr key={spIdx} style={{ background: '#f9f9f9' }}>
                      <td></td>
                      <td colSpan={2} style={{ paddingLeft: 40 }}>→ {sp.name}</td>
                      <td>
                        <span style={{ fontWeight: 'bold' }}>
                          {sp.type === 'Amount' ? `₹${sp.value}` : `${sp.value}%`}
                        </span>
                      </td>
                      <td>{sp.dueDate}</td>
                      <td>
                        <span style={{ fontWeight: 'bold' }}>{spTotal.toFixed(2)}</span>
                      </td>
                      <td></td>
                    </tr>
                  );
                })}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

renderSubProductDialog = () => {
