        </div>
      </div>
      <div className="row">
        <div className="col-md-4"><b>Customer:</b> {q.customer}</div>
        <div className="col-md-4"><b>Date:</b> {q.quoteDate}</div>
        <div className="col-md-4"><b>Status:</b> {q.status}</div>
      </div>
      <div className="row mt-2">
        <div className="col-md-6"><b>Bill To:</b><br />{q.billTo}</div>
        <div className="col-md-6"><b>Ship To:</b><br />{q.shipTo}</div>
      </div>
      <h5 className="mt-4">Line Items</h5>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Item Code</th><th>Description</th><th>Qty</th><th>Unit Price</th><th>Total</th>
          </tr>
        </thead>
        <tbody>
          {q.lineItems?.map((item, i) => (
            <tr key={i}>
              <td>{item.itemCode}</td>
              <td>{item.itemDescription}</td>
              <td>{item.qty}</td>
              <td>{item.unitPrice}</td>
              <td>{item.total}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="text-right"><b>Total:</b> {q.quoteValue}</div>
      <button className="btn btn-secondary mt-3" onClick={() => this.setState({ previewMode: false })}>Back to List</button>
    </div>
  );
};

  render() {
    return (
      <div className="container-fluid">
        {this.state.previewMode? this.renderQuotePreview(): this.state.showForm ? this.renderQuoteForm() : this.renderQuoteTable()}
        

      </div>
    );
  }
}

export default withRouter(Quote);
