  );
};


  
  renderTabs = () => {
    const { activeTab } = this.state;
    const tabNames = ['Lines', 'Terms', 'Notes'];
    return (
      <ul className="nav nav-tabs mb-3" style={{ fontSize: '16px' }}>
        {tabNames.map((tab, idx) => (
          <li className="nav-item" key={tab}>
            <button
              type="button"
              className={`nav-link${activeTab === idx ? ' active' : ''}`}
              onClick={() => this.handleTabChange(idx)}
              style={{
                color: '#007bff',
                background: activeTab === idx ? '#e9ecef' : '#fff',
                border: '1px solid #dee2e6',
                borderBottom: activeTab === idx ? 'none' : undefined,
                fontWeight: activeTab === idx ? 'bold' : 'normal'
              }}
            >
              {tab}
            </button>
          </li>
        ))}
      </ul>
    );
  };

  renderLinesTab = () => {
    const { formData, showProductOverlay } = this.state;
    return (
      <div>
        <div className="form-row mb-2">
          <button className="btn btn-primary btn-sm mr-2" onClick={this.showProductOverlay} disabled={!this.state.canAddItems}>Add Items</button>
          <button className="btn btn-info btn-sm" onClick={() => this.setState({ showResourceOverlay: true })}>Add Resource</button>
        </div>
        <div className="table-responsive" style={{ overflowX: 'auto' }}>
          <table className="table table-bordered" style={{ minWidth: 1200 }}>
            <thead>
              <tr>
                <th>Item Code</th>
                <th>Item Desc</th>
                <th>HSN No</th>
                <th>UOM</th>
                <th>Qty</th>
                <th>Unit/Day or Wk</th>
                <th>Unit Price</th>
                <th>Months</th>
                <th>Tax Group</th>
                <th>Tax Amt</th>
                <th>Item Total</th>
              </tr>
            </thead>
            <tbody>
              {(formData.lineItems || []).map((item, idx) => (
                <tr key={idx}>
                  <td>{item.itemCode}</td>
                  <td>{item.itemDesc}</td>
                  <td>{item.hsnNo}</td>
                  <td>{item.uom}</td>
                  <td>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={item.qty}
                      min="1"
                      style={{ width: 80 }}
                      onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={item.unitDayOrWk}
                      min="1"
                      style={{ width: 80 }}
                      onChange={e => this.handleLineItemChange(idx, 'unitDayOrWk', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={item.unitPrice}
                      min="0"
                      style={{ width: 100 }}
                      onChange={e => this.handleLineItemChange(idx, 'unitPrice', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="form-control form-control-sm"
                      value={item.months}
                      min="1"
                      style={{ width: 80 }}
                      onChange={e => this.handleLineItemChange(idx, 'months', e.target.value)}
                    />
                  </td>
                  <td>
                    <select
                      className="form-control form-control-sm"
                      value={item.taxGroupNames[0] || ''}
                      onChange={e => this.toggleTaxGroupSelection(e.target.value, idx, true)}
                    >
                      <option value="">-Select-</option>
                      {this.state.taxGroups.map((tg, i) => (
                        <option key={i} value={tg.groupName}>{tg.groupName}</option>
                      ))}
                    </select>
                  </td>
                  <td>{item.taxAmt}</td>
                  <td>{item.itemTotal}</td>
                </tr>
              ))}
              {(!formData.lineItems || formData.lineItems.length === 0) && (
                <tr>
                  <td colSpan="11" className="text-center">No items</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        {showProductOverlay && this.renderProductOverlay()}
      </div>
    );
  };

  renderTermsTab = () => {
