import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import bsCustomFileInput from 'bs-custom-file-input';
import { db } from '../../firebase';
import { collection, getDocs, addDoc, doc, updateDoc } from 'firebase/firestore';
import { withRouter } from 'react-router-dom';

class Quote extends Component {
  state = {
    activeTab: 'co',
    quotes: [],
    customers: [],
    products: [],
    despatchModes: [],
    paymentTerms: [],
    showForm: false,
    editingId: null,
    overlayType: '',
    overlaySearch: '',
    productOverlayVisible: false,
    productOverlaySearch: '',
    selectedProductIds: [],
    taxComponents: [],
    taxGroups:[],
    previewMode: false,
    selectedQuote: null,
    formData: {
      quoteNo: '',
      quoteDate: new Date().toISOString().split('T')[0],
      quoteType: 'Standard',
      customer: '',
      status: 'Entered', // Static 'Entered'
      choose: 'Service',
      qrefNo: '',
      impExp: 'None',
      currency: '',
      conversionRate: '',
      taxAmount: '',
      quoteValue: '',
      billTo: '',
      shipTo: '',
      despatchMode: 'By Air',
      paymentTerms: '',
      freightCharges: '',
      freighttaxAmount: '',
      taxPercent: '',
      packingCharges: '',
      lineItems: [],
    }
  };
  customerInputRef = React.createRef();

  // Helper to format address for Bill To / Ship To
  formatAddress = (addr) => {
    if (!addr) return '';
    if (typeof addr === 'string') return addr;
    return [
      addr.address,
      [addr.city, addr.state, addr.country].filter(Boolean).join(', '),
      addr.zip
    ].filter(Boolean).join('\n');
  };

  componentDidMount() {
    bsCustomFileInput.init();
    this.fetchQuotes();
    this.fetchCustomers();
    this.fetchProducts();
    this.fetchDespatchModes();
    this.fetchPaymentTerms();

  }

getTaxDetailsFromGroup(groupId) {
  if (!groupId || groupId === 'nill' || groupId === 'more') return { totalPercent: 0, totalAmount: 0 };

  const taxGroup = this.state.taxGroups.find(tg => tg.id === groupId);
  if (!taxGroup || !Array.isArray(taxGroup.lineItems)) return { totalPercent: 0, totalAmount: 0 };

  let totalPercent = 0;
  let totalAmount = 0;

  taxGroup.lineItems.forEach(item => {
    if (item.type === 'Percentage') {
      totalPercent += parseFloat(item.percentOrAmt || 0);
    } else if (item.type === 'Amount') {
      totalAmount += parseFloat(item.percentOrAmt || 0);
    }
  });

  return { totalPercent, totalAmount };
}

handleLineItemChange = (idx, field, value) => {
  const updatedItems = [...this.state.formData.lineItems];
  const item = { ...updatedItems[idx] };

  if (field === 'taxId') {
    if (value === 'more') {
      this.setState({ showTaxOverlay: true });
      return;
    }
    item.taxId = value;
  } else {
    item[field] = value;
  }

  updatedItems[idx] = item;

  this.setState(prev => ({
    formData: {
      ...prev.formData,
      lineItems: updatedItems
    }
  }), this.recalculateQuoteTotals);
};

recalculateQuoteTotals() {
  const { impExp, freightCharges, taxPercent, packingCharges, lineItems } = this.state.formData;
  let freight = parseFloat(freightCharges) || 0;
  let packing = parseFloat(packingCharges) || 0;
  let taxOnFreight = 0;
  if (['None', 'CIF'].includes(impExp)) {
    taxOnFreight = (freight * (parseFloat(taxPercent) || 0)) / 100;
  }

  let lineTotal = 0;
  let itemTaxTotal = 0;

  const updatedLineItems = lineItems.map(item => {
    const baseTotal = (parseFloat(item.unitPrice || 0) * parseFloat(item.qty || 1));
    const taxDetails = this.getTaxDetailsFromGroup(item.taxId);
    const taxAmt = (baseTotal * taxDetails.totalPercent / 100) + (taxDetails.totalAmount * baseTotal);

    const total = baseTotal + taxAmt;
    item.total = total.toFixed(2);
    item.taxAmt = taxAmt.toFixed(2);

    lineTotal += baseTotal;
    itemTaxTotal += taxAmt;

    return item;
  });

  const totalTaxAmount = itemTaxTotal + taxOnFreight;
  const quoteValue = lineTotal + freight + packing + itemTaxTotal;

  // Avoid unnecessary update loop by checking
  if (
    this.state.formData.taxAmount !== totalTaxAmount.toFixed(2) ||
    this.state.formData.quoteValue !== quoteValue.toFixed(2)
  ) {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        taxAmount: totalTaxAmount.toFixed(2),
        quoteValue: quoteValue.toFixed(2),
        lineItems: updatedLineItems
      }
    }));
  }
}

resetFOBFields = () => {
  const { freightCharges, taxPercent, taxAmount, packingCharges, quoteValue } = this.state.formData;
  const needsReset = [freightCharges, taxPercent, taxAmount, packingCharges, quoteValue]
    .some(val => val !== '' && val !== null && val !== undefined);

  if (needsReset) {
    this.setState(prev => ({
      formData: {
        ...prev.formData,
        freightCharges: '',
        taxPercent: '',
        taxAmount: '',
        packingCharges: '',
        quoteValue: ''
      }
    }));
  }
};

componentDidUpdate(prevProps, prevState) {
  // Step 1: Autofill logic (unchanged)
  if (
    this.state.showForm &&
    this.state.customers.length > 0 &&
    this.state.formData.customer &&
    (prevState.formData.customer !== this.state.formData.customer ||
     (!this.state.formData.billTo && !this.state.formData.shipTo))
  ) {
    const customerObj = this.state.customers.find(
      c =>
        c.custname === this.state.formData.customer ||
        c.custcode === this.state.formData.customer
    );
    if (customerObj) {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          billTo: this.formatAddress(customerObj.billTo),
          shipTo: this.formatAddress(customerObj.shipTo),
          currency: customerObj.currency || ''
        }
      }));
    }
  }

  // Step 2: Prevent infinite recalculate calls
  const changed =
    prevState.formData.lineItems !== this.state.formData.lineItems ||
    prevState.formData.freightCharges !== this.state.formData.freightCharges ||
    prevState.formData.taxPercent !== this.state.formData.taxPercent ||
    prevState.formData.packingCharges !== this.state.formData.packingCharges ||
    prevState.formData.impExp !== this.state.formData.impExp;

  if (changed) {
    this.recalculateQuoteTotals();
  }
}

  fetchQuotes = async () => {
    const snap = await getDocs(collection(db, 'quotes'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ quotes: data.reverse() });
  };

  fetchCustomers = async () => {
    const snap = await getDocs(collection(db, 'customers'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ customers: data });
  };

  fetchProducts = async () => {
    const snap = await getDocs(collection(db, 'products'));
    const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    this.setState({ products: data });
  };
  fetchDespatchModes = async () => {
  const snap = await getDocs(collection(db, 'modes'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ despatchModes: data });
};

fetchPaymentTerms = async () => {
  const snap = await getDocs(collection(db, 'paymentTerms'));
  const data = snap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  this.setState({ paymentTerms: data });
};


  handleInputChange = (field, value) => {
    this.setState(prev => ({
      formData: { ...prev.formData, [field]: value }
    }));
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { editingId, formData, quotes } = this.state;
    if (!formData.customer) return alert("Customer is required");
    if (formData.lineItems.length === 0) return alert("At least one line item is required");

    if (editingId) {
      await updateDoc(doc(db, 'quotes', editingId), formData);
    } else {
      formData.quoteNo = `QT${1000 + quotes.length}`;
      await addDoc(collection(db, 'quotes'), formData);
    }
    this.setState({ showForm: false, editingId: null });
    this.fetchQuotes();
  };
loadQuotePreview = (q) => {
  this.setState({
    selectedQuote: q,
    previewMode: true
  });
};
getTaxPercentage = (taxId) => {
  if (!taxId || taxId === 'nill') return 0;
  const tax = this.state.taxComponents.find(tc => tc.id === taxId);
  return tax?.percentage || 0;
};


  loadQuoteForEdit = (quote) => {
    this.setState({
      formData: {
        ...quote,
        lineItems: Array.isArray(quote.lineItems) ? quote.lineItems : [] // Ensure array
      },
      editingId: quote.id,
      showForm: true,
      previewMode: false,
      activeTab: 'co'
    }, () => {
      if (this.customerInputRef.current) {
        this.customerInputRef.current.value = quote.customer;
      }
    });
  };
convertToOrder = (quote) => {
  // Save quote in sessionStorage (or navigate with state via router if you use react-router)
  sessionStorage.setItem('quoteToConvert', JSON.stringify(quote));
 this.props.history.push('/panelone/Order');
};

  showOverlay = (type) => this.setState({ overlayType: type, overlaySearch: '' });
  hideOverlay = () => this.setState({ overlayType: '', overlaySearch: '' });

  selectOverlayValue = (value) => {
    if (this.state.overlayType === 'customer') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          customer: value.custname || value.custcode || '', // Use custname or custcode
          billTo: this.formatAddress(value.billTo),
          shipTo: this.formatAddress(value.shipTo),
          currency: value.currency || '',
          // despatchMode: value.despatchMode || '', // No auto-fill for despatch mode
          // paymentTerms: value.paymentTerms || '' // No auto-fill for payment terms
        },
        overlayType: '',
        overlaySearch: ''
      }), () => {
        if (this.customerInputRef.current) {
          this.customerInputRef.current.value = value.custname || value.custcode || '';
        }
      });
    }
  };
  renderTaxOverlay = () => {
  const { taxComponents, taxLineIndex } = this.state;

  return (
    <div className="custom-overlay">
      <div className="custom-overlay-content">
        <div className="custom-overlay-title">Select Tax Group</div>
        <table className="table table-bordered table-sm">
          <thead>
            <tr><th>Component</th><th>%</th></tr>
          </thead>
          <tbody>
            {taxComponents.map(tc => (
              <tr key={tc.id} style={{ cursor: 'pointer' }} onClick={() => {
                const updated = [...this.state.formData.lineItems];
                updated[taxLineIndex].taxId = tc.id;
                this.setState(prev => ({
                  formData: { ...prev.formData, lineItems: updated },
                  showTaxOverlay: false,
                  taxLineIndex: null
                }), this.updateQuoteTotals);
              }}>
                <td>{tc.code}</td>
                <td>{tc.percentage}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-sm btn-secondary mt-2" onClick={() => this.setState({ showTaxOverlay: false })}>Cancel</button>
      </div>
    </div>
  );
}


 renderOverlay = () => {
  const { overlayType, overlaySearch, customers, despatchModes, paymentTerms } = this.state;

  const getFilteredRows = (list, nameKey = 'name', codeKey = 'shortName') =>
    list.filter(item =>
      (item[nameKey] || '').toLowerCase().includes((overlaySearch || '').toLowerCase()) ||
      (item[codeKey] || '').toLowerCase().includes((overlaySearch || '').toLowerCase())
    );

  const handleSelect = (item) => {
    if (overlayType === 'customer') {
      this.selectOverlayValue(item);
    } else if (overlayType === 'despatchMode') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          despatchMode: item.name
        },
        overlayType: '',
        overlaySearch: ''
      }));
    } else if (overlayType === 'paymentTerms') {
      this.setState(prev => ({
        formData: {
          ...prev.formData,
          paymentTerms: item.name
        },
        overlayType: '',
        overlaySearch: ''
      }));
    }
  };

  let title = '';
  let headers = [];
  let rows = [];

  if (overlayType === 'customer') {
    title = 'Select Customer';
    headers = ['Name', 'Code', 'Short Name'];
    rows = getFilteredRows(customers, 'custname', 'custcode');
  } else if (overlayType === 'despatchMode') {
    title = 'Select Despatch Mode';
    headers = ['Name', 'Short Name'];
    rows = getFilteredRows(despatchModes);
  } else if (overlayType === 'paymentTerms') {
    title = 'Select Payment Terms';
    headers = ['Name', 'Short Name'];
    rows = getFilteredRows(paymentTerms);
  } else {
    return null;
  }

  return (
    <div className="custom-overlay">
      <div className="custom-overlay-content">
        <div className="custom-overlay-title">{title}</div>
        <input
          type="text"
          className="form-control mb-2"
          placeholder={`Search ${overlayType}...`}
          value={overlaySearch}
          onChange={e => this.setState({ overlaySearch: e.target.value })}
        />
        <div style={{ maxHeight: 300, overflowY: 'auto' }}>
          <table className="table table-bordered table-sm">
            <thead>
              <tr>
                {headers.map((h, i) => <th key={i}>{h}</th>)}
              </tr>
            </thead>
            <tbody>
              {rows.map((item, i) => (
                <tr key={item.id || i} onClick={() => handleSelect(item)} style={{ cursor: 'pointer' }}>
                  {headers.map((h, j) => (
                    <td key={j}>
                      {overlayType === 'customer'
                        ? h === 'Name' ? item.custname
                          : h === 'Code' ? item.custcode
                          : item.custshortName
                        : h === 'Name' ? item.name
                          : item.shortName}
                    </td>
                  ))}
                </tr>
              ))}
              {rows.length === 0 && (
                <tr>
                  <td colSpan={headers.length} className="text-center">No records found</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        <button className="btn btn-secondary btn-sm mt-2" onClick={this.hideOverlay}>Cancel</button>
      </div>
    </div>
  );
};

  renderProductOverlay = () => {
    const { products, productOverlaySearch, selectedProductIds } = this.state;
    const filtered = products.filter(p =>
      (p.ptshortName || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.ptdescription || '').toLowerCase().includes(productOverlaySearch.toLowerCase()) ||
      (p.itemCode || '').toLowerCase().includes(productOverlaySearch.toLowerCase())
    );
    return (
      <div className="custom-overlay">
        <div className="custom-overlay-content">
          <div className="d-flex justify-content-between align-items-center mb-2">
            <div className="custom-overlay-title">Select Products</div>
            <button
              className="btn btn-success btn-sm"
              onClick={() => {
                const selectedProducts = products.filter(p => this.state.selectedProductIds.includes(p.id));
                this.setState(prev => ({
                  formData: {
                    ...prev.formData,
                    lineItems: [
                      ...prev.formData.lineItems,
                      ...selectedProducts
                        .filter(p => !prev.formData.lineItems.some(item => item.id === p.id))
                        .map(p => ({
                          id: p.id,
                          itemCode: p.productId || '',
                          itemDescription: p.ptdescription || '',
                          itemType: p.itemType || '',
                          materialType: p.materialType || '',
                          onHand: p.onHand || 0,
                          taxGroup: p.taxGroup || '',
                          custPartNo: p.custPartNo || '', // Assuming this is cust part table
                          hsnNo: p.hsnNo || '', // Assuming HSN No.
                          unitPrice: p.unitPrice || 0,
                          qty: 1,
                          total: (p.unitPrice || 0).toFixed(2)
                        }))
                    ]
                  },
                  productOverlayVisible: false,
                  selectedProductIds: []
                }));
              }}
            >Add Selected</button>
          </div>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Search products..."
            value={productOverlaySearch}
            onChange={e => this.setState({ productOverlaySearch: e.target.value })}
          />
          <div style={{ maxHeight: 300, overflowY: 'auto' }}>
            <table className="table table-bordered table-sm">
              <thead>
                <tr>
                  <th></th>
                  <th>Item Code</th>
                  <th>Item Desc</th>
                  <th>Item Type</th>
                  <th>Material Type</th>
                  <th>On Hand</th>
                  <th>Tax Grp</th>
                  <th>Cust Part No</th>
                </tr>
              </thead>
              <tbody>
                {filtered.map(p => (
                  <tr key={p.id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={selectedProductIds.includes(p.id)}
                        onChange={e => {
                          const checked = e.target.checked;
                          this.setState(prev => ({
                            selectedProductIds: checked
                              ? [...prev.selectedProductIds, p.id]
                              : prev.selectedProductIds.filter(id => id !== p.id)
                          }));
                        }}
                      />
                    </td>
                    <td>{p.productId}</td>
                    <td>{p.ptdescription || ''}</td>
                    <td>{p.itemType || ''}</td>
                    <td>{p.materialType || ''}</td>
                    <td>{p.onHand || 0}</td>
                    <td>{p.taxGroup || ''}</td>
                    <td>{p.custPartNo || ''}</td>
                  </tr>
                ))}
                {filtered.length === 0 && (
                  <tr>
                    <td colSpan="8" className="text-center">No products found</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          {/* Add pagination here if needed, for now, it's just a placeholder */}
          <div className="d-flex justify-content-between align-items-center mt-2">
            <span>Page 1 of 1</span>
            <button className="btn btn-secondary btn-sm" onClick={() => this.setState({ productOverlayVisible: false, selectedProductIds: [] })}>Cancel</button>
          </div>
        </div>
      </div>
    );
  };



  renderQuoteTable = () => (
    <div className="card mt-4 full-height">
      <div className="card-body">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="card-title">Quotes</h4>
          <button className="btn btn-primary" onClick={() => this.setState({ showForm: true })}>+ Add Quote</button>
        </div>
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="thead-light">
              <tr style={{ fontSize: '14px' }}>
                <th>Quote No</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Quote Value</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {this.state.quotes.map((q, i) => (
                <tr key={i} style={{ fontSize: '14px' }}>
                  <td>
                    <button
                      className="btn btn-link p-0"
                      onClick={() => this.loadQuotePreview(q)}
                    >
                      {q.quoteNo}
                    </button>
                  </td>
                  <td>{q.customer}</td>
                  <td>{q.quoteDate}</td>
                  <td>{q.quoteValue}</td>
                  <td><label className="badge badge-info">{q.status}</label></td>
          
                </tr>
              ))}
              {this.state.quotes.length === 0 && (
                <tr><td colSpan="6" className="text-center">No quotes found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  renderQuoteForm = () => {
    const { formData, overlayType, productOverlayVisible ,taxGroups} = this.state;
    const isFOB = formData.impExp === 'FOB';

    return (
      <div>
        <div className="card full-height">
          <div style={{ flex: 1, overflowY: 'auto', padding: '24px' }}>
            <h4 className="mb-3">Quote Form</h4>
            <ul className="nav nav-tabs" role="tablist">
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'co' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'co' })}>CO Details</button>
              </li>
              <li className="nav-item">
                <button type="button" className={`nav-link ${this.state.activeTab === 'commercial' ? 'active' : ''}`} onClick={() => this.setState({ activeTab: 'commercial' })}>Commercial Terms</button>
              </li>
            </ul>
            <form className="form-sample" onSubmit={this.handleSubmit}>
              <div className="tab-content pt-3">
                {this.state.activeTab === 'co' && (
                  <>
                    <div className="form-row">
                      <div className="form-group col-md-3">
                        <label>Quote No</label>
                        <input type="text" className="form-control form-control-sm" value={formData.quoteNo} onChange={(e) => this.handleInputChange('quoteNo', e.target.value)} />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Date</label>
                        <input type="date" className="form-control form-control-sm" value={formData.quoteDate} onChange={(e) => this.handleInputChange('quoteDate', e.target.value)} />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Type</label>
                        <select className="form-control form-control-sm" value={formData.quoteType} onChange={(e) => this.handleInputChange('quoteType', e.target.value)}>
                          <option>Standard</option>
                          <option>Manual</option>
                        </select>
                      </div>
                      <div className="form-group col-md-3">
                        <label>Status</label>
                        <input type="text" className="form-control form-control-sm" value={formData.status}   style={{ backgroundColor: '#fff' }} readOnly />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Customer</label>
                        <div className="input-group input-group-sm">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Choose Customer"
                            ref={this.customerInputRef}
                            value={formData.customer}
                            readOnly
                            onClick={() => this.showOverlay('customer')}
                            style={{ cursor: 'pointer' }}
                          />
                          <div className="input-group-append">
                            <button className="btn btn-outline-secondary btn-sm" type="button" onClick={() => this.showOverlay('customer')}>Select</button>
                          </div>
                        </div>
                      </div>
                      <div className="form-group col-md-3">
                        <label>IMP/EXP</label>
                        <select
                          className="form-control form-control-sm"
                          value={formData.impExp}
                          onChange={(e) => this.handleInputChange('impExp', e.target.value)}
                        >
                          <option>None</option>
                          <option>COB</option>
                          <option>FOB</option>
                          <option>CIF</option>
                        </select>
                      </div>
                      <div className="form-group col-md-2">
                        <label>Ref No.</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.qrefNo}
                          onChange={(e) => this.handleInputChange('qrefNo', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Choose</label>
                        <div className="d-flex align-items-center">
                          <div className="form-check mr-4">
                            <input type="radio" className="form-check-input" name="choose" id="chooseService" value="Service" checked={formData.choose === 'Service'} onChange={(e) => this.handleInputChange('choose', e.target.value)} />
                            <label className="form-check-label" htmlFor="chooseService">Service</label>
                          </div>
                          <div className="form-check">
                            <input type="radio" className="form-check-input" name="choose" id="chooseProduct" value="Product" checked={formData.choose === 'Product'} onChange={(e) => this.handleInputChange('choose', e.target.value)} />
                            <label className="form-check-label" htmlFor="chooseProduct">Product</label>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-3">
                        <label>Currency</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.currency}
                          style={{ backgroundColor: '#fff' }}
                          readOnly
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Conversion Rate</label>
                        <input
                          type="text"
                          className="form-control form-control-sm"
                          value={formData.conversionRate}
                          onChange={(e) => this.handleInputChange('conversionRate', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Tax Amount</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxAmount}
                          onChange={(e) => this.handleInputChange('taxAmount', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-3">
                        <label>Quote Value</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.quoteValue}
                          onChange={(e) => this.handleInputChange('quoteValue', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                   
                          <div className="d-flex justify-content-between align-items-center mb-3">
                            <h4 className="card-title">Line Item</h4>
                            <button type="button" className="btn btn-primary btn-sm" onClick={() => this.setState({ productOverlayVisible: true })}>
                              + Add Product
                            </button>
                          </div>
                          <div className="table-responsive">
        <table className="table table-bordered">
          <thead className="thead-light">
            <tr>
              <th>Item Code</th>
              <th>Item Desc</th>
              <th>Cust Part No</th>
              <th>HSN No</th>
              <th>UOM</th>
              <th>On Hand</th>
              <th>Unit Price</th>
              <th>Quantity</th>
              <th>Tax Id</th>
              <th>Item Total</th>
              <th>Tax Amt</th>
              <th>Tax Info</th>
            </tr>
          </thead>
          <tbody>
            {formData.lineItems.map((item, idx) => {
              const itemTotal = item.unitPrice * item.qty;
              const taxDetails = this.getTaxDetailsFromGroup(item.taxId);
              const taxAmt = (itemTotal * taxDetails.totalPercent / 100) + (taxDetails.totalAmount * itemTotal);
              const totalWithTax = itemTotal + taxAmt;
              const taxGroup = taxGroups.find(tg => tg.id === item.taxId);
              const taxInfo = taxGroup
                ? `${taxGroup.name} (${(taxDetails.totalPercent || 0) + (taxDetails.totalAmount ? ` + ${taxDetails.totalAmount}` : '')})`
                : item.taxId === 'more'
                ? 'Select via More'
                : item.taxId === 'nill'
                ? 'Nill Tax'
                : '-';

              return (
                <tr key={item.id || idx}>
                  <td>{item.itemCode}</td>
                  <td>{item.itemDescription}</td>
                  <td>{item.custPartNo}</td>
                  <td>{item.hsnNo}</td>
                  <td>{item.uom}</td>
                  <td>{item.onHand}</td>
                  <td>
                    <input
                      type="number"
                      value={item.unitPrice}
                      onChange={e => this.handleLineItemChange(idx, 'unitPrice', e.target.value)}
                      style={{ width: 80 }}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      value={item.qty}
                      onChange={e => this.handleLineItemChange(idx, 'qty', e.target.value)}
                      style={{ width: 60 }}
                    />
                  </td>
                  <td>
                    <select
                      value={item.taxId || ''}
                      onChange={e => this.handleLineItemChange(idx, 'taxId', e.target.value)}
                    >
                      <option value="">-Select-</option>
                      <option value="nill">Nill Tax</option>
                      {Array.isArray(taxGroups) && taxGroups.map(group => (
                        <option key={group.id} value={group.id}>{group.name}</option>
                      ))}
                      <option value="more">More</option>
                    </select>
                  </td>
                  <td>{totalWithTax.toFixed(2)}</td>
                  <td>{taxAmt.toFixed(2)}</td>
                  <td>{taxInfo}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
                          {/* Add pagination for line items here if needed */}
                          <div className="d-flex justify-content-between align-items-center mt-2">
                            <span>Page 1 of 1</span>
                          </div>
                      
                  </>
                )}
                {this.state.activeTab === 'commercial' && (
                  <>
                    <div className="form-row">
                      <div className="form-group col-md-6">
                        <label>Bill To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.billTo}
                          onChange={(e) => this.handleInputChange('billTo', e.target.value)}
                        />
                      </div>
                      <div className="form-group col-md-6">
                        <label>Ship To</label>
                        <textarea
                          className="form-control form-control-sm"
                          rows="3"
                          value={formData.shipTo}
                          onChange={(e) => this.handleInputChange('shipTo', e.target.value)}
                        />
                      </div>
                    </div>
                    <div className="form-row">
                    <div className="form-group col-md-4">
  <label>Despatch Mode</label>
  <div className="input-group input-group-sm">
    <input
      type="text"
      className="form-control"
      value={formData.despatchMode}
      readOnly
      onClick={() => !isFOB && this.showOverlay('despatchMode')}
      style={{ cursor: isFOB ? 'not-allowed' : 'pointer', backgroundColor: '#fff' }}
    />
    <div className="input-group-append">
      <button
        className="btn btn-outline-secondary btn-sm"
        type="button"
        disabled={isFOB}
        onClick={() => this.showOverlay('despatchMode')}
      >
        Select
      </button>
    </div>
  </div>
</div>

<div className="form-group col-md-4">
  <label>Payment Terms</label>
  <div className="input-group input-group-sm">
    <input
      type="text"
      className="form-control"
      value={formData.paymentTerms}
      readOnly
      onClick={() => !isFOB && this.showOverlay('paymentTerms')}
      style={{ cursor: isFOB ? 'not-allowed' : 'pointer', backgroundColor: '#fff' }}
    />
    <div className="input-group-append">
      <button
        className="btn btn-outline-secondary btn-sm"
        type="button"
        disabled={isFOB}
        onClick={() => this.showOverlay('paymentTerms')}
      >
        Select
      </button>
    </div>
  </div>
</div>

                      <div className="form-group col-md-4">
                        <label>Freight Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.freightCharges}
                          onChange={(e) => this.handleInputChange('freightCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group col-md-4">
                        <label>Tax %</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxPercent}
                          onChange={(e) => this.handleInputChange('taxPercent', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Freight Tax Amount</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.taxAmount}
                          onChange={(e) => this.handleInputChange('freighttaxAmount', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                      <div className="form-group col-md-4">
                        <label>Packing Charges</label>
                        <input
                          type="number"
                          className="form-control form-control-sm"
                          value={formData.packingCharges}
                          onChange={(e) => this.handleInputChange('packingCharges', e.target.value)}
                          readOnly={isFOB} // Read-only if FOB
                        />
                      </div>
                    </div>
                    
                  </>
                )}
                

              </div>
              <div className="fixed-card-footer text-right p-3 border-top bg-white">
                <button type="submit" className="btn btn-success mr-2">Save All Details</button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => this.setState({ showForm: false, editingId: null })}
                >
                  Cancel
                </button>
              </div>
            </form>
            {overlayType && this.renderOverlay()}
            {productOverlayVisible && this.renderProductOverlay()}
          </div>
          
        </div>
      </div>
    );
  };
renderQuotePreview = () => {
  const q = this.state.selectedQuote;
  if (!q) return null;

  return (
    <div className="card mt-4 full-height p-4">
      <div className="d-flex justify-content-between mb-3">
        <h4 className="card-title">Quote Preview - {q.quoteNo}</h4>
        <div>
          <button className="btn btn-sm btn-info mr-2" onClick={() => this.loadQuoteForEdit(q)}>Edit</button>
          <button className="btn btn-sm btn-success" onClick={() => this.convertToOrder(q)}>Convert to Order</button>
        </div>
      </div>
      <div className="row">
        <div className="col-md-4"><b>Customer:</b> {q.customer}</div>
        <div className="col-md-4"><b>Date:</b> {q.quoteDate}</div>
        <div className="col-md-4"><b>Status:</b> {q.status}</div>
      </div>
      <div className="row mt-2">
        <div className="col-md-6"><b>Bill To:</b><br />{q.billTo}</div>
        <div className="col-md-6"><b>Ship To:</b><br />{q.shipTo}</div>
      </div>
      <h5 className="mt-4">Line Items</h5>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Item Code</th><th>Description</th><th>Qty</th><th>Unit Price</th><th>Total</th>
          </tr>
        </thead>
        <tbody>
          {q.lineItems?.map((item, i) => (
            <tr key={i}>
              <td>{item.itemCode}</td>
              <td>{item.itemDescription}</td>
              <td>{item.qty}</td>
              <td>{item.unitPrice}</td>
              <td>{item.total}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="text-right"><b>Total:</b> {q.quoteValue}</div>
      <button className="btn btn-secondary mt-3" onClick={() => this.setState({ previewMode: false })}>Back to List</button>
    </div>
  );
};

  render() {
    return (
      <div className="container-fluid">
        {this.state.previewMode? this.renderQuotePreview(): this.state.showForm ? this.renderQuoteForm() : this.renderQuoteTable()}
         {this.state.showTaxOverlay && this.renderTaxOverlay()}

      </div>
    );
  }
}

export default withRouter(Quote);
